css:
@-moz-document url-prefix("http://9gag.com") {
/* latin */
@font-face {
  font-family: 'Carme';
  font-style: normal;
  font-weight: 400;
  src: local('Carme Regular'), local('Carme'), url(http://fonts.gstatic.com/s/carme/v7/sK--wWW5U-jTcduXZtmv9A.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

* {
 font-family: Carme;   
}


#container {
  position: relative;
  overflow: hidden;
  margin: 0 auto;
  clear: both;
  background-color: black;
}

body {
	background-color: black;
}

::-webkit-input-placeholder { /* WebKit, Blink, Edge */
    color:    #444;
    opacity:  0;
}
:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
   color:    #444;
   opacity:  0;
}
::-moz-placeholder { /* Mozilla Firefox 19+ */
   color:    #444;
   opacity:  0;
}
:-ms-input-placeholder { /* Internet Explorer 10-11 */
   color:    #444;
    opacity:  0;
}

.share{
	display: none;
}

.badge-entry-toolbar-sticky {
  display: none;   
}

.CS3 a {
  color: orange;
  text-decoration: none;
}

section#list-view-2 h2 a {
  font-size: 20px;
  color: #5D5D5D !important
}

a {
  color: #09f;
  text-decoration: none;
}

.btn-vote a {
  border: 1px solid #222;
}

section#list-view-2 article {
  margin-bottom: 30px;
  border-top: 1px solid #222;
  padding: 20px 0 0;
}

section.block-feature-cover .info-container a {
  display: block;
  color: #5D5D5D;
}

.post-afterbar-a.in-post-top {
  background-color: #000;
}

.horizontal-vote a.up span {
  background-color: #000;
}

.CS3{
    background-color: black
}

.CS3 .comment-box .payload .textarea-container textarea.focus, .CS3 .comment-box.indent-1 .payload .textarea-container textarea {
  background-color: black;
}

.CS3 .comment-entry .payload .username {
  color: #333;
}

.post-text-area{
  color: lightgrey;
}

.CS3 label, .CS3 li, .CS3 ol, .CS3 p, .CS3 pre, .CS3 td, .CS3 th, .CS3 ul {
  color: orange;
}

.CS3 .comment-entry .payload .content {
  color: grey;
}

.CS3 .comment-box .action {
  padding: 5px 10px;
  border: 1px solid #222;
}

.collapsed-comment span{
  color: maroon;
  text-decoration: none;
}

a:hover {
  color: black;
  border-bottom: 0 solid #000000;
}

#jsid-featured-sidebar-tail{
	display: none;
}

section#individual-post header h2 {
  color: darkgrey;
}

.horizontal-vote a, .vertical-vote a {
  border: 1px solid #222;
}

.notification-menu{
   	background-color:#1E1E1E;
    color: #333;
}

.section-nav {
  background-color: #111;
  border-bottom: 0px;
}

.section-nav a.selected {
  color: orange;
  border-bottom: 3px solid orange;
}

.section-nav a {
  color: #999;
}

a {
  color: #444;
  text-decoration: none;
}

section.modal {
  background-color: #222;
}

.CS3 textarea {
  border: 1px solid #222;
}

.CS3 .tab-bar ul.tab li.active a {
  color: #999;
  border-bottom: 2px solid orange;
}

.CS3 .tab-bar ul.tab a {
  color: #333;
}

.CS3 .tab-bar {
  border-bottom: 0;
}

.idle h2 {
  color: orange;
}

.modal.idle{
	display: none;
}

.user-function .btn-primary, .visitor-function .btn-primary {
  color: #aaa;
  background-color: #222;
}

.CS3 a.cmnt-btn {
    color: #aaa;
    background-color: maroon;
}

.CS3 a.cmnt-btn:hover {
    background-color: orange;
    color: black;
    border-color: #222;
}

.CS3 a.cmnt-btn:active {
    background-color: #07b;
    border-color: #222;
}

.CS3 a.cmnt-btn {
 border: 0;   
}

.headbar-search {
  border: 1px solid #222;
  background-color: #333;
  width: 500px;
}

.headbar-search input[type=text] {
  background-color: #333;
  color: #00ff00;
}

section.section-header .social-love span.instagram-follow a {
    border: 1px solid #222;
}

.headbar-search input[type=text] {
  background-color: #333;
}

section.section-header .social-love span.instagram-follow a {
    border: 1px solid #222;
}

.popup-menu ul {
    border: 1px solid #222
}

.headbar-search {
    border: 1px solid #222;
}

.flexMenu-popup {
    border: 1px solid #222;
}

.notification-menu {
    background-color: #000;
    border: 1px solid #222;
}


/* Let's get this party started */
::-webkit-scrollbar {
    width: 12px;
}
 
/* Track */
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 2px rgba(0,0,0,0.3); 
    -webkit-border-radius: 2px;
    border-radius: 2px;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 2px;
    border-radius: 2px;
    background: rgba(100,0,0,0.8); 
    -webkit-box-shadow: inset 0 0 2px rgba(0,0,0,0.5); 
}

section#list-view-2 p.post-meta a {
  color: rgba(150,0,0,1); 
}

#jsid-header-funny-menu{
	display: none;
}

.flex.menu{
  display: none;
}

.nav-menu .primary a {
  text-align: center;
  border-right: 0;
  color: maroon;
  font-weight: 700;
}

::-webkit-scrollbar-thumb:window-inactive {
	background: rgba(100,0,0,0.4); 
}

.nav-menu .primary a.selected {
  background-color: black;
}

.nav-menu a::hover {
  color: #444;   
}

.post-afterbar-meta a{
    color: maroon;
}

#jsid-featured-item-container{
 display: none;   
}

.post-afterbar-meta {
  border-top: 0;
}

header#top-nav a.logo {
  background: black;
  border-right: 0;
  width:3px;
}
}
