css:
@-moz-document domain("signalcommunity.com") {
body, td, th, tr {
    color: #fff;
}

#menu {
    border-top: 1px solid #000;
}

header {
    background: #1d212a;
    border-bottom: 1px solid #000;
    border-top: 1px solid #000;
}

a:link, a:visited, a.new_win:link, a.new_win:visited {
    color: #fff;
    text-decoration: none;
}

.roundframe {
    background: #1d212a;
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

span.upperframe span {
    background: #1d212a;
    display: none;
}

span.upperframe {
    background: #1d212a;
    border: 0px solid #000;
    display: none;
}

span.lowerframe span {
    background: #1d212a;
}

span.lowerframe {
    background: #1d212a;
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

hr, .hrcolor {
    color: #1d212a;
    background-color: #1d212a;
}

.windowbg, #preview_body, .windowbg2, .windowbg3 {
    color: #fff;
    background-color: #1d212a;
}

.table_list td.info, .table_list td.stats {
    border-bottom: 0px solid #000;
}

.table_list td.icon {
    border-bottom: 0px solid #000;
    border-left: 0px solid #000;
}

.windowbg4 {
    color: #fff;
    background-color: #1d212a;
    border: 1px solid #000;
}

.table_list td.lastpost {
    border-bottom: 0px solid #000;
    border-right: 0px solid #000;
}

.table_list tbody.content td.info a.subject {
    font-size: 115%;
    color: #fff;
}

#IC ul.nav.nav-tabs {
    background: #1d212a;
    border: 1px solid #191919;
    border-top: none;
}

.nav-tabs {
    border-bottom: 1px solid #000;
}

#IC .tab-content {
    background: #1d212a;
    border: 0px solid #000;
}

#IC .nav-tabs > li.active > a, #IC .nav-tabs > li.active > a:hover {
    border-left-color: #000;
    border-right-color: #000;
}

#IC .nav-tabs > li.active > a:hover {
    background: #C7C7C7;
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    color: #fff;
    background-color: #000;
    border: 1px solid #000;
}

.poster ul li {
    background: #1d212a;
}

.body_content {
    background: #1d212a;
}

.body_content span.arrow-left {
    border-color: transparent transparent transparent transparent;
}

.description, .description_board, .plainbox {
    border: 1px solid #1d212a;
    background: #1d212a;
}

.topic_table table thead {
    border-bottom: 1px solid #000;
}

tr.catbg2 th {
    color: #fff;
    background: #1d212a;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #1d212a;
}

.table_grid td.icon1 {
    border-left: 0px solid #000;
}

.table_grid td.lastpost, .table_grid td.moderation {
    border-right: 0px solid #000;
}

#mlist > table {
    background-color: #1d212a;
    background: #1d212a;
    border: 1px solid #1d212a;
}

.table_grid td {
    border-bottom: 1px solid #000;
}

.table > thead > tr > th {
    border-bottom: 1px solid #000;
}

.windowbg span.topslice {
    background: #1d212a;
}

.windowbg span.topslice span {
    background: #1d212a;
}

.windowbg span.botslice span {
    background: #1d212a;
}

.windowbg span.botslice {
    background: #1d212a;
}

.windowbg2 span.topslice span {
    background: #1d212a;
}

.windowbg2 span.topslice {
    background: #1d212a;
}

.windowbg2 span.botslice span {
    background: #1d212a;
}

.windowbg2 span.botslice {
    background: #1d212a;
}

#detailedinfo div.content dl, #tracking div.content dl {
    border-bottom: 1px #000 solid;
}

.table-striped > tbody > tr {
    background-color: #1d212a;
}

.inner {
    border-top: 1px solid #000;
}

input, button, select, textarea {
    color: #fff;
    background: #212122;
    border: 1px solid #000;
}

.poster ul ul {
    background: #1d212a;
}

.signature, .custom_fields_above_signature {
    border-top: 1px #000 solid;
}

#poll_options dl.options {
    border: solid #000;
    border-width: 1px 0;
    padding: 1em 2.5em 1em 2em;
    margin: 0 1em 1em 0;
    line-height: 1.1em !important;
}

.adk_pointer {
    display: none;
}

#category_1 > tr > td > div > h3 > a.collapse {
    display: none;
}

#category_2 > tr > td > div > h3 > a.collapse {
    display: none;
}

#upshrink_ic {
    display: none;
}

.adk_height_1 {
    height: 15px;
}

.adk_7 {
    border-top: solid #000;
    border-width: 1px 0;
}

.lockedbg {
    background: #2E1C00;
}

.stickybg {
    background: #28283C;
}

.richedit_resize {
    border: 0px solid #ddd;
    background: #000;
}
blockquote.bbc_standard_quote {
    background-color: #232733;
}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    color: #fff;
    border: 1px solid #000;
}
blockquote.bbc_alternate_quote {
    background-color: #2C3140;
}

#category_1 {
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

#category_1_boards {
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

#category_2 > tr > td {
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

#category_2_boards {
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

#upshrinkHeaderIC p.last {
    border-top: 1px solid #000;
}

#IC {
    border: 1px solid #000;
}

#main_content_section > div.cat_bar {
    border: 1px solid #000;
}

#help_container > div {
    border: 1px solid #000;
}

#searchform > div {
    border: 1px solid #000;
}

#memberlist > div.cat_bar {
    border: 1px solid #000;
}

#mlist > table > tbody {
    border: 1px solid #000;
}

#mlist > table > thead {
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

.img-circle {
    border-radius: 50%;
}

.img-thumbnail {
    background-color: #000;
    border: #000;
}

.poster ul {
    border-radius: 0 0 4px 4px;
    border: 1px solid #000;
}

.body_content {
    border: 1px solid #000;
}

#messageindex > table > thead {
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

#messageindex > table > tbody {
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

#main_content_section > div > div.buttonlist.floatright {
    border: 1px solid #000;
}

#display_jump_to {
    border: 1px solid #000;
}

#quickreplybox {
    border: 1px solid #000;
}

#poll > div.cat_bar {
    border: 1px solid #000;
}

#poll > div.windowbg {
    border: 1px solid #000;
}

#poll > div.windowbg > span.topslice {
    padding-left: 0px;
}

#pollmoderation > div > ul > li {
    border: 1px solid #000;
}

#poll > div > ul > li {
    border: 1px solid #000;
}

#message {
    border: 1px solid #000;
}

#postMoreExpand {
    display: none;
}

#message_resizer {
    display: none;
}

#recent > div.cat_bar {
    border: 1px solid #000;
}

#recent > div {
    border: 1px solid #000;
}

#postmodify > div.cat_bar {
    border: 1px solid #000;
}

#personal_messages > div {
    border: 1px solid #000;
}

footer {
    border: 1px solid #000;
}

code.bbc_code {
    background: #00263F;
    border: 1px solid #000;
}
}
