css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {

    /* Original user style URL: https://userstyles.org/styles/118213/
    */
    /* font size (cards etc) */
    html, body.body-board-view, input, select, textarea {font-size: 13px !important;} /* instead of 14px */

    /**************** HEADER *****************/

    /* header bar */
    #header {background: #2B2B2B !important;}

    /* header buttons */
    .header-btn.header-notifications, .header-btn.header-boards, 
    .header-btn.header-search, .header-btn.header-login, 
    .header-btn.header-signup {
        background: #6B6B6B !important;
        }
    .header-btn:hover {background: #868686 !important;}

    /* header search box */
    #header-search input[type="text"] {background: #6B6B6B !important;}
    #header-search input[type="text"]:focus {background: #fff !important;}

    /* board title */
    #board-header .board-name {font-weight: normal !important;}
    
    /* "Show menu" link */
    .board-header-btn-text {
        text-decoration: none;
    }

    /**************** BOARD*****************/

    /* board background */
    #board {
        background: #f2f2f2;       
        padding-top: 10px;
    }
    
    /**************** LISTS *****************/
    .list-header {
        padding-top: 12px;
    }
    .list-header-menu-icon {
        top: 3px;
    }
    .list {
        background-color: #f2f2f2;
    }
    .list-card {
        border: 1px solid #ddd;
        border-width: 0 1px 1px 0;
        margin-bottom: 2px;
    }
    .list-card:last-child {        
    }
    .list-card.selected, .list-card.active-card { 
        box-shadow: 0px 1px 4px rgba(0,0,0,0.3);
        background: white;
    }
    .list-card-operation {
        background: white;
    }
    .list-wrapper.mod-add.is-idle {
        width: auto;
    }
    /* date */

    .badges .badge.is-due-future {
        background-color: #888;
        color: white;
        border-radius: 3px;
    }
    .icon-clock {
        color: white;
        opacity: 0.35;
    }
    /* badges custom fields */
    .badge {
        background-color: #eee;
    }
    /* new card link */
    .open-card-composer {
        font-size: 10px;
        text-transform: uppercase;
        letter-spacing: 1px;
        margin: 0 8px;
        padding: 8px 8px;
    }
    .open-card-composer:hover {
        text-decoration: none;
        background-color: #ededed;
    }
    /* new list link */
    .list-wrapper.mod-add.is-idle {
        background: #f2f2f2;
    }
    .list-wrapper.mod-add .placeholder {
        background: none !important;
        box-shadow: none !important;
        text-transform: uppercase !important;
        font-size: 10px !important;
        letter-spacing: 1px;
        color: #888;
    }
    .list-wrapper.mod-add:hover .placeholder {
        color: white;
    }

    /**************** CARD CONTENT (back side) *****************/
    
    /* popup window */
    .window {
        background-color: #f7f7f7;
    }   
    
    /**************** SIDE MENU right, settings *****************/
    
    .board-menu {
        background-color: #f7f7f7;
    }   
    
    /**************** SIDE MENU left, boards list *****************/
    
    .sidebar-boards-list a {
        height: 24px;
    }
    a .sidebar-section-header-title {
        text-decoration: none;
    }
    .sidebar-boards-list .thumbnail {
        height: 24px;
    }
    .sidebar-boards-list .thumbnail {
        height: 24px;
        width: 24px;
    }
    .sidebar-boards-list .details {
        margin-left: 1px;
        padding: 3px 10px;
    }
    .compact-board-tile-link-text-sub-name {
        display: none;
    }
    .board-list .board-list-item-icon-star, 
    .sidebar-boards-list .board-list-item-icon-star {
        padding: 3px 9px;
    }
    .boards-sidebar-item-link-thumbnail {
        height: auto;
    }
    .board-list .unread-indicator, .sidebar-boards-list .unread-indicator {
        top: 6px;
    }
    .boards-sidebar-item-link-text-sub-name {
        display: none;
    }
    .compact-board-tile-link-thumbnail {
        height: auto;
    }
    /* Links at the bottom */
    .boards-drawer .quiet-button {
        text-decoration: none;
    }
    .boards-drawer .empty, .boards-drawer .quiet-button {
        margin: 0px 4px 0 0;
        padding: 3px 9pt;
    }
    /* Star */
    .compact-board-tile-link-options-remove, 
    .compact-board-tile-link-options-star {
        margin: 2px 0
    }
    
    /**************** BOARDS PAGE *****************/
    
    body .board-list .details {
        min-height: 40px;
    }
    body .board-list .board-list-item--add-board {
        line-height: 40px;
    }



/* end */
}
