css:
@-moz-document domain("500px.com") {
html{
    background-color:#1A1A1A;
}

body.v3 .photo_show .photo_activity {
    background-color:#2B2B2B;
}

body.v3 .photo_show .photo_details{
    background-color:#333333;
    padding-top: 40px;
}

body.v3 .photo_show {
    background-color:#FFF;
}

body.v3 .photo_show .photo.segment{
    background-color:#1A1A1A;
}

body.v3 .photo_show .photo_header.bottom-fold{
    border-top: 1px solid #000000;
    border-bottom: 1px solid #000000;
    background-color: #000;
    margin-bottom: 0px;
}

body.v3 .photo_index{
    background-color:#1A1A1A;
}

body.v3{
     background-color:#1A1A1A;
}

div.main-nav{
    background-color:#000;
}

.grid .photo img, .grid .ad img{
    border-radius:4px;
}

.grid .photo, .grid .ad{
    border-radius:4px;
}

body.v3 .photo_index .sidenav{
	border-radius:4px;
    background-color:#333;
    border: none;
}

body.v3 .photo_index .sidenav ul.main{
        border-bottom: 2px solid #1A1A1A;
}

.pager ul li .disabled {
    background: #545454;
    color:#EEE;
}

.pager ul li a {
    background: #545454;
    color:#EEE;
}

.grid.paginated .pager {
    padding-top: 20px;
    padding-bottom: 20px;
}

body.v3 .photo_index .sidenav ul li a.active {
    color: #EEE;
    font-weight: bold;
}

body.v3 .photo_index .sidenav ul li a {
    color: #868686;
}
}
