css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.refuges.info") {

  body {
    background: white;
    font-family: Fira Sans, Open Sans, Arial, Sans;
    font-weight: 200;
    overflow-x: hidden;
  }
  a, a:visited {
    color: blue;
  }
  a:hover {
    color: black;
    background: #c1c1c1;
    border-radius: 3px;
  }
  
  /* header */
  
  #entete {
    background: #c1c1c1;
    height: 55px;
  }
  #entete > a > img {
    display: none;
  }
  #entete div a, #entete div a:first-child {
    color: blue;
    font-weight: 300;
    padding: 0 2px;
  }
  #entete div a:hover {
    color: black;
    background: white;
    border-radius: 3px;
  }
  #entete input[type=text] {
    border: 1px solid blue;
  }
  #entete > a:after {
    content: "Refuges.info";
    position: absolute;
    top: 10px;
    left: 25px;
    font-weight: 200;
    font-style: italic;
    color: black;
    font-size: 1.7em;
  }
  
  /********/
  /* MENU */
  /********/
  
  .menu > ul > li, .menu > ul > li > a {
    background-color: transparent;
  }
  .menu > ul {
    background-color: black;
    border-top: 1px solid black !important;
    border-bottom: 1px solid #555 !important;
    box-shadow: 0 -1px 0 #777 inset, 0 0 5px black;
    height: 25px;
  }
  .menu > ul > li {
    border-bottom: none;
    border-right: 1px solid #444;
    height: 22px;
    color: white;
    font-weight: 400;
    padding: 2px 0.5em 1px 0.5em;
  }
  .menu > ul > li > a {
    color: white;
    display: block;
    position: relative;
    top: 0px;
    margin: -2px -0.5em 0px -0.5em;
    padding: 2px 0.5em 1px 0.5em;
    font-weight: 400;
  }
  .menu > ul > li:hover {
    color: white;
    background: #000088;
    border-bottom: 1px solid blue;
    padding-bottom: 0;
  }
  .menu ul li a:hover {
    background: transparent;
  }
  /* sub-menu */
  .menu > ul > li > ul > li > a {
    background-color: #000088;
    display: block;
    color: white;
    padding: 0px 0.7em 1px 0.7em;
    margin: -2px -0.7em 0px -0.7em;
    font-weight: 400;
    border-bottom: 1px solid #777;
    border-right: 1px solid #777;
    border-left: 1px solid #777;
    height: 24px;
    line-height: 24px;
    border-radius: 0;
  }
  .menu > ul > li > ul > li > a:hover {
    background-color: #c1c1c1;
    color: black;
  }
  .menu > ul > li > ul > li {
    height: 24px;
  }
  .menu > ul > li > ul {
    margin-top: 2px;
  }

  /***************/
  /* FICHE POINT */
  /***************/
  
  .fiche_cadre > div:first-child > dl:first-child > dd:first-child {
    position: relative;
    left: 0px;
    margin-left: -30px;
    margin-top: -5px;
    margin-bottom: 20px;
    padding: 10px 10px 10px 30px;
    height: 20px;
    display: block;
    width: 200%;
    background: orange;
    font-weight: 400;
    opacity: 1;
  }
  .fiche_cadre > div:first-child > dl:first-child > dd:first-child > a {
    color: black;
    font-weight: 200;
  }
  .fiche_cadre > div:first-child > dl:first-child > dd:first-child:before {
    content: "Administration : ";
    color: black;
    font-weight: 400;
    font-size: 13px;
  }

  .fiche_cadre > div:first-child > dl:first-child > dd > em {
    font-size: 30px;
    font-weight: 600;
    opacity: 1 !important;
  }
  .fiche_cadre > div:first-child > dl:first-child > dt, .fiche_cadre > div:first-child > dl:first-child > dd {
    font-style: italic;
    font-weight: 200;
  }
}
