css:
@-moz-document domain("theoldreader.com") {
.navbar-fixed-top {
     top: -50px !important;
  }
  
  .container-fluid {
     top: 17px !important; /* 67 - 50 */
  }

  .navbar-fixed-top:hover {
     top: 0px !important;
  }
  
  .navbar-fixed-top:hover + .container-fluid {
     top: 67px !important;
  }

div[class="list-post"] h3 {
    /* Feed Item Content Heading */
    font-size: 32px !important;
    line-height: 1.1em !important;
    font-family: Georgia !important;
}

div[class="content clearfix"] p, div[class="content-body"] p {
    font-size: 18px !important;
    line-height: 1.3em !important;
    font-family: Georgia !important;
    color: #262626;
}

div[class="content clearfix"] * li, div[class="content-body"] * li {
    margin-left: 50px !important;
    font-size: large !important;
    line-height: 1.5em !important;
    font-family: Georgia !important;
}

div[class="content clearfix"] a, div[class="content-body"] a {
    font-size: large !important;
    line-height:1.5em !important;
    font-family: Georgia !important;
}

div[class="content-body"] {
    font-size: large !important;
    line-height: 1.5em !important;
    font-family: Georgia !important;
}


span[class="label label-transparent"] {
    font-size: small !important;
    line-height: 1.5em !important;
    font-family: Georgia !important;
}

div[class="content-body"] img {
	max-height:180px;
    float:left;
    margin-right:10px;
}

div[class="content-body"] > p:nth-child(1) {
 	display:none;   
}

div[class="enclosure"] {
 	display:none;   
}


/* 2017 April 10. Not sure if above still works.
If any problems delete and use below only. */

div.clearfix span.label,
div.content-body + div.enclosure {
 display:none;   
}

.post .content .responsive-img-wrapper {
    float:left;
    display:inline-block;
    width:300px;
    margin-right:10px;
}

.post .content .responsive-img-wrapper img {
 	width:300px;
    max-width:300px;
}

/* use +2 to skip first */
div.content-body p:nth-child(n+1) {
    display:none;
}


}
