css:
@-moz-document domain("roblox.com") {
.rbx-header {
    background-color: #BC0101;
}
.rbx-btn-primary-sm {
    background-color: #BC0101;
    border-color: #BC0101;
}
.feeds .feed-game {
    border-top: 2px solid #BC0101;
}
a.ad img {
    display: none;
    border: none;
}
.abp .ad-identification {
    display: none;
}
.abp .BadAdButton:hover {
    display: none;
}
.abp .UpsellAdButton, .abp .UpsellAdButton:link, .abp .UpsellAdButton:visited, .abp .BadAdButton, .abp .BadAdButton:link, .abp .BadAdButton:visited {
    display: none;
}
.btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited {
    border-color: #BC0101;
    background-color: #BC0101;
    background-image: url(http://img.ctrlv.in/img/15/08/30/55e332900ec38.png);
    color: white;
}
#StudioSplashScreen .header-container a, #StudioSplashScreen .blurbs-container a {
    color: #BC0101;
}

.rbx-icon-default-games, .rbx-icon-games, .rbx-icon-like, .rbx-icon-like.selected, .rbx-icon-dislike, .rbx-icon-dislike.selected, .rbx-icon-privateserver, .rbx-icon-allgears, .rbx-icon-partialgears, .rbx-icon-nogear, .rbx-icon-MeleeWeapon, .rbx-icon-RangedWeapon, .rbx-icon-PowerUp, .rbx-icon-Explosive, .rbx-icon-MusicalInstrument, .rbx-icon-NavigationEnhancer, .rbx-icon-BuildingTool, .rbx-icon-SocialItem, .rbx-icon-PersonalTransport {
    background-image: url(http://img.ctrlv.in/img/15/08/30/55e32c9fb1b07.png);
}
.rbx-link, .rbx-link-sm, .rbx-link-xs, .vlist .list-item .list-body .list-content a, .rbx-link:link, .rbx-link-sm:link, .rbx-link-xs:link, .vlist .list-item .list-body .list-content a:link, .rbx-link:visited, .rbx-link-sm:visited, .rbx-link-xs:visited, .vlist .list-item .list-body .list-content a:visited, .rbx-link:active, .rbx-link-sm:active, .rbx-link-xs:active, .vlist .list-item .list-body .list-content a:active {
    color: #BC0101;
}
body .forum-table-header, body .forum-table-header th, body .forum-table-footer {
    background-color: #BC0101;
}
.rbx-btn-secondary-xs:active, .rbx-btn-secondary-xs:visited, .rbx-btn-secondary-xs.active {
    background-color: #BC0101;
}
.rbx-btn-secondary-xs {
    background-color: #BC0101;
    border-color: #BC0101;
}
.rbx-highlight {
    background-color: #BC0101;
}
input[type=text] {
    border: 1px solid #BC0101;
}
a.linkSmallBold:visited, a.linkMenuSink:visited {
    font-weight: normal;
    color: #BC0101;
}
a.linkSmallBold, a.linkMenuSink {
    font-weight: bold;
    color: #BC0101;
}
a:link, a:visited, a:active {
    color: #BC0101;
}
}
