	.ytp-bezel {
	position:absolute;
	left:50%;
	top:50%;
	width:1px;
	height:1px;
	z-index:17;
	margin-left:0px;
	margin-top:0px;
	background:rgba(0,0,0,.5);
	border-radius:26px;
	pointer-events:none
}
.ytp-big-mode .ytp-bezel {
	width:2px;
	height:2px;
	margin-left:-1px;
	margin-top:-1px;
	border-radius:39px
}
	
	.ytp-bezel-icon {
	width:1px;
	height:1px;
	margin:0 0 0 0;
  transform: translateY(-9.15px);
}
.ytp-big-mode .ytp-bezel-icon {
	width:2px;
	height:2px;
	margin:0 0 0 0;
	transform: translateY(-13.55px);
}
	
	.ytp-bezel[aria-hidden=true] {
	opacity:0;
	-moz-transform:scale(72);
	-ms-transform:scale(72);
	-webkit-transform:scale(72);
	transform:scale(72);
	-moz-transition:all .5s linear;
	-webkit-transition:all .5s linear;
	transition:all .5s linear;
}
	
