css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("workflowy.com"){
    
    .project > .name > .content, .nameEditor > textarea {
    

  font-size: 12pt !important;

}
    
    div.project.selected > .name > .content
{
    color: #000 !important; /*blue by default*/
font-size: 20pt !important;
}

parent > .name > .content


parent > .name > .content
{
    color: #2288FF !important; /*blue by default*/
}
    
    /*BULLETS*/

    .bullet, #bulletBucket .bulletBucketBullet {

    height: 18px;

    width: 18px;

    background-color: #bbf;

    border-radius: 12px;

}
    
.project.shared > .name > .bullet {
    background-color: #bbf;
}


    
/*TAGS*/

    .contentTag {

        font-weight: bold !important;
        padding: 5px !important; 
        line-height: 25px;
        margin-right: 6px !important;
        color: #28f !important; /* grey by default(#888888)*/
        font-size: 18px !important;
        background-color: #eff1ff;
    }
    
/*SEARCH RESULTS*/
    
    .page.searching .project .name, .page.searching .project .notes {
    display: none;
}

.page.searching .project.matches .name {
    display: block;
}

.page.searching .selected > .children > .project .project {
    margin: 0 0 4px;
}


.page.searching .contentTag {
    opacity: .3;
}

.page.searching .contentMatch {
    background: transparent;
}
    
    
    
.page.searching .contentTag {
    opacity: .3;
}

.page.searching .contentMatch {
    background: transparent;
}
    
.page.searching .contentMatch {
    background-color: #fff;padding:3px;
}
 .page.searching .contentTag {

display:none;
    }
 
    
    
    /*LINKS*/
    
    .selected .project > .name > .content a {
	color:#28F;
        font-weight: normal;
	text-decoration:none
}
    
    /*NOTES*/
    
        .noted > .notes > .content {
	color:#000;
}
   

    /*hide the tags when editing */

    .content.editing .contentTag {

        visibility: hidden;
    }
    

    /* remove the underline from tags */

    .content .contentTagText {

        text-decoration: none !important;
    }

/*double spacing between bullets*/

    .selected .project > .name > .content {
    margin-bottom: 10px;
}
}
