css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("radio.allgames.com"), domain("forums.allgames.com") {

* {
font-family: arial, sans-serif !important;
}

body {
color: #f5f5f5 !important;
background: #333 !important;
}

a:link {
color: #04C3C3 !important;
}

#network {
padding: 5px 0 !important;
background: #282828 !important;
border-bottom: 1px solid #060606 !important;
}
#network a {
color: #DFDFDF !important;
}
#network a:hover {
color: #fff !important;
}


#wrapper {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
width: auto !important;
border-top: 1px solid #4A4A4A !important;
}

#page{
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
width: 970px !important;
}


#mainlogo {
margin: 7px 0 8px -10px !important;
}
#mainlogo a {
background: transparent url("http://i279.photobucket.com/albums/kk133/rabidtapeworm/oldlogo.gif") no-repeat left top !important;
}


#user_panel {
background: #2b2b2b !important;
float: right !important;
margin: 29px 0 !important;
padding: 10px 15px !important;
border: 2px solid #222 !important;
height: auto !important;
width: auto !important;
-moz-border-radius: 5px !important
}
#user_panel h6 {
background: transparent !important;
width: auto !important;
border: 0 !important;
padding: 2px 5px 0 0 !important;
}
#user_panel div {
height: auto !important;
width: auto !important;
padding: 0 0 0 1px !important;
}
#user_panel input {
float: left !important;
font-size: 0.85em !important;
margin: 0 5px 0 0 !important;
padding: 2px 0 2px 3px !important;
border: 0 !important;
-moz-border-radius: 3px !important;
width: 150px !important;
}
#user_panel .btn {
float: left !important;
border: 0 !important;
color: #f1f1f1 !important;
margin: 0 !important;
padding: 1px 1px 2px !important;
-moz-border-radius: 3px !important;
height: auto !important;
width: auto !important;
cursor: pointer !important;
}


/****** TOP MAIN NAVIGATION ******/
#nav {
float: none !important;
width: auto !important;
height: auto !important;
clear: both !important;
overflow: hidden !important;
background: #111 !important;
border: 0 !important;
padding: 0 11px 0 15px !important;
margin: 0 !important;
-moz-border-radius: 7px 7px 0 0 !important;
}
#nav li {
float: left;
height: auto !important;
display: inline-block !important;
font-size: 16px !important;
background: transparent !important;
-moz-border-radius: 5px 5px 0 0 !important;
}
#nav li a {
display: inline-block !important;
height: auto !important;
line-height: 36px !important;
padding: 0 10px !important;
color: #dfdfdf !important;
-moz-border-radius: 5px 5px 0 0 !important;
}
#nav li a:hover {
color: #bf0000 !important;
background: #fff !important;
}
#nav li:hover, #nav li.sfhover { 
color: #bf0000 !important;
background: #fff !important;
}
#nav li ul {
background: #fff !important;
border: 0 !important;
padding: 0 0 5px !important;
-moz-border-radius: 0 0 5px 5px !important
}
#nav li li {
float: none !important;
width: auto !important;
display: block !important;
height: auto !important;
border: 0 !important;
}
#nav li li a {
float: none !important;
width: auto !important;
display: block !important;
line-height: normal !important;
padding: 3px 10px !important;
font-size: 14px !important;
color: #7f7f7f !important;
border-bottom: 1px solid #cfcfcf !important;
}
#nav li li a:hover {
color: #bf0000 !important;
padding: 3px 11px !important;
}
#nav li.search {
margin: 3px 0 0 0 !important;
border-left: 0 !important;
}
#nav li.search:hover {
background: #111 !important;
}
#nav input {
float: left !important;
font-size: 0.85em !important;
margin: 4px 0 0 0 !important;
padding: 3px 0 0 3px !important;
border: 0 !important;
-moz-border-radius: 3px !important;
width: 204px !important;
height: 19px !important;
}
#nav .btn {
float: left !important;
border: 0 !important;
color: #f1f1f1 !important;
margin: 4px 0 0 5px !important;
padding: 3px 1px 2px !important;
-moz-border-radius: 3px !important;
height: auto !important;
width: auto !important;
cursor: pointer !important;
}


/****** CONTENT HOLDS COLUMNS ******/
#cont {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
background: #222 !important;
border: 0 !important;
margin: 0 !important;
padding: 15px !important;
}


/****** THE THINNER COLUMN ******/
#left_clm {
float: right !important;
width: 250px !important;
padding: 0 !important;
margin: 0 !important;
background: transparent !important;
}
/****** THINNER COLUMN POD BOX ******/
.left_box {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
margin: 0 0 15px !important;
}
#whatson {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
background: #1a1a1a !important;
border: 0 !important;
font-size: .9em !important;
padding: 5px 9px !important;
-moz-border-radius: 0 0 4px 4px !important;
}
.left_box h2 {
padding: 5px 8px 3px !important;
background: #111 !important;
-moz-border-radius: 4px 4px 0 0 !important;
}
.left_box ul,
.left_box p {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
background: #1a1a1a !important;
border: 0 !important;
-moz-border-radius: 0 0 4px 4px !important;
}
.left_box li a {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
font-size: .9em !important;
border-bottom: 1px solid #2b2b2b !important;
border-bottom: 0 !important;
padding: 4px 9px !important;
}
.left_box li:last-child a {
border: 0 !important;
-moz-border-radius: 0 0 4px 4px !important;
}
.left_box li a:hover {
background-position: 239px center !important;
}
.foo_250x250 {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
margin-bottom: 15px !important;
}


/****** MAIN WIDER COLUMN ******/
#mid-clm {
float: left !important;
width: 675px !important;
margin: 0 !important;
padding: 0 !important;
}

/****** RADIO PLAYER ******/
#player {
margin: 0 15px 15px 0 !important;
width: 324px !important;
height: 108px !important;
}
#player object[width="300"],
#player embed[width="300"] {
margin: 0 0 15px 0 !important;
width: 324px !important;
height: 108px !important;
}


#foo_336x280 {
width: 336px !important;
height: 280px !important;
margin: 0 0 15px 0 !important;
background: transparent !important;
}

/****** SCHEDULE ******/
#schedule {
width: 324px !important;
height: auto !important;
margin: 0 0 15px 0 !important;
}
#schedule h3 {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
background: #111 !important;
font-size: 17px !important;
padding: 4px 8px 3px !important;
-moz-border-radius: 4px 4px 0 0 !important;
}
#schedule .rebroadcast {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
background: #1a1a1a !important;
border: 0 !important;
border-top: 0 !important;
height: auto !important;
padding: 0 10px !important;
}
#schedule .rebroadcast:hover, #schedule .rebroadcast:hover a {
background: #2b2b2b !important;
color: #f1f1f1 !important;
display: block !important;
}
#schedule .live {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
background: #a31400 !important;
border: 0 !important;
border-top: 0 !important;
height: auto !important;
color: #fff !important;
padding: 0 10px !important;
}
#schedule .live a {
color: #fff !important;
}
#schedule .live:hover, #schedule .live:hover a {
background: #2b2b2b !important;
color: #f1f1f1 !important;
display: block !important;
}
#schedule .datetime {
width: auto !important;
text-align: left !important;
padding: 5px 0 !important;
}
#schedule .showname {
float: right !important;
width: auto !important;
padding: 5px 0 !important;
}


/****** SHOW INFOS ******/
#showcase {
height: auto !important;
width: 360px !important;
margin: 0 15px 15px 0 !important;
}
#showcase h3 {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
display: block !important;
font-size: 13pt !important;
padding: 5px 10px 3px !important;
background: #111 !important;
-moz-border-radius: 4px 4px 0 0 !important;
}
#showcase span {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
height: auto !important;
display: block !important;
padding: 3px 10px !important;
font-size: 14px !important;
background: #000 !important;
color: #fff !important;
}
#showcase p {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
height: auto !important;
display: block !important;
background: #1a1a1a !important;
color: #fff !important;
padding: 8px 10px 9px !important;
}
#bigalbum {
margin: 0 0 15px 0 !important;
}

.title {
padding: 2px 0 0 !important;
}


.foo_text {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
margin: 0 0 15px !important;
padding: 10px !important;
background: #1a1a1a !important;
}



/****** TABS FOR NEWS ******/
#tabs2 {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
padding: 4px 15px 0 !important;
margin: 0 !important;
background: #111 !important;
-moz-border-radius: 4px 4px 0 0 !important;
}
ul#tabs {
margin: 0 !important;
padding: 0 !important;
height: 30px !important;
}
#tabs li {
float: none !important;
width: auto !important;
overflow: hidden !important;
display: inline-block !important;
margin: 0 !important;
padding: 5px 0 !important;
}
#tabs li a {
float: none !important;
width: auto !important;
overflow: hidden !important;
font-size: .95em !important;
font-weight: normal !important;
padding: 5px 10px !important;
margin: 0 !important;
color: #9f9f9f !important;
background: #222 !important;
-moz-border-radius: 2px 2px 0 0 !important;
}
#tabs li a:hover {
color: #fff !important;
background: #333 !important;
}

#tabs li.selected a {
background: #1a1a1a !important;
color: #fff !important;
font-weight: bold !important;
}

/****** NEWS AND PODCAST HOLDER ******/
.news_area {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
padding: 15px !important;
background: #1a1a1a !important;
}
.item {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
padding: 0 0 15px !important;
margin: 0 0 15px !important;
}
.icon {
margin: 4px 0 0 !important;
}
.meta {
color: #7f7f7f !important;
}
.news_content {
float: none !important;
width: auto !important;
overflow: hidden !important;
margin: 0 0 0 115px !important;
}
.news_area ul {
float: none !important;
width: auto !important;
overflow: hidden !important;
}

#centertext {
height: auto !important;
}


.show_list {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
padding: 0 0 15px !important;
margin: 0 0 15px !important;
border-bottom: 1px solid #2B2B2B !important;
}
.icon2 {
margin: 4px 0 0 !important;
}
.sml_discr {
float: none !important;
width: auto !important;
overflow: hidden !important;
margin: 2px 0 0 233px !important;
}


/****** PAGINATION ******/
.pages {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
padding: 0 0 15px !important;
margin: 0 0 15px !important;
border-top: 0 !important;
border-bottom: 1px solid #2b2b2b !important;
}
.pages:last-child {
padding: 0 !important;
margin: 0 !important;
border-bottom: 0 !important;
}
.pages ul {
float: none !important;
width: auto !important;
clear: both !important;
overflow: hidden !important;
border: 0 !important;
margin: 0 !important;
padding: 0 !important;
}
.pages li {
display: inline-block !important;
border: 0 !important;
margin: 0 1px !important;
padding: 0 !important;
color: #9F9F9F !important;
}
.pages li a {
display: inline-block !important;
border: 0 !important;
margin: 0 !important;
padding: 0 5px !important;
color: #9F9F9F !important;
}
.pages li a:hover {
background: #2b2b2b !important;
}
.pages ul li.current {
background: #04C3C3 !important;
margin: 0 2px !important;
padding: 0 5px !important;
color: #111 !important;
}


/****** FORUMS ******/
.page a:hover {
color: #fff !important;
}
.tborder {
border: 0 !important;
background: transparent !important;
}
table .tborder {
border: 0 !important;
background: transparent !important;
}
.tcat {
background: #2b2b2b !important;
}
.thead {
color: #fff !important;
border: 0 !important;
}
.alt1, .alt1Active {
color: #fff !important;
background: #1a1a1a !important;
border: 0 !important;
}
.alt2, .alt2Active {
color: #fff !important;
background: #2b2b2b !important;
border: 0 !important;
}
td[style="border-right: 1px solid rgb(127, 164, 223);"] {
padding: 14px 20px !important;
}
hr {
color: #2b2b2b !important;
}


#countdown,
.foo_728x90,
.foo_feedburner,
#ad-clm,
#schedule .timezone,
#ads_336x280,
#footer,
img[src="http://www.allgames.com/oldsite/images/leftedge_septop.gif"],
img[src="http://www.allgames.com/oldsite/images/leftedge_line2top.gif"],
img[src="http://www.allgames.com/oldsite/images/leftedge_line1top.gif"],
img[src="http://www.allgames.com/oldsite/images/rightedge_septop.gif"],
img[src="http://www.allgames.com/oldsite/images/leftedge_line2bot.gif"],
img[src="http://www.allgames.com/oldsite/images/leftedge_line1bot.gif"],
img[src="images/icons/icon1.gif"],
img[src="images/icons/icon2.gif"],
img[src="images/icons/icon3.gif"],
img[src="images/icons/icon4.gif"],
img[src="images/icons/icon5.gif"],
img[src="images/icons/icon6.gif"],
img[src="images/icons/icon7.gif"],
img[src="images/icons/icon8.gif"],
img[src="images/icons/icon9.gif"],
img[src="images/icons/icon10.gif"],
img[src="images/icons/icon11.gif"],
img[src="images/icons/icon12.gif"],
img[src="images/icons/icon13.gif"],
img[src="images/icons/icon14.gif"],
img[src="images/misc/multipage.gif"],
img[src="images/agdefault/statusicon/user_offline.gif"],
img[alt="Default"],
#Table1 td[width="1"],
#Table1 td[width="8"],
#Table1 td[width="176"],
#Table2 td[height="141"],
#Table2 td[height="30"],
#Table2 td[height="74"],
#Table6,
#Table5,
#Table3,
form[action="index.php"],
hr {
display: none !important;
}

}
