@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*Tabs*/ 
/*gris: 31363b gris claro: 64686b     azul: 4d68b3 azul claro: 7796f1 */

 #TabsToolbar {
        background: rgb(251,251,251) !important;
        margin: 0 !important;
    }
     
     
    #TabsToolbar .tabbrowser-tabs {
        min-height: 23px !important;
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        text-align: center !important;
        margin-left: 0 !important;
        margin-right: 0 !important;
        background: #31363b !important; /*Fondo del resto de la barra*/
    }
     
    #tabbrowser-tabs tab .tab-close-button {
        display: inline !important;
    }
     
    .tabbrowser-tab:not([pinned]) .tab-icon-image {
        display: inline !important;
    }
     
     
    #TabsToolbar .tabbrowser-tab {
        text-shadow: 0 0 4px rgba(80,80,80,0) !important;
        padding: 0px 0px !important;
        border-radius: 0px !important;
        background: #31363b !important;
        background-clip: padding-box !important;
        transition: all .1s !important;
        margin: 0px !important;
        color: rgb(180,180,180) !important;
        font-family: 'Ubuntu Medium' !important;
        font-size: 13px !important;
        min-height: 13px !important;
    }
     
    #TabsToolbar .tabs-newtab-button {
        border: none !important;
        background: transparent !important;
        background-clip: padding-box !important;
        transition: all .1s !important;
    }
    #TabsToolbar .tabbrowser-tab[selected] {
        color: #fff !important;
        background: #64686b !important;
        background-clip: padding-box !important;
    }
     
    #TabsToolbar .tabs-newtab-button:hover,
    #TabsToolbar .tabbrowser-tab:hover:not([selected]) {
        background-color: #64686b !important;
        color: #fff !important;
    }
     
    #TabsToolbar .tab-background {
        margin: 0 !important;
        background: transparent !important;
    }
     
    #TabsToolbar .tab-background-start,
    #TabsToolbar .tab-background-end {
        display: none !important;
    }
     
    #TabsToolbar .tab-background-middle {
        margin: -4px -2px !important;
        background: transparent !important;
    }
     
    #TabsToolbar .tabbrowser-tab:after,
    #TabsToolbar .tabbrowser-tab:before {
        display: none !important;
    }
     
    #TabsToolbar .tabs-newtab-button {
        border: none !important;
        margin: 0 !important;
        width: auto !important;
        padding: 0 5px !important;
    }
     

#addon-bar,
    #PersonalToolbar,
    #nav-bar {
        background: #fff !important;
        box-shadow: none !important;
        background-clip: padding-box !important;
        border-radius: 0 !important;
    }
     
     
#urlbar{
    border: 1px solid #7796f1 !important;
    background:#fff !important;
    color:rgb(10,10,10) !important;
}

#back-button, #forward-button{
    display: none !important;
}
