css:
@-moz-document domain("x.sankuai.com") {

body {
	background-color: #d9dbd6;
}

@keyframes animatedBackground {
	from { background-position: 0 0; }
	to { background-position: 100% 0; }
}
body:after {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 152px;
    background-color: #22956c;
    display: block;
}
.panel-left-chat .panel-list-menu.tab-chat .menu-tab-item-chat .icon-tab, .panel-left-chat .panel-list-menu.tab-contact .menu-tab-item-contact .icon-tab, .panel-left-chat .panel-list-menu.tab-room .menu-tab-item-room .icon-tab {
	color: #20956d;
}
.dx-sh div.panel.panel-chat.css3-slide, .panel-chat {
    width: 940px;
}
#dx_leftPanelChat ::-webkit-scrollbar {
    width: 0;
}
#left-menu-wrapper {
    display: none;
}
/* left */
.panel-chat .panel-list-content {
	border-radius: 0;
}
#dx_leftPanelChat .panel-left-chat {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
}
.panel-chat .panel-list-content {
    border-bottom-right-radius: 0;
}
#dx_leftPanelChat, #ui_chart_panel {
    box-shadow: none;
}
#dx_leftPanelChat .panel-left-header {
    background: #eeeeee;
    /*border-top-right-radius: 0;
    border-bottom-right-radius: 0;*/
    border-radius: 0;
    box-shadow: 0 1px 1px rgba(0,1,3,0.1);
}
#dx_tips {
	background: transparent;
    color: #000;
}
.panel-chat .myProfile img {
	position: absolute;
    top: 4px;
    left: 10px;
    z-index: 100;
    width: 35px;
    height: 35px;
    border-radius: 35px;
    -moz-border-radius: 35px;
    -webkit-border-radius: 35px;
    border: 2px solid #e8f2ff;
    box-shadow: 0 0 0 rgba(0,0,0,.8);
    cursor: pointer;
}
.panel-chat .myProfile .info {
    margin: 12px 0 0 55px;
}
.panel-chat .item-info-chat {
	background-color: #fff;
}
.panel-chat .myProfile .nickName, .panel-left-header a.globalMenu {
	color: #000;
}
.panel-left-chat .panel-search {
	border-top-color: #eeeeee;
}
#dx_leftPanelChat .panel-left-header .split {
    background-image: none;
}
#dx_leftPanelChat .item-info-chat, #dx_leftPanelChat .item-info-chat .avatar_wrap {
    transition: all 0.3s;
    -moz-transition: all 0.3s;
    -webkit-transition: all 0.3s; /* Safari下会引起搜索栏字体的变化，先忽略 */
    -o-transition: all 0.3s; 
}
#dx_leftPanelChat .item-info-chat:hover {
	background: #FAFAFB;
    color: #000;
    padding-left: 17px;
}
#dx_leftPanelChat .item-info-chat:hover .avatar_wrap {
    transform:scale(1.2,1.2);
	-ms-transform:scale(1.2,1.2); 
	-moz-transform:scale(1.2,1.2); 
	-webkit-transform:scale(1.2,1.2); 
	-o-transform:scale(1.2,1.2);
}
#dx_leftPanelChat .selected.item-info-chat {
    background: #e9eaec;
    border-right: 2px solid #20956d;
    color: #000;
}
.panel-chat .item-info-chat {
    border-top-color: #FAFAFB;
    height: 64px;
}
.panel-chat .selected .vcard .name, .panel-chat .selected .vcard .org, .panel-chat .selected.item-info-chat .info .desc, .panel-chat .selected.item-info-chat .info .name, .panel-chat .selected.item-info-chat .time {
    color: #333;
}
#dx_leftPanelChat .avatar_wrap img, .list-info .vcard .avatar {
    border-radius: 40px;
    border: 2px solid #efefef;
    /*padding: 1px;*/
}
.list-info .vcard {
	height: 40px;
}
.contactListContent .tree, .panel-chat .contact-list, .panel-list-content .list-info {
	background-color: #fff;
}
.contactListContent .tree h4, .list-info .vcard {
	border-top-color: #FAFAFB;
}
.list-info .vcard.selected {
    background: -webkit-linear-gradient(left, #ffffff, #e9eaec);
	background: linear-gradient(to right, #ffffff, #e9eaec);
    border-right: 2px solid #20956d;
}
.list-info .vcard .detail, .list-info .vcard .name {
	color: #333;
    line-height: 40px;
    font-size: 14px;
}
.contactListContent h3 {
	background-color: #e9eaec;
    border-bottom-color: #FAFAFB;
    text-shadow: none;
}
/* dialog */ 
.dialog-person-vcard {
	box-shadow: none;
}
.window {
	border-radius: 0;
    background-color: #fff;
    border-top: 2px solid #20956d;
    box-shadow: none;
}
.dialog-person-vcard .window .content .panel-info-base .photo-vcard {
	border-radius: 70px;
}
.dialog-person-vcard .window .content .panel-info-description, .dialog-person-vcard .window .content .panel-info-base,
.window .content .detail {
	border-bottom-color: #FAFAFB;
    border-top-color: #FAFAFB;
}
.window .content .detail .vcard .avatar img {
	border-radius: 40px;
}
/* right */
#ui_chart_panel {
    margin-left: 0;
}
.split.menu-split {
	background-image:-webkit-linear-gradient(to top, #f1f1f1, #999999, #f1f1f1);
  	background-image:linear-gradient(to top, #f1f1f1, #999999, #f1f1f1);
    width: 1px;
}
.panel-chat .chat {
    margin-left: 280px;
    border-radius: 0;
}
.panel-chat .hd-chat-roster-title {
	background-color: #eeeeee;
    border-bottom-color: #eee;
    border-left: 1px solid #eeeeee;
    background-image: none;
    border-top-right-radius: 0;
}
.panel-chat .groupNameWrap {
	border-radius: 0;
    color: #000;
}
.panel-chat-container .btn-group-add a, .panel-chat-container .btn-roster-list a.active {
	color: #20956d;
}
.panel-chat .panel-chat-wrap {
    border-left: 1px solid #E5E6EA;
    /*background-color: #e3dbd8;*/
}
#ui_chart_panel ::-webkit-scrollbar {
    width: 2px;
}
.panel-chat-container .hd-chat-title {
	border-radius: 0;
}
.panel-chat .chatNameWrap .chatName{
	font-family: Helvetica,Arial;
	padding-top: 12px;
	color: #000;
}
.hd-chat-title .editChatTitleWrap input {
	color: #000;
}
.panel-chat-container .menu-wrapper a, .panel-chat-container .btn-roster-list a {
	color: #000;
}
.bubble-list .time p {
	background:#f1f1f1;
}
#ui_chart_panel #dx_chatContainer {
    border-radius: 0;
}
#ui_chart_panel .panel-chat-container .hd-chat-title {
    background: #eeeeee;
    border-bottom-color: #eeeeee;
    border-radius: 0;
    color: #000;
    box-shadow: 0 1px 1px rgba(0,1,3,0.1);
}
.panel-chat .panel-chat-wrap, .chatsplit .panel-chat-roster {
	background-color: #f1f1f1;
}
.nx_groupchat .item .talker-name {
	color: #4f4f4f;
}
.bubble-list .cont {
	background-color: #fff;
	border-color: #ffffff;
}
.bubble-list .cont:after {
	position: absolute;
    top: 14px;
    border: 6px solid transparent;
    content: " ";
    border-right-color: #ffffff;
    border-right-width: 6px;
    left: -12px;
}
.bubble-list .cont:before {
	background: none;
}
.bubble-list .me .cont {
	background-color: #b2e281;
	border-color: #20956d;
	border-width:0;
	color: #000;
}
.bubble-list .me .cont:after {
	position: absolute;
    top: 14px;
    border: 6px solid transparent;
    content: " ";
    border-left-color: #b2e281;
    border-left-width: 6px;
    left: 100%;
}
.bubble-list .emotion:after {
	display:none;
}
.bubble-list .me .cont a {
	color: #0070C9;
}
.bubble-list .me .cont:before {
	background:none;
}
#ui_chart_panel #dx_chatWrap .person img {
    border-radius: 40px;
}
#ui_chart_panel #chat_editor {
	background-color: #edecea;
    border-left: 1px solid #E5E6EA;
}
.panel-input-area .chatInput {
	box-shadow: none;
}
.panel-input-area .attach a {
    background: url("http://7j1wtm.com1.z0.glb.clouddn.com/sprite_sheet.png");
}
.panel-input-area button#sendMessage.btn-chatSend {
	background-image: none !important;
}
.panel-input-area button.btn-chatSend[disabled=disabled] {
	color: #fff;
    background-color: #b2e281 !important;
}
.panel-input-area button.btn-chatSend:hover {
	color: #000;
    background-color: #b2e281 !important;
}
.panel-input-area button.btn-chatSend[disabled=disabled] {
	color: #fff;
    background-color: rgba(0,0,0,0.3) !important;
}
.panel-input-area button.btn-primary {
	color: #000;
    background-color: #b2e281 !important;
}
.panel-chat-container .chatsplit .list-chat-roster {
	background-color: #f1f1f1;
}
.panel-chat-container .list-roster .photo-roster {
	border-radius: 30px;
}
.panel-chat-container .list-roster .item-roster {
	border-bottom-color: #E6E6EA;
}
/* cloud */
.dxplugin_mbox-group-header {
	border-top-left-radius: 0;
    border-top-right-radius: 0;
    background: #eeeeee;
}
.dxplugin_mbox-group-header-back, .dxplugin_mbox-group-header-name {
	color: #333;
}
div#fileDragArea {
	background: #f5f5f5;
}
#J_FBMan {
	display: none;
}
.ft-chat .logo-ft {
	display: none;
}
.ft-chat p {
	text-indent: -999em;
}
/* cloud position */
.dxplugin_mbox-group-header, .dxplugin_mbox-to-mbox {
	margin-left: 280px;
}
.dxplugin_mbox-to-mbox {
	width: 660px;
}
.dxplugin_mbox-group-header-back-icon {
	left: 290px;
}
.dxplugin_mbox-group-header-back {
	left: 312px;
}
.dxplugin_mbox-group-path, .dxplugin_mbox-file-list {
	left: 280px;
}
/* emoji */
#ui_chart_panel .emojiPanel {
	box-shadow: none;
    border-color: #E8EAEA;
}
#ui_chart_panel .faceBoxWrap img, .xiaoTuanBox img {
    transition: transform 0.5s;
    -moz-transition: -moz-transform 0.5s;
    -webkit-transition: -webkit-transform 0.5s; 
    -o-transition: -o-transform 0.5s; 
}
#ui_chart_panel .faceBoxWrap img:hover, .xiaoTuanBox img:hover {
    transform:scale(1.3,1.3);
	-ms-transform:scale(1.3,1.3); 
	-moz-transform:scale(1.3,1.3); 
	/*-webkit-transform:scale(1.3,1.3);//Safari下会引起左侧字体的变化，先不支持Safari*/
	-o-transform:scale(1.3,1.3);
}
/* footer */
.footer .btn.btn-primary {
	background: #20956d;
    border-color: #20956d;
    box-shadow: none;
    color: #fff;
    text-shadow: none;
}
.footer .btn.btn-primary:hover {
	background: #0E8D62;
    border-color: #0E8D62;
}
.footer .btn.btn-success, .ui_roomVcard .btn-contact {
	background: #E3A016;
    border-color: #E3A016;
    box-shadow: none;
    color: #fff;
    text-shadow: none;
}
.footer .btn.btn-success:hover, .ui_roomVcard .btn-contact:hover {
	background: #D89711;
    border-color: #D89711;
}

#downloadApp {
    display: none;
}

}
