css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arcgames.com") {
  
#Head
  {
    background: #000000;
    background-image: url("http://ricardo.ishibashi.com.br/neverwinter/img/main-background.jpg") !important; 
    background-position: 52% 36%; 
    text-align:center;   
  }
   
body 
  {
         font-family: sans-serif !important; font-size: 15px !important;
         background: #000000 !important;                         
         background-position: bottom !important;
         background-image: url("http://ricardo.ishibashi.com.br/neverwinter/img/main-background-footer.jpg") !important; 
         background-repeat: no-repeat !important;                         
         
   }

.forum-body.node-nw, .forum-body {
  background: #000000 ;
}

.module-header-platfrom hr.line{
  border-width: 0px 0px 1px;
  background: #000 !important;
} 

textarea { color: #A4A4A4 ;}  

.P.PageDescription { font-size: 0%; }

.TextBox { background-color: #000000 !important; }

.MessageList.Comments {
  padding-left: 0px; 
  background: transparent;
}

.CommentFormWrap { background: #1c110e; }  

a {
  color: #ffbe32 !important;
  font-family: "Sava Pro" !important;
  }

.MiniPager a {
  text-decoration: underline;
  color: #6C89C1 !important;
  }
  
.Pager span, .Pager a, .Button, div.UserSpoiler div.SpoilerTitle input {
  border-radius: 5px;
  border-color: #545454;
  background: transparent linear-gradient(rgb(59, 36, 29), rgb(38, 23, 19)) repeat scroll 0% 0%;
  color: rgb(255, 190, 50) !important;
  font-family: "Sava Pro";
  font-weight: normal;
  text-decoration: none;
  cursor: pointer;
  transition: all 0.5s ease 0s;
  }
  
.Pager span:hover, .Pager a:hover, .Button:hover, div.UserSpoiler div.SpoilerTitle input:hover {
    box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.8), 0px 0px 6px 0px rgba(255, 255, 255, 0.8);
    transition: all 0.1s ease 0.1s;
}

.ProfilePhotoMedium {
  width: 100px;
  height: 100px;
  margin: 10%; 
}

.CommentHeader span {
  font-size: 9px;
}
  
.CommentHeader{
  box-sizing: content-box;
  width: 120px;
  position: absolute;
  background-color: rgba(28, 17, 14, 0.5);
  }
  
.ItemDiscussion .Item-Header, .ItemDiscussion .Item-BodyWrap{
    background-color: rgba(28, 17, 14, 0.5);
  }

.AuthorWrap, .Meta.DiscussionMeta, .Meta.CommentMeta.CommentInfo{
    margin: -5px;
    text-align: center;
  }
  
div.UserSpoiler {
  background-color: #211b19;
  border-radius: 5px;
  border: 1px solid #545454;
  }
  
.Comment .Item-BodyWrap .Reactions{
  text-align: bottom;
  }

.Comment .Item-BodyWrap{
  padding: 5px 5px 0px;
  position: relative;
  left: 137px;
  box-sizing: content-box;
  width: 85.7%;
  border: 1px solid #545454;
  background-color: rgba(28, 17, 14, 0.5);
  }

.Comment .Options {
  position: absolute;
  right: -25px;
  top: 0px;
  }
  
.Comment .Item-BodyWrap .Message {
  min-height: 174px;
  }
  
.UserQuote{
  background: #211b19;
  border-radius: 5px;
  }

.Discussion .UserSignature{
  margin-top: 0px;
  padding-top: 5px;
  padding-bottom: 0px;
  max-height: 100px;
  overflow: hidden;
}

.Signature img, .UserSignature img {
  max-height: 100px !important;
  }
  
.Discussion .PageTitle h1, h1.H {
  font-size: 22px;
  font-family: "Sava Pro";
  padding-bottom: 0px;
  margin-bottom: 5px;
  text-indent: -290px;
  text-align: center;
  }

.CategoryTable > thead > tr td, .DiscussionsTable > thead > tr td {
  background-color: rgba(28, 17, 14, 0.6);
  }

.CategoryTable > tbody > tr, .DiscussionsTable > tbody > tr {
  background-color: rgba(28, 17, 14, 0.3) !important;
  }

.Pager .Highlight {
  background: #1c110e none repeat scroll 0% 0%;
  box-shadow: 0px 0px 7px 0px #B17464;
  border-radius: 5px !important;
  }
   
.CommentHeader span.PostCount, .DiscussionHeader span.PostCount {
  font-weight: bold;
  font-family: "Sava Pro";
  display: inline-block;
  margin-right: 20px;
  }
  
.MItem:first-child {
  margin-left: 0px;
  white-space: nowrap !important;
  font-family: "Sava Pro";
  font-weight: bold;
  }
  
span.Rank.MItem {
  margin-right: 10px;
  font-family: "Sava Pro";
  font-weight: bold;
  color: #ffce66;
  font-size: 10px; 
  max-width: 130px;
  white-space: pre-wrap;
  }
  
.CommentHeader span.DateUpdated, .DiscussionHeader span.DateUpdated {
  position: absolute;
  visibility: hidden;
  }
  
.DiscussionHeader span {
  line-height: 55px;
  font-size: 12px !important;
  }
  
#Panel {
  position: absolute;
  }
  
.Messages .ContentColumnExtend {
  min-height: 465px;
  }

/*@media screen and (max-device-aspect-ratio: 3/2) {
 #Content.Column.ContentColumn > .DataTableWrap > table.DataTable.CategoryTable {
  width: 104%;
  position: relative !important;
  right: 24%;
  }  
 }*/

}
