css:
/* i really want this to be global */

@namespace html url(http://www.w3.org/1999/xhtml);
/* YT */

/* .html5-video-player.ytp-autohide div.ytp-chrome-bottom {
    opacity: 0.2;
} */
.ytp-watch-later-button,
.ytp-share-button {
    opacity: 0.12!important;
    ;
}
.ytp-watch-later-button:hover,
.ytp-share-button:hover {
    opacity: 0.62!important;
    ;
}

/* timeline */
.html5-video-player.ytp-autohide div.ytp-chrome-bottom:hover {
    opacity: 1
}
.html5-video-player div.ytp-chrome-bottom {
    opacity: 0.2;
}
.html5-video-player.ytp-autohide div.ytp-chrome-bottom {
    opacity: 0;
}
.html5-video-player div.ytp-chrome-bottom:hover {
    opacity: 0.85;
}
.html5-video-player div.ytp-gradient-bottom {
    opacity: 0.2;
}
/* #movie_player.html5-video-player.ytp-hide-info-bar.ytp-autohide div.ytp-gradient-bottom {opacity:0;} */

.html5-video-player div.ytp-gradient-bottom:hover {
    opacity: 1;
}
.html5-video-player div.ytp-iv-video-content div.video-annotations,
div#movie_player.html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled div.ytp-iv-video-content div.video-annotations {
    opacity: 0.12!important;
}
.html5-video-player div.ytp-iv-video-content div.video-annotations:hover,
.html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled div.ytp-iv-video-content div.video-annotations:hover {
    opacity: 0.82 !important;
}
/* .html5-video-player div.ytp-chrome-top.ytp-watch-later-button-visible.ytp-share-button-visible {
    opacity: 0.2;
} */
/* .html5-video-player.ytp-autohide div.ytp-chrome-top.ytp-watch-later-button-visible.ytp-share-button-visible {
    opacity: 0;
} */


/* top */
.ytp-chrome-top {
    opacity: 0.12
}.ytp-chrome-top:hover {
    opacity: 0.85
}
/* .html5-video-player .ytp-title-text, .html5-video-player .ytp-title-channel {
    opacity: 0.12 !important
}
.html5-video-player .ytp-title-text:hover, .html5-video-player:hover .ytp-title-channel:hover {
    opacity: 0.45 !important
} */

/* .html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled.ytp-autohide div.ytp-chrome-top.ytp-watch-later-button-visible.ytp-share-button-visible {
    opacity: 0.2;
} */
.html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled.ytp-autohide button.ytp-button.ytp-cards-button {
    opacity: 0.2;
}
/* .html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled div.ytp-chrome-top.ytp-watch-later-button-visible.ytp-share-button-visible {
    opacity: 0;
} */
.html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled button.ytp-button.ytp-cards-button {
    opacity: 0;
}
/* .html5-video-player div.ytp-chrome-top.ytp-watch-later-button-visible.ytp-share-button-visible:hover,
.html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled.ytp-fullscreen.paused-mode div.ytp-chrome-top.ytp-watch-later-button-visible.ytp-share-button-visible:hover,
.html5-video-player.ytp-video-ad-learn-more-ui.ytp-iv-drawer-enabled button.ytp-button.ytp-cards-button:hover {
    opacity: 0.85;
} */
.html5-video-player.ytp-video-ad-learn-more-ui div.ytp-tooltip.ytp-bottom div.ytp-tooltip-bg {
    opacity: 0.45;
}
.html5-video-player div.ytp-ce-element.ytp-ce-video.ytp-ce-element-show {
    opacity: 0.3;
}
.html5-video-player div.ytp-ce-element.ytp-ce-video.ytp-ce-element-show:hover {
    opacity: 1;
}
.html5-video-player.paused-mode.ytp-expand-pause-overlay div.ytp-pause-overlay.ytp-scroll-min {
    opacity: 0.125;
}
.html5-video-player.paused-mode.ytp-expand-pause-overlay div.ytp-pause-overlay.ytp-scroll-min:hover {
    opacity: 1;
}
/* advert */

.ad-container.ad-container-single-media-element-annotations.ad-overlay > .ima-container {
    display: none !important;
    opacity: 0.08 !important;
}
.ad-container.ad-container-single-media-element-annotations.ad-overlay > .ima-container:hover {
    opacity: 1 !important;
}
/* vimeo */

#mediaplayer > #player > #controls,
#mediaplayer > #player > #playerHeader {
    opacity: 0.50;
}
.player > .controls-wrapper > .controls,
div.player_area-wrapper.js-player_area-wrapper div.player_area .with-sticky-custom-logo div.controls-wrapper div.sidedock {
    opacity: 0.25;
}
div.player_area-wrapper.js-player_area-wrapper div.player_area .with-sticky-custom-logo div.controls-wrapper div.sidedock:hover {
    opacity: 0.85;
}
div.player_area-wrapper.js-player_area-wrapper div.player_area .with-sticky-custom-logo div.controls-wrapper div.sidedock.invisible {
    opacity: 0;
}
/* jwplayer */

.jwplayer #player_controlbar.jwcontrolbar {
    opacity: 0.50 !important;
}
.html5-video-player > .html5-video-controls:hover,
.html5-video-player.paused-mode > .ytp-chrome-bottom:hover,
.html5-video-player.playing-mode > .ytp-chrome-bottom:hover,
.html5-video-player.paused-mode > .ytp-chrome-top:hover,
.html5-video-player.playing-mode > .ytp-chrome-top:hover,
.player > .controls-wrapper > .controls:hover,
.jwcontrols > #player_controlbar.jwcontrolbar:hover,
#mediaplayer > #player > #controls:hover,
#mediaplayer #player #playerHeader:hover {
    opacity: 0.85 !important;
}
/* mp4upload */

div#player_code #player_code_view.jwmain .jwcontrols #player_code_controlbar.jwcontrolbar {
    opacity: 0.25!important;
}
div#player_code #player_code_view.jwmain .jwcontrols #player_code_controlbar.jwcontrolbar:hover {
    opacity: 0.85!important;
}
div#player_code.jwplayer.aspectMode.playlist-none.jw-user-inactive #player_code_view.jwmain span.jwcontrols div#player_code_dock.jwdock {
    opacity: 0.3!important;
}
#player_code_view.jwmain .jwcontrols img#player_code_logo.jwlogo {
    opacity: 0.25!important;
}
#player_code_view.jwmain .jwcontrols img#player_code_logo.jwlogo:hover {
    opacity: 0.85!important;
}
/* anyfiles */

.video-js.vjs-controls-enabled.vjs-workinghover.vjs-user-active div.vjs-control-bar {
    opacity: 0.25;
}
.video-js.vjs-controls-enabled.vjs-workinghover.vjs-user-active div.vjs-control-bar:hover {
    opacity: 0.85;
}
.video-js.vjs-controls-enabled.vjs-workinghover.vjs-user-active aside.vjs-social-share {
    opacity: 0.32;
}
.video-js.vjs-controls-enabled.vjs-user-inactive aside.vjs-social-share {
    opacity: 0.12;
}
/* d-on */

div#playerv5_box div#player div.dmp_Player div.dmp_ControlBar.dmp_will-transition.dmp_is-transitioned--fadeinslide {
    opacity: 0.25;
}
div#playerv5_box div#player div.dmp_Player div.dmp_ControlBar.dmp_will-transition.dmp_is-transitioned--fadeinslide:hover {
    opacity: 0.85;
}
/* tune.pk */

.player-elements .player-controlbar {
    opacity: 0.2;
}
.player-elements .player-controlbar:hover {
    opacity: 1;
}
.player-container #tpkp-toolbar {
    opacity: 0.12;
}
.player-container #tpkp-toolbar:hover {
    opacity: 0.42;
}
div.media-information-container.top div.media-information {
    opacity: 0.2;
}
div.media-information-container.top div.media-information:hover {
    opacity: 1;
}
/*Vidloxtv */

div#vplayer div.media-control.live div.media-control-layer {
    opacity: 0.25;
}
div#vplayer div.media-control.live div.media-control-layer:hover {
    opacity: 0.3;
}
div#vplayer div div a.dwnld img {
    opacity: 0.2
}
div#vplayer div div a.dwnld img:hover {
    opacity: 0.85;
}
.media-control-background:before {
    opacity: 0.25;
    background-color: #1d1d1d !important;
}
/* Thevideome */

div#vplayer.jwplayer.jw-reset div.jw-controls.jw-reset div.jw-controlbar.jw-background-color.jw-reset {
    opacity: 0.2 !important;
}
/* div#vplayer.jwplayer.jw-reset.jw-flag-user-inactive div.jw-controls.jw-reset div.jw-controlbar.jw-background-color.jw-reset {opacity: 1 !important;} */

div#vplayer.jwplayer.jw-reset div.jw-controls.jw-reset div.jw-controlbar.jw-background-color.jw-reset:hover {
    opacity: 1 !important;
}
div#vplayer.jwplayer.jw-reset.jw-state-playing.jw-flag-user-inactive div.jw-controls.jw-reset div.jw-controlbar.jw-background-color.jw-reset {
    opacity: 0 !important;
}
/* Okru */

div.html5-vpl_panel.vpl-ani-bottom,
div.html5-vpl_shadow.vpl-ani-bottom,
div.html5-vpl_title.vpl-ani-top {
    opacity: 0.25;
}
div.html5-vpl_panel.vpl-ani-bottom:hover,
div.html5-vpl_shadow.vpl-ani-bottom:hover,
div.html5-vpl_title.vpl-ani-top:hover {
    opacity: 0.85;
}
/* Rapidvideo */

.jwplayer div.jw-controls.jw-reset div.jw-nextup-container.jw-reset {
    opacity: 0.2;
}
.jwplayer div.jw-controls.jw-reset div.jw-nextup-container.jw-reset:hover {
    opacity: 0.85;
}
.jwplayer div.jw-controls.jw-reset div.jw-controlbar.jw-background-color.jw-reset {
    opacity: 0.2;
}
.jwplayer div.jw-controls.jw-reset div.jw-controlbar.jw-background-color.jw-reset:hover {
    opacity: 0.95;
}
/* VK player */

.videoplayer .videoplayer_ui._playing div.videoplayer_controls._has_quality,
div.videoplayer div.videoplayer_ui._fullscreen div.videoplayer_controls._has_quality {
    opacity: 0.12;
}
.videoplayer .videoplayer_ui._playing div.videoplayer_controls._has_quality:hover,
div.videoplayer div.videoplayer_ui._fullscreen div.videoplayer_controls._has_quality:hover {
    opacity: 1;
}
div.videoplayer_title a.videoplayer_title_link {
    opacity: 0.42;
}
/* Vidfile */

div#player.video-js.vjs-user-active div.vjs-control-bar {
    opacity: 0.25;
}
div#player.video-js.vjs-user-active div.vjs-control-bar:hover {
    opacity: 0.75;
}
/* sibnet */

div#video_html5_wrapper.video-js.vjs-controls-enabled.vjs-user-inactive div.vjs-control-bar {
    opacity: 0.2;
}
div#video_html5_wrapper.video-js.vjs-controls-enabled.vjs-user-active div.vjs-control-bar {
    opacity: 0.3;
}
div#video_html5_wrapper.video-js.vjs-controls-enabled.vjs-user-active div.vjs-control-bar:hover {
    opacity: 0.75;
}
div#video_html5_wrapper.video-js.vjs-controls-enabled a#vjs-logobrand-image-destination,
div#video_html5_wrapper.video-js.vjs-controls-enabled div.vjs-control,
div#video_html5_wrapper.video-js.vjs-controls-enabled div.vjs-big-play-button {
    opacity: 0.2;
}
div#video_html5_wrapper.video-js.vjs-controls-enabled a#vjs-logobrand-image-destination:hover,
div#video_html5_wrapper.video-js.vjs-controls-enabled div.vjs-control:hover,
div#video_html5_wrapper.video-js.vjs-controls-enabled div.vjs-big-play-button:hover {
    opacity: 0.75 !important;
}
div#video_html5_wrapper.video-js.vjs-controls-enabled .vjs-share-overlay-first:hover {
    opacity: 0.75 !important;
}
div#video_html5_wrapper.video-js.vjs-controls-enabled .vjs-share-overlay-first {
    opacity: 0.25 !important;
}
/* openload */

div.videocontainer div#olvideo.video-js.vjs-controls-enabled.vjs-workinghover.vjs-user-active div.vjs-control-bar {
    opacity: 0.25;
}
div.videocontainer div#olvideo.video-js.vjs-controls-enabled.vjs-workinghover.vjs-user-active div.vjs-control-bar:hover {
    opacity: 0.75;
}
/* cda pb player */

.pb-player-html-wrapper.pb-video-full div.pb-player-content div.pb-player-html.pb-bar-no-seeking.pb-video-active .pb-video-player-wrap .pb-logo {
    opacity: 0;
}
.pb-player-html-wrapper.pb-video-full div.pb-player-content div.pb-player-html.pb-bar-no-seeking.pb-video-active .pb-video-player-wrap .pb-logo:hover {
    opacity: 0.8;
}
div.pb-player-html-wrapper.pb-video-full div.pb-player-content div.pb-player-html.pb-bar-no-seeking.pb-video-active .button-players {
    opacity: 0.2;
}
div.pb-player-html-wrapper.pb-video-full div.pb-player-content div.pb-player-html.pb-bar-no-seeking.pb-video-active .button-players:hover {
    opacity: 0.85;
}
.block-black.mrg-when-no-complete div.container div div.head {
    opacity: 0.2;
}
.block-black.mrg-when-no-complete div.container div div.head:hover {
    opacity: 0.85;
}
div.pb-player-html.pb-video-active .pb-video-player-wrap .pb-play-ico {
    opacity: 0.2;
}
div.pb-player-html.pb-video-active .pb-video-player-wrap .pb-play-ico:hover {
    opacity: 0.85;
}
