css:
@-moz-document domain(noxxic.com) {

#noxxicHeader {
    position: relative;
    background: rgb(22,23,22);
    background: rgba(0,0,0,0.9);
    padding: 45px 0 57px;
    box-shadow: 0 15px 43px rgba(0,0,0,0.47);
    -moz-box-shadow: 0 15px 43px rgba(0,0,0,0.47);
    opacity: 0.4;
    filter: alpha(opacity=40);
}

.entry-content div.widget.container {
    position: relative;
    display: block;
    margin: 0;
    padding: 30px 30px 20px;
    background-color: #ecedec;
    background-image: -webkit-linear-gradient(top,#eeefee 0%,#ffffff 100%);
    background-image: -moz-linear-gradient(top,#eeefee 0%,#ffffff 100%);
    background-image: -ms-linear-gradient(top,#eeefee 0%,#ffffff 100%);
    background-image: linear-gradient(to bottom,#09141B 0%,#000000 100%);
    min-height: 80px;
    position: relative;
}

.game-portal .game-container>ul>li>a>span {
    display: block;
    position: absolute;
    width: 98%;
    height: 50px;
    left: 1.2%;
    bottom: 3px;
    line-height: 50px;
    background: rgba(0, 0, 0, 0.68);
    color: #969696;
    font-weight: 600;
    transition-property: background;
    transition-duration: 1.5s;
}

html {
    font-family: "Open Sans","Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 13px;
    font-weight: normal;
    color: #333;
    background: #191919;
    background-attachment: fixed;
    -webkit-font-smoothing: antialiased;
    text-rendering: optimizelegibility;
}



#sidebar {
    position: absolute;
    display: block;
    right: 2px;
    top: 265px;
    width: 300px;
    min-height: 845px;
    background-color: #000000;
}

.guide-landing-page {
    margin: 50px 0 0;
    background-color: #000000;
}

.entry-content p, .entry-content ul, .entry-content ol, .entry-content h4, .entry-content h5, .entry-content pre {
    margin: 0 40px 25px;
    line-height: 1.6em;
    color: #A7A7A7;
}

.entry-content>.contentContainer h2 {
    text-indent: 35px;
    margin: 40px 0 20px 0;
    height: 50px;
    line-height: 50px;
    border-bottom: 2px solid #3460AB;
    color: #A1B7CC;
}

.split_ranks h5 {
    text-align: center;
    color: #A7A7A7!important;
    margin: 0 6px;
    border: none;
}

.split_ranks ul.dps_rankings {
    position: relative;
    max-width: 100%;
    margin: 20px 5px 0 5px;
    box-sizing: border-box;
    padding: 20px;
    border: none;
    background: #171717!important;
    border-radius: 2px!important;
    border: 1px solid #027EFF;
    -moz-box-shadow: inset 0 0 2px rgba(0,0,0,0.55);
    box-shadow: inset 0 0 2px rgba(0,0,0,0.55);
}

#noxxicContent .contentContainer {
    background-color: #395F82;
    padding: 5px 0 0;
    box-sizing: border-box;
}

.entry-content .widget ul li a.bbp-forum-title {
    font-weight: 600;
}
.entry-content .widget ul li a {
    position: relative;
    display: block;
    line-height: 22px;
    height: 22px;
    padding: 1px;
    box-sizing: border-box;
    color: #CECCCC;
    font-weight: 600;
    font-weight: normal;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    background-color: #21465F;
}

.entry-content .widget ul li {
    display: block;
    margin: 0 0 2px;
    overflow: hidden;
    height: 24px;
    line-height: 24px;
    color: #21465F;
    padding: 0 20px;
    background: #21465F;
    transition-property: background,border-color;
    transition-duration: .1s;
    border-radius: 15px;
}

#noxxicContent .contentContainer {
    padding-bottom: 90px;
    display: block;
    min-height: 644px;
}
#noxxicContent .contentContainer {
    background-color: #000000;
    padding: 5px 0 0;
    box-sizing: border-box;
    height: 2300px;
}


.bottomBanner {
    padding: 35px 0;
    background-color: #000000;
}

html.wow body {
    background: url(http://www.noxxic.com/media/images/wallpapers/slim/wow_s.jpg) no-repeat center 0;
    background-attachment: fixed;
    background-color: #000000;
}

#footer {
    position: relative;
    display: block;
    max-width: 100%;
    margin: 40px auto 0;
    padding: 10px 0 10px;
    border-right: none;
    border-left: none;
    z-index: 1;
    background: #000000;
}


.guideMenu>.menu>ul>li>a {
    position: relative;
    display: inline-block;
    height: 46px;
    line-height: 46px;
    min-width: 50px;
    font-weight: 600;
    font-size: .9em;
    padding: 0 22px;
    color: #000;
    border-radius: 0;
    color: #000000;
    background: rgb(34, 85, 136);
    background-image: -webkit-linear-gradient(top,#eeefee 0%,#c8c9c8 100%);
    background-image: -moz-linear-gradient(top,#eeefee 0%,#c8c9c8 100%);
    background-image: -ms-linear-gradient(top,#eeefee 0%,#c8c9c8 100%);
    background-image: linear-gradient(to bottom,#000000 0%,#8AB8FD 100%);
    box-shadow: inset 0 -6px rgb(0, 111, 249);
    -moz-box-shadow: inset 0 -6px rgba(0,0,0,0.1);
    border-top: 1px solid #0096FF;
    border-bottom: 1px solid #6496A5;
}

.wow .entry-content h1.alt {
    margin: 20px 0 0!important;
    height: 50px;
    line-height: 50px;
    text-transform: inherit;
    background-color: #ececec;
    text-align: left;
    text-indent: 15px;
    color: #7C96CC;
    font-size: 15px;
    background-color: #202020;
}

.guideMenu>.menu>ul>li.active:hover>a, .guideMenu>.menu>ul>li.active>a, .guideMenu>.menu>ul>li>a.active, .guideMenu>.menu>ul>li.current_page_item>a, .guideMenu>.menu>ul>li.current_page_parent>a {
    color: #828282;
    background: linear-gradient(to bottom,#e3e3e3 0%,#ffffff 88.2%) #ffffff;
    border-color: #FFFFFF;
    box-shadow: none;
    padding: 0 32px;
    border-bottom-color: #fff!important;
}

.rank_config ul li.headline {
    display: block;
    line-height: 20px;
    margin: 5px 0;
    text-align: left;
    color: #C1C1C1;
    position: absolute;
    width: 100%;
    top: -30px;
    font-size: 1.1em;
    font-family: 'Open Sans',sans-serif;
    font-weight: 600;
    box-sizing: border-box;
}

.entry-content ul.build_ranking>li {
    position: relative;
    width: 100%;
    box-sizing: border-box;
    padding: 0 10px;
    margin: 0;
    border-bottom: 1px solid #177CE8;
    line-height: 45px;
    background-image: -webkit-linear-gradient(right,#37495D 0%,#042447 100%);
    background-image: -moz-linear-gradient(right,#1159a6 0%,#042447 100%);
    background-image: -ms-linear-gradient(right,#1159a6 0%,#042447 100%);
    background-image: linear-gradient(to left,rgba(56, 97, 142, 0) 0%,rgba(4, 36, 71, 0) 100%);
    transition-property: background;
    transition-duration: .2s;
}

ul.build_ranking>li ul.mini-builds li {
    display: inline-block;
    padding: 5px 2px;
    border-right: 2px solid rgb(0, 119, 255);
    background: rgba(43, 43, 43, 0);
}

.entry-content ul.build_ranking li>span.b4 a {
    position: relative;
    display: inline-block;
    color: #fff;
    color: #6D5594;
    background: #171717;
    padding: 5px 10px;
}

._57i_ {
    color: #A9A9A9;
    line-height: 1.358;
    margin-bottom: 2px;
}

._h7n {
    background: #060606;
    border-color: #e9eaed #dfe0e4 #d0d1d5;
    border-radius: 2px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .12);
    position: relative;
}

._h7l>div {
    box-sizing: border-box;
    height: 100%;
    position: relative;
    width: 100%;
    background-color: #080808;
}

._1drm._1b90, ._1drm._1ds4 {
    border: 1px solid #1C1D1D;
}

.entry-content .noxxic-style ul>li:last-child {
    border-bottom: none;
    background-color: #1D1D1D;
}

.entry-content .noxxic-style ul>li>span {
    position: relative;
    left: 0;
    top: 0;
    display: inline-block;
    padding: 3px 0;
    color: #ADADAD;
    font-weight: 600;
    line-height: 30px;
    min-width: 125px;
    margin-right: 10px;
    margin-left: 30px;
}

.entry-content .noxxic-style ul>li:nth-child(2n+1) {
    background: #1D1D1D;
}

.tb {
    display: block;
    position: relative;
    background: #000;
    padding: 16px 16px 14px;
    margin: 20px auto;
    color: #CECECE;
    width: auto;
    max-width: 500px;
    box-sizing: border-box;
    border: 3px solid rgba(222,222,222,0.1);
    z-index: 9999!important;
}

.tb h1 {
    text-transform: inherit;
    text-align: left;
    border-bottom: 3px solid #5E9AFF;
    padding-bottom: 10px;
}

.entry-content .span-style p.stat-priority>span, .entry-content .span-style p>span.stat_priority, .entry-content .noxxic-style p>span.stat_priority {
    display: inline-block;
    border-radius: 20px;
    margin-top: 0;
    padding: 10px 25px;
    background: rgb(41, 41, 41);
    -moz-box-shadow: 0 0 1px rgba(0,0,0,.2);
    box-shadow: 0 0 1px rgba(0,0,0,0.2);
    color: #D8D7D7;
    font-family: inherit!important;
    font-size: inherit!important;
    font-weight: normal!important;
}

.wow #content .noxxic_talent_builds>ul>li>span {
    min-width: 45px!important;
    width: 45px!important;
    height: 55px;
    line-height: 55px;
    position: relative;
    display: inline-block;
    padding: 0 0;
    margin: 0 8px 0 0;
    color: #A2A2A2;
    text-indent: 5px;
    text-align: center;
    float: left;
    background: none;
    font-weight: 600;
    border-radius: 0 10px 10px 0;
    border-right: none;
}

.noxxic_talent_builds>ul>li>a.expand_tier {
    position: absolute;
    right: 0;
    text-indent: 0;
    text-align: center;
    padding: 0;
    top: 4px;
    width: 37px;
    font-family: "Oswald";
    font-size: 32px;
    line-height: 45px;
    color: #888888;
}

.noxxic_talent_builds>ul>li>a>span {
    position: relative;
    height: 50px;
    padding-right: 10px;
    padding-left: 55px;
    vertical-align: middle;
    display: table-cell;
    font-weight: 600;
    font-size: .9em;
    color: #DCDCDC;
}

.entry-content .noxxic-style ul>li:nth-child(2n+1) {
    background: #1B1B1B;
    color: #B9B9B9;
}

.entry-content .noxxic-style ul>li, .entry-content .noxxic-style>.IMR>ul>li {
    position: relative;
    padding: 4px;
    background: none;
    border-radius: 0!important;
    margin: 0;
    color: #868686;
    box-sizing: border-box;
}

.entry-content .noxxic-style ul>li:last-child {
    border-bottom: none;
    background-color: #000000;
}

.entry-content .normal-style p>span {
    display: block;
    margin-bottom: 15px;
    font: 1.1em/1.3em 'Arimo'!important;
    border-bottom: 2px solid #e6e6e6;
    padding-bottom: 5px;
    color: #7FA5D0;
}

ul.chart_menu>li>a {
    position: relative;
    display: block;
    padding: 10px 3px;
    color: #CACACA;
    font-size: .9em;
    background-color: #292929;
}

ul.chart_menu {
    text-align: center;
    background: rgb(41, 41, 41);
    margin-top: 40px;
    margin-bottom: 44px;
    border-bottom: 2px solid #292929;
    border-radius: 20px;
    overflow: hidden;
}

p.top_bubble {
    position: relative;
    margin-top: 30px;
    background: #151515;
    padding: 15px;
}

.entry-content p, .entry-content ul, .entry-content ol, .entry-content h4, .entry-content h5, .entry-content pre {
    margin: 0 40px 25px;
    line-height: 1.6em;
    color: #BFBFBF;
}

.chart_wrapper:nth-child(2n+1) {
    background: #1F1F1F;
    padding: 0 0 5px;
    margin-top: 35px;
}

#login_noxxic .login_div input {
    display: block;
    width: 100%;
    box-sizing: border-box;
    line-height: 25px;
    padding: 0 10px;
    margin-bottom: 15px;
    border-color: #C1BEBE;
    background-color: #005D8E;
}
    
#login_noxxic .login_div input.input {
    background-color: #9A9A9A!important;
    border: none!important;
    margin-top: 10px;
    color: #014569;
}

.misc-bp-pages>.wrap>h1 {
    padding: 10px;
    background-color: #000000;
    color: #A4BCDA;
}

.misc-bp-pages {
    max-width: 900px;
    overflow: hidden;
    padding: 20px;
    background: #000;
    margin: 0 auto;
    color: #B1B1B1;
}


#buddypress #commentform input[type=text], #buddypress #commentform textarea, #buddypress .form-allowed-tags, #buddypress .standard-form#signup_form input[type=text], #buddypress .standard-form#signup_form textarea {
    width: 90%;
    background-color: #BFBFBF;
    border-color: #0064D6;
}

#buddypress .standard-form #basic-details-section input[type=password], #buddypress .standard-form #blog-details-section input#signup_blog_url {
    width: 35%;
    background-color: #BFBFBF;
    border-color: #0064D6;
}

.portal-social>div {
    position: relative;
    background: #eff0ef;
    margin: 0 auto;
    min-width: 150px;
    padding: 12px 40px 0;
    min-height: 38px;
    box-sizing: border-box;
    border-top: 1px solid #191919;
    -moz-box-shadow: 0 -10px 15px rgba(0,0,0,0.1);
    box-shadow: 0 -10px 15px rgba(0,0,0,0.1);
    text-align: center!important;
    background-color: #191919;
}

#noxxicContent>section {
    padding-right: 306px;
    background-color: rgba(0, 0, 0, 0.82);
}

.entry-content .span-style p>span, .entry-content p>span.stat_priority {
    display: inline-block;
    color: #7172A9;
    margin-top: 15px;
}
  
  }
