css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neogaf.com"), domain("neogaf.net") {

html, body, div, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, abbr, address, cite, code, del, dfn, em, img, ins, kbd, q, samp, small, strike, strong, sub, sup, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, input, button, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, dialog, figure, footer, header, hgroup, nav, section, time, mark, audio, video { margin: 0 !important; padding: 0 !important; border: 0 !important; outline: 0 !important; font-weight: inherit !important; font-style: inherit !important; font-size: 100% !important; font-family: inherit !important; vertical-align: baseline !important; }
body { line-height: 1 !important; }
ol, ul { list-style: none !important; }
table { border-collapse: separate !important; border-spacing: 0 !important; }
caption, th, td { text-align: left !important; font-weight: normal !important; }
blockquote:before, blockquote:after, q:before, q:after { content: "" !important; }
blockquote, q { quotes: "" "" !important; }
article, aside, dialog, figure, footer, header, hgroup, nav, section { display: block !important; }
input, select { vertical-align: middle !important; }
h1,h2,h3,h4,h5,h6,strong {font-weight: bold !important;}
/*------------------------------------------------------------*/


* {
font-family: arial !important;
}

body {
background: #111 !important;
color: #fff !important;
font: 62.5% arial, sans-serif !important;
padding: 0 20px !important;
}
.page {
background: #111 !important;
color: #fff !important;
}

a {
outline: none !important;
}

a:link, a:visited {
color: #61B2E1 !important
}
a:hover, a:visited:hover {
color: #DCF2FF !important;
}

table {
border-spacing: 0 !important;
}

div[style="margin: 5px 20px 20px;"] {
padding: 15px 20px !important;
background: #262626 !important;
border-left: 3px solid #444 !important;
}
.quotearea {
background: #262626 !important;
padding: 0 !important;
border: 0 !important;
font-size: 1em !important;
line-height: 1.35em !important;
}

pre {
width: auto !important;
height: auto !important;
}

.spoiler {
background: #000 !important;
color: #000 !important;
}
.spoiler:hover {
color: #fff !important;
}

hr {
border: 0 !important;
color: gray !important;
display: block !important;
height: 0 !important;
margin: 0.5em auto !important;
}

.tborder {
border: 0 !important;
background: #111 !important;
color: #fff !important;
}

.vbmenu_control a:link {
color: #fff !important;
}

.tcat {
background: #111 !important;
color: #fff !important;
}
.tcat a:link {
color: #fff !important;
}
.tcat a:hover {
text-decoration: none !important;
}

tr[valign="bottom"] td,
table[style="margin-top: 3px;"] td,
#collapseobj_forumbit_1 td,
tr[align="center"] td,
#threadslist td {
vertical-align: middle !important;
}

.thead {
background: #111 !important;
color: #fff !important;
padding: 0 !important;
}
.thead:nth-child(5),
td[align="center"] {
text-align: center !important;
}
td[align="right"] {
text-align: right !important;
}
.thead a:link {
color: #fff !important;
}
.thead a:hover {
text-decoration: underline !important;
}

.tfoot {
background: #111 !important;
color: #fff !important;
}
.tfoot a:link {
color: #bbb !important;
}
.tfoot a:hover {
color: #fff !important;
}

.tborder tr {
background: #1b1b1b !important;
overflow: hidden !important;
}
.tborder tr:hover {
background: #202020 !important;
}

div[align="center"] {
margin-bottom: 20px !important;
}

.alt1,
.alt1Active,
.alt2,
.alt2Active {
border: 1px solid #111 !important;
vertical-align: top !important;
}
#threadslist .alt2 {
vertical-align: middle !important;
}
.alt1,
.alt1Active {
background: transparent !important;
color: #ddd !important;
padding: 15px 20px !important;
}
.alt2,
.alt2Active {
color: #ddd !important;
background: #202020 !important;
padding: 5px 10px !important;
}
#posts .alt1,
#posts .alt1Active,
#posts .alt2,
#posts .alt2Active {
padding: 15px !important;
background: #202020 !important;
}

.pagenav td {
padding: 0 !important;
}
.pagenav a:link,
.pagenav a:visited,
.pagenav strong {
display: inline-block !important;
padding: 4px 8px !important;
}
.pagenav a strong {
padding: 0 !important;
}
.pagenav a:hover {
background: #292929 !important;
}

.time {
color: #888 !important;
}

div.page > div > div > table > tbody > tr > td > a > img,
a[href="#top"],
#forumtools,
.vbmenu_control,
#collapseobj_forumhome_activeusers,
#collapseobj_forumhome_stats,
td[colspan="5"],
form[action="index.php"],
.lstile,
/*
img[width="90"],
*/
table[style="border-bottom-width: 0px;"],
hr,
span[style="float: right;"],
td.thead[colspan="2"],
td.tcat[colspan="2"],
table[cellspacing="2"] tr[valign="bottom"],
table[style="border-top-width: 0px;"],
td[colspan="10"],
div[style="padding: 0px 0px 6px;"],
center b a,
.container tr,
.users-bar,
.mainlevel-nav,
#threadslist tr td:nth-child(3),
#threadslist tr td:nth-child(4),
#threadslist tr td:nth-child(6),
a[href="http://www.big-boards.com/"],
td[width="426"],
td[background="images/m0dus-skin/logo1.gif"],
img[width="244"],
img[width="42"],
td[background="images/m0dus-skin/top_bg.gif"],
td[background="images/m0dus-skin/top_corner.gif"],
#header-logo,
#header-nav {
display: none !important;
}
#header,
#header-user {
background: transparent !important;
}

.container tr:first-child,
#mainlevel-nav li:last-child a {
display: block !important;
}

.container,
.container td {
width: 900px !important;
}
div.header-nav,
div.header-nav a {
float: none !important;
margin: 0 !important;
padding: 0 !important;
position: absolute !important;
top: 0 !important;
left: 0 !important;
width: 100% !important;
height: 100% !important;
text-align: center !important;
border: 0 !important;
font-size: 3em !important;
line-height: 100px !important;
}


.forum-tabs,
.forum-tabs li,
.forum-tabs li a {
width: auto !important;
height: auto !important;
background: transparent !important;
}
.forum-tabs li.activetab a {
background: #333 !important;
margin-bottom: 20px !important;
}

span[style="white-space: nowrap;"] {
font-size: .9em !important;
}


}
