css:
@-moz-document domain("play.pokemonshowdown.com") {
body {
    background:url("http://i.imgur.com/7n482uK.jpg") no-repeat;
    background-size:1400px;
    background-color:#F2E48B;
  }
 
  .ps-room.ps-room-light.scrollable {
    background:url("https://33.media.tumblr.com/tumblr_m6giqrFaK11qkki6ro1_500.gif") no-repeat transparent;
    background-size:1200px;
    background-color:#68A05F;
    opacity: 0.93;
  }
 
  #header {  
   background: rgba(234, 234, 206,0.8);
   background: -moz-linear-gradient(top, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 10%, rgba(116, 188, 187,0.8) 89%, rgba(116, 188, 187,0.8) 100%);
  }
 
  .mainmenufooter small:after{
    content:"Made by: Mio Isurugi MM";
    font-size:9pt;
    color:#cccccc;
    text-shadow:0 1px black;
    position:absolute;
    size:6px;
    left:230px;
    top:21px;
    width: 300px;
  }
 
  .menugroup {
    background:rgba(255, 255, 255, 0);
  }
 
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    color: #FFF;
    text-align:left;
  }
 
  a {
    color: #72BCBD;
  }
 
  a:hover {
    color: #46AEB2;
  }
 
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    background: rgba(234, 234, 206,0.8);
    background: -moz-linear-gradient(top, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 10%, rgba(116, 188, 187,0.8) 89%, rgba(116, 188, 187,0.8) 100%);
    text-shadow:none;
    color:#212121;
    border:1px solid #212121;
    font-size:13px;
    box-shadow:none;
  }
 
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    background:#74BCBB;
    color:#212121;
    border:1px solid #212121;
  }
 
  .mainmenu .menugroup:nth-child(1) .button {
    font-size:15px;
  }
 
  .select {
    border-top-left-radius:0px;
    color:#000;
    background: rgba(231, 237, 203,0.8);
    background: -moz-linear-gradient(top, rgba(231, 237, 203,0.8) 0%, rgba(231, 237, 203,0.8) 0%, rgba(231, 237, 203,0.8) 10%, rgba(214, 176, 142,0.8) 89%, rgba(214, 176, 142,0.8) 100%);
    color:#212121;
    border:1px solid #454545;
  }
 
  .select:hover {
    color:#212121;
    border:1px solid #212121;
    background: rgba(231, 237, 203,0.8);
    background: -moz-linear-gradient(top, rgba(231, 237, 203,0.8) 0%, rgba(231, 237, 203,0.8) 0%, rgba(231, 237, 203,0.8) 10%, rgba(214, 176, 142,0.8) 89%, rgba(214, 176, 142,0.8) 100%);
  }
 
  .select:disabled:hover {
    border-color: #999;
    background: none repeat scroll 0% 0% rgba(220, 220, 220, 0.65);
    color: #000;
  }
 
  .pm-log, .battle-log {
    background:url("http://i.imgur.com/Q8vHT0Y.png");
  }
 
  .pmbox {
    opacity: .9;
  }
 
  .ps-popup {
    background:url("http://i.imgur.com/Q8vHT0Y.png");
    color:#000;
  }
 
  .pm-window h3 {
    background: rgba(234, 234, 206,0.8);
    background: -moz-linear-gradient(top, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 10%, rgba(116, 188, 187,0.8) 89%, rgba(116, 188, 187,0.8) 100%);
    color:#000080;
    font-size:13px;
    font-weight:normal;
  }
 
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover  {
    background:#FFFFE0;
  }
 
  .pm-window h3 small {
    color:#FFA500;
  }
 
  .pm-log-add {
        background: rgba(116, 188, 187,0.8);
    background: -moz-linear-gradient(top, rgba(116, 188, 187,0.8) 0%, rgba(116, 188, 187,0.8) 0%, rgba(116, 188, 187,0.8) 10%, rgba(234, 234, 206,0.8) 89%, rgba(234, 234, 206,0.8) 100%);
  }
 
  .closebutton, .minimizebutton {
    color:#8B4513;
  }
 
  .closebutton:hover {
    color:#F4320F;
  }
 
  .minimizebutton:hover {
    color:#FFD36D;
  }
 
  .mainmenufooter {
    color: #72BCBD;
    text-shadow:none;
  }
 
  .mainmenufooter a {
    color: #72BCBD;
    text-shadow:1px 1px #FFF;
    font-weight:bold;
  }
 
  .maintabbarbottom {
    background:#eaeace;
    border:1px solid #212121;
  }
 
  .tabbar a.button.notifying {
    border:1px solid #AB1111;
    color:#AB1111;
    text-shadow:none;
    background:#72BCBD;
  }
  .tabbar a.button.notifying:hover {
    border:1px solid #F4320F;
    color:#F4320F;
    background:#72BCBD;
  }
 
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #D6AF8E;
    background: -moz-linear-gradient(top, #D6AF8E 0%, #D6AF8E 0%, #D6AF8E 10%, #eaeace 89%, #eaeace 100%);
    border:1px solid #212121;
    color:#212121;
    text-shadow:none
  }
 
  .tabbar a.button {
    border:1px solid #212121;
    background:#eaeace;
  }
 
  .tabbar a.button:hover {
    border:1px solid #212121;
    background:#F0F0EF;
  }
 
  .popupmenu h3 {
    color:#6DB7B8;
  }
 
  .userlist {
    border-right:none;
  }
 
  .chat-log-add {
    border-top:none;
  }
 
  .chat-log {
    border-bottom:1px solid #B6B6B6;
    border-left:1px solid #B6B6B6;
  }
 
  .battle-log h2, .battle-log-add {  
    background: rgba(234, 234, 206,0.8);
    background: -moz-linear-gradient(top, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 0%, rgba(234, 234, 206,0.8) 10%, rgba(116, 188, 187,0.8) 89%, rgba(116, 188, 187,0.8) 100%);
  }
 
.battle-log {
    background:url(http://i.imgur.com/vS5KYZT.jpg) no-repeat bottom right;
 }
 
  .rightbar, .leftbar {
    background:rgba(116, 188, 187, 0.6);
  }
 
.weather {
        text-shadow:none;
    color: rgba(0,0,0,0);
}
 
.weather em small {
    font-size: 8pt;
    color: white;
    text-shadow: 1px 1px black;
    position: absolute;
    left:0;
    top: 85px;
    width: 95px;
    text-align:center;
  }
 
.sunweather, .sunnydayweather {
background: url(http://i.imgur.com/K6mV4nW.gif) no-repeat scroll left top;
 opacity: 1 !important;
background-size: cover;
}
 
.rainweather, .raindanceweather {
      background: url(http://i.imgur.com/Sbn9yKh.gif) no-repeat scroll left top;
    opacity: 1 !important;
        background-size: cover;
  }
 
.sandstormweather {
background: url(http://i.imgur.com/XbSeBJV.gif) no-repeat scroll left top;
  opacity: 1 !important;
  background-size: cover;
  }
 
.hailweather {
background: url(http://i.imgur.com/eUqkcVW.gif) no-repeat scroll left top;
        opacity: 1 !important;
  background-size: cover;  
}
 
.sandstormweather {
background: url(http://i.imgur.com/XbSeBJV.gif) no-repeat scroll left top;
  opacity: 1 !important;
  background-size: cover;
  }
 
.pseudoweather{
background: url(http://i.imgur.com/rPMtZca.png) no-repeat scroll left top;
  opacity: 1 !important;
  background-size: cover;
}
 
  .trainer strong {
    color:#000080;
  }
 
  .ps-room.ps-room-opaque {
    background:rgba(116, 188, 187, 0.8);
  }
 
  .battle {
    border:1px solid #FFC0CB;
  }
 
.backdrop:before{
        display: block;
        content: "";
        background: url(https://41.media.tumblr.com/tumblr_m9qnjxO7951rfumu9o1_500.jpg);
        background-size: cover;
        width: 700px;
    height: 460px;
}
 
  .broadcast-red, .broadcast-blue, .broadcast-green {
    color: #000;
    border: 2px solid #000;
    padding: 5px;
    font-family: helvetica,arial,sans-serif;
    font-size: 10pt;
    text-shadow: none;
  }
 
  .broadcast-red {
    background: none repeat scroll 0% 0% #EC5C4D;
  }
 
  .broadcast-blue {
    background: none repeat scroll 0% 0% #8FA6B3;
  }
 
  .broadcast-green {
    background: none repeat scroll 0% 0% #9BB38F;
  }
 
  .chat small {
    color: #000022;
  }
 
  .roomlist a.ilink {
    color:#4B0082;
    border:1px solid #72BCBD;
  }
 
  .roomlist a.ilink:hover {
    color:#FFFF00;
  }
 
  .roomlist a.ilink small {
    color:#FFD700;
  }
 
  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .itemdesccol, .utilichart .statcol em, .utilichart .bstcol em, .utilichart .numcol {
    color:#444;
  }
 
  .teambar button {
    background: linear-gradient(to bottom, #E7EDCB, #D6AF8E) repeat scroll 0% 0% transparent;
  }
 
  .teambar button:disabled {
    background: linear-gradient(to bottom, #E7EDCB, #D6AF8E) repeat scroll 0% 0% transparent;
  }
 
  .ladder table, .ladder td, .ladder th {
    border: 1px solid #212121;
  }
 
  .chat-log .notice {  
    color:#228B22;
  }
 
  .ps-room .battle-controls {
    background: transparent !important;
  }
  }
