css:
@-moz-document regexp("https?://www\\.baidu\\.com/((s|baidu)\\?.*|#wd|(index.*)?$)"){
  /* ===== 百度网页搜索结果拉宽 第四版 Start=====*/
  /*布局拉宽*/div#content_left, div#content_left>div{width:auto!important;float:none!important;} div#content_left>table{width:100%!important;} div.c-span18:not(.op-movievideo-right){width:auto!important;float:none!important;}
  /*右栏隐藏*/div#content_right, div#content_left>br{display:none!important;}
  /*外框美化*/div#content_left{border:2px solid #ccc;padding:0!important;margin:0 35px!important;} div#container{width:auto!important;}
  /*奇数行美化*/div#content_left>:nth-child(2n+1){background-color: #EAEBEC!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 2px #ccc;/*-webkit-border-radius:5px;*/}
  /*偶数行美化*/div#content_left>:nth-child(2n+2){background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 2px #ccc;/*-webkit-border-radius:5px;*/}
  /*搜索结果表格美化*/div.c-border, div.op_best_answer_content, td.plf, td.op_wiseapp_f{width:auto!important;} table.result, table.result-op{width:100%!important;} td.f, td.op_wiseapp_f{padding:8px 10px!important;} table.subLink_factory_right{width:50%!important;}
  /*搜索结果最新信息美化*/div.c-offset>div.c-row>span[style*="color:#666"]:nth-child(1){color:#fff!important;float:left!important;Background:#2191f7!important;padding:0 5px!important;}
  /*搜索结果优化*/div.op_baikeperson_container>:nth-child(1){width:auto!important;}
  /*顶部为您推荐*/ div.hint_toprq,span.hint_toprq_items,span.hint_toprq_short,p.hit_top{width:auto!important}
  /*搜索结果悬停美化*/div#content_left>:hover{background-color: #B5E0F5!important;}
  /*搜索结果文字更大*/h3.t{font-size:17px!important;font-weight:bold!important;} div.c-span18, div.c-span21, div.c-abstract, p.f13, div.f13, div.c-offset, span.c-showurl, div.c-gap-top-small, div.c-offset td.c-default{font-size:15px!important;}
  /*相关信息推荐*/
div.result-op.xpath-log[style*="background-color: rgb(255, 255, 255);"]{width:400px!Important;right:0px!important;left:auto!important;}
div.rrecom-container{width:400px!Important;}
  /* ===== 百度网页搜索结果拉宽 第四版 End=====*/
}
