css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sql.learncodethehardway.org") {

  body, p,
  h1, h2, h3, h4, h5, h6 {
    background-color: #0b0b0b;
    color: #EEEEEE;
    /*font-family: 'Source Sans Pro';
    font-size: 20px;*/
  }
  
  pre {
    background-color: black;
  }

  .n {
    color: #E7E7E7;
  }
  
  .nb {
    color: rgb(242, 218, 137);
  }
  
  .k {
    color: rgb(0, 156, 255);
  }
  
  .go {
    color: #8F8F8F;
  }
  
  .p {
    color: #969696;
  }
  
  .mi {
    color: #00CFA3;
  }
  
  .literal {
    background-color: black;
    padding: 4px 7px;
    border-radius: 6px;
  }
}
