.activity-content blockquote, .activity-content blockquote, .shout-item blockquote{
    border: none!important;
    margin: 0!important;
    background: #fff!important;
    color: #222222!important;
    padding: 20px 147px 20px 55px!important;
    box-sizing: border-box!important;
    box-shadow: 0 2px 4px rgba(34, 34, 34, 0.12)!important;
    position: relative!important;
    overflow: hidden!important;
    min-height: 120px!important;
    font-size: 22px!important;
    line-height: 1.5!important;
}
.shout-item blockquote {
    margin: 10px 20px!important;
}
blockquote:before {
    font-family: Georgia, serif!important;
    content: "“"!important;
    position: absolute!important;
    top: -10px!important;
    left: 10px!important;
    font-size: 5em!important;
    color: rgba(238, 238, 238, 0.8)!important;
    font-weight: normal!important;
}
blockquote:after {
    font-family: Georgia, serif!important;
    content: "”"!important;
    position: absolute!important;
    bottom: -111px!important;
    line-height: 100px!important;
    right: -25px!important;
    font-size: 18em!important;
    color: rgba(238, 238, 238, 0.8)!important;
    font-weight: normal!important;
}

