css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.netkobiety.pl") {

BODY {
    background: none repeat scroll 0px 0px #151515;
}
li.userage {
font-weight: bold;
}
p, li.info-lastpost cite, li.info-lastpost span, li.info-forum a, ul li strong, span {
color: #3D5266;
}
a:link, a:visited, .js_link, #brdinfo A {
        color: #2c7b55;
    }
#brd-navlinks, .main-menu, .admin-menu, .main-head, .main-foot {
    background-color: #2c7b55;
    background-image: -moz-linear-gradient(center bottom , #2c7b55 0px, #2c7b55 100%);
}
    #brd-navlinks {
    border-color: #2c7b55;
}
    #netkobiet{
    background: #2c7b55 !important;
}
    #brd-navlinks .isactive a, .main-menu .active a, .admin-menu .active a {
    color: #FFF;
    background: #1e563b none repeat scroll 0% 0%;
}
    
.user-ident .username strong {
    color: #E02146;
}
.entry-content {
    padding-bottom: 2em;
    overflow: hidden;
    width: 100%;
    min-height: 7em;
    font: 100%/1.7em;
    font-family: Verdana;
    font-size: 14px;
}
#welcome {
color: #E02146;
}
.main-head, .main-foot {
    background-color: #202020;
    background-image: -moz-linear-gradient(center bottom , #202020 0px, #202020 100%);
}
.main-head, .main-foot {
    color: #555;
    border-color: #151515;
    font-size: 1em;
    font-weight: bold;
}
DIV#brdinfo {
    background: none repeat scroll 0px 0px #252525;
    color: #444;
}
.crumbs {
    margin: 0.5em 0px;
    padding: 0.7em 1.417em;
    font-size: 1.084em;
    background: none repeat scroll 0% 0% #202020;
}
.paging span, .paging a, .paging strong {
    border-color: #333;
    color: #444;
}
.main-frm .ct-group, #brd-announcement {
    background: none repeat scroll 0% 0% #252525;
}
.ct-box, .ct-set, .ct-group, .frm-group {
    border-color: #252525;
}
.posthead, .postbody, .post-options {
    background: none repeat scroll 0% 0% #202020;
    color: #555;
}
.post-num, .post-edit {
    color: #444;
}
.post {
    border-color: #151515;
    background: none repeat scroll 0% 0% #252525;
}
.post div, h3.post-ident, h4.post-title, .posthead h3, .userstatus span, .sig-line {
    border-color: #151515;
}
.gen-content, .main-content {
    background: none repeat scroll 0% 0% #252525;
    border-color: #151515;
}
.posthead .hn {
    background: none repeat scroll 0% 0% #252525;
}
.entry-content .quotebox, .entry-content .codebox {
    background: none repeat scroll 0% 0% #252525;
    border-color: #202020;
}
.posthead .hn {
    border-style: none none solid;
    border-width: 1px;
}
.gen-content, .main-content {
    border-color: #151515;
}
.main-subhead {
    border-color: #151515;
    background: none repeat scroll 0% 0% #202020;
    color: #E02046;
}
.txt-set textarea {
   background: #333;
   color: #666;
   border: 0px !important;
}
input[type="text"], input[type="password"], input[type="email"], input[type="url"], input[type="number"], input[type="search"], textarea {
    border-color: #333;
}
.frm-buttons, .extension .options, .hotfix .options, #rules-content {
    border-color: #333;
}
.main-item, .main-item li, .main-item div, td {
    border-color: #151515;
}
    .main-content .sticky {
     background: #303030 none repeat scroll 0% 0%;
    }
}
