css:
@-moz-document domain("jira.atlassian.com") {
  /* -----------------------------------------------------------[main] */
  html {
    -webkit-filter: invert(100%);
  }
  img {
    -webkit-filter: invert(100%);
  }
  /* ----------------------------------------------------------[tempo] */
  .timeframe-color {
    color: #333;
  }
  .aui-button .aui-icon {
    color: #ccc;
    text-shadow: 0 1px 0 #333;
  }
  .aui-button .icon-previous:after {
    border-right-color: #000; 
  }
  .aui-button .icon-next:after {
    border-left-color: #000;  
  }
  .tempo-timesheet #issuetable td.tt-current-day, 
  .tempo-calendar #issuetable td.tt-current-day, 
  .tempo-calendar #issuetable td.tt-current-day:hover {
    background-color: #ccc; 
  }
  /* ---------------------------------------------------------[images] */
  #heading-avatar {
    -webkit-filter: invert(100%);
  }
  .mw-drilldown-avatar {
    -webkit-filter: invert(0%);
  }
  /* ----------------------------------------------------------[links] */
  a,
  a:visited {
    color: #957373;
  }
  a:hover {
    color: #957373;
  }
  /* -----------------------------------------------------------[text] */
  pre {
    font-family: monospace;
    color: #000;
  }
  .code, 
  .preformatted {
    border-color: #D8D7DD;
    background: #D8D7DD;
  }
  /* ------------------------------------------------------------[nav] */
  #announcement-banner,
  #footer {
    background: #ccc;
  }
  .aui-header{
    color: #000;
    border-bottom: 2px solid #bbb;
    background: #aaa;
  }
  .aui-button,
  .aui-button-primary {
    background-color: #957373;
    background-image:-moz-linear-gradient(#957373, #b2a4a4);
    background-image:-webkit-linear-gradient(#957373, #b2a4a4);
    background-image:linear-gradient(#957373, #b2a4a4);
    border-color: #b2a4a4;
    color: #000;
    text-shadow: none;
  }
  .aui-nav-link, 
  .aui-dropdown2-ajax, 
  .jira-ajax-menu, 
  .aui-dropdown2-trigger, 
  .aui-dropdown2-active {
    color: #222 !important;
  }

  .aui-nav-link:hover, 
  .aui-dropdown2-trigger:hover {
    background-color: #aaa;
  }
  a.aui-button {
    color: #000;  
  }
  .active,
  .aui-dropdown2-active {
    background-color: #ccc;
  }
  /* -------------------------------------------------------[sections] */
  .mod-header {
    text-align: center;
    border-bottom: 1px solid #fff;
  }
  .aui-item,
  .detail-panel, 
  .navigator-issue-only, 
  .active {
    background-color: #eee;
  }
  .issue-list>li.focused {
    background-color: #957373;
  }
  .issue-list>li.focused a {
    color: #fff;
  }
  .issue-list>li.focused a span {
    text-shadow: 0 0 0 #333;
  }
  .aui-message.warning,
  .aui-message.info,
  .aui-message.success,
  .aui-message.hint {
    background-color: #ddd;
  }
  .aui-message.error{
    background:#2FBBC8;
    border-color:#2FBBC8;
    color:#000;
  }
  .aui-message.warning{
    border-color:#001551;
  }
  .aui-message.info{
    border-color:#73604D;
  }
  .aui-message.success{
    border-color:#6E3763;
  }
  .aui-message.hint{
    border-color:#554739;
  }
  /* --------------------------------------------------------[gadgets] */
  .gadget.color1 .dashboard-item-header, 
  .gadget-colors .color1>a {
    background-color: #957373;
  }
  .gadget.color1>.gadget-hover>.dashboard-item-header,.gadget.color1>.gadget-hover>.dashboard-item-content{
    border-color:#999;
  }
  .dashboard-item-title {
    color: #000;
    text-shadow: none;  
  }
  .aui-list-item a:hover {
    background: #bbb;
  }
  .dashboard-item-frame,
  .gadget-container,
  .gadget-hover,
  .gadget {
    border: none !important;
  }
  .dashboard-item-header,
  .dashboard-item-content {
    border-color: #ccc !important;    
  }
  /* ----------------------------------------------------------[icons] */
  .aui-icon,
  .icon-move,
  .icon-move-d,
  .icon-dropdown,
  .icon-dropdown-d,
  .icon-dropdown-active,
  .icon-dropdown-active-d,
  .icon-minimize,
  .icon-minimize-d,
  .icon-maximize,
  .icon-maximize-d,
  .aui-icon.icon-move,
  .aui-icon.icon-move-d,
  .aui-icon.icon-dropdown,
  .aui-icon.icon-dropdown-d,
  .aui-icon.icon-dropdown-active,
  .aui-icon.icon-dropdown-active-d,
  .aui-icon.icon-minimize,
  .aui-icon.icon-minimize-d,
  .aui-icon.icon-maximize,
  .aui-icon.icon-maximize-d,
  form.aui .icon-date,
  .aui-icon-date,
  form.aui .icon-range,
  .aui-icon-range,
  form.aui .icon-required,
  .aui-icon-required,
  form.aui .icon-users,
  .aui-icon-users,
  form.aui .icon-help,
  .aui-icon-help,
  form.aui .icon-inline-help,
  .aui-icon-inline-help,
  .aui-message .icon-generic,
  .aui-icon-generic,
  .aui-message .icon-error,
  .aui-icon-error,
  .aui-message .icon-hint,
  .aui-icon-hint,
  .aui-message .icon-info,
  .aui-icon-info,
  .aui-message .icon-success,
  .aui-icon-success,
  .aui-message .icon-warning,
  .aui-icon-warning,
  .aui-message .icon-close,
  .aui-icon-close,
  .aui-message .icon-close-inverted,
  .aui-message.error .icon-close,
  .aui-icon-close-inverted,
  .aui-icon-dropdown,
  .aui-icon-wait,
  .aui-dropdown2 .active .aui-icon-wait,
  .aui-button-primary .aui-icon-wait,
  .aui-button-primary[aria-disabled="true"] .aui-icon-wait {
    -webkit-filter: invert(100%);
  }
  /* --------------------------------------------------------[errors] */
  form.aui .field-group .error, 
  form.aui .group .error, 
  form.aui .checkbox .error, 
  form.aui .radio .error,
  form.tempo-form.aui .error-list {
    color: #0FF0FF;
  }
  form.tempo-form.aui .field-group input.error-field, 
  form.tempo-form.aui .field-group select.error-field {
    border: 1px solid #0FF0FF;
  }
  /* -------------------------------------------------------[lozenges] */
  .jira-issue-status-render-error{
    color: #2FBBC8;
  }
  .aui-lozenge.jira-issue-status-lozenge-green{
    background-color: #EB76D3;
    border-color: #EB76D3;
    color: #000;
  }
  .aui-lozenge.aui-lozenge-subtle.jira-issue-status-lozenge-green{
    background-color: #000;
    border-color: #4D2746;
    color: #EB76D3;
  }
  .aui-lozenge.jira-issue-status-lozenge-yellow{
    background-color: #002CAE;
    border-color: #002CAE;
    color: #A6BCFF;
  }
  .aui-lozenge.aui-lozenge-subtle.jira-issue-status-lozenge-yellow{
    background-color: #000;
    border-color: #001D73;
    color: #A6BCFF;
  }
  .aui-lozenge.jira-issue-status-lozenge-brown{
    background-color: #7EA4C5;
    border-color: #7EA4C5;
    color: #000;
  }
  .aui-lozenge.aui-lozenge-subtle.jira-issue-status-lozenge-brown{
    background-color: #000;
    border-color: #13181D;
    color: #7EA4C5;
  }
  .aui-lozenge.jira-issue-status-lozenge-warm-red{
    background-color: #2FBBC8;
    border-color: #2FBBC8;
    color: #000;
  }
  .aui-lozenge.aui-lozenge-subtle.jira-issue-status-lozenge-warm-red{
    background-color: #000;
    border-color: #072C2E;
    color: #F8D3D1;
  }
  .aui-lozenge.jira-issue-status-lozenge-blue-gray{
    background-color: #B5987A;
    border-color: #B5987A;
    color: #000;
  }
  .aui-lozenge.aui-lozenge-subtle.jira-issue-status-lozenge-blue-gray{
    background-color: #000;
    border-color: #1B1712;
    color: #B5987A;
  }
  .aui-lozenge.jira-issue-status-lozenge-medium-gray{
    background-color: #333;
    border-color: #333;
    color: #ccc;
  }
  .aui-lozenge.aui-lozenge-subtle.jira-issue-status-lozenge-medium-gray{
    background-color: #000;
    border-color: #333;
    color: #ccc;
  }
  .g-favourite-filters #filter-list li {
    border-bottom-color: #404040 !important;
  }
  .config-options {
    border-top-color: #000 !important;    
  }

}
