css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dailycaller.com") {

  img.img_ad,
  div.str-adunit
  {
    visibility: hidden !important;
    height: 0px !important;
  }
  
  body
  {
    background-color: #202027;
    background: transparent -moz-linear-gradient(center left , #101019, #202027 10%, #202027 90%, #101019) repeat scroll 0% 0%;
    color: #ccc;
  }

  
  [class^="icon-"], [class*=" icon-"], header #search_icon .icon
  {
    color: #693;
  }

    [class^="icon-"]:hover, [class*=" icon-"]:hover
  {
    color: #cf9;
  }

  .box-post,  div.ui-dialog-titlebar
  {
    background-color: #272730 !important;
  }
  
  .box-post.large-6, div.ui-dialog
  {
    background-color: #17171B !important;
  }
  
  
  section.top-bar-section li.active:not(.has-form) a:not(.button), 
  section.top-bar-section li:not(.has-form) a:active:not(.button), 
  header nav.top-bar ul li:active 
  {
    background-color: black;
  }
  
  
  .dc-go-top
  {
    background-color: #000;
    border-radius:10px;
  }

    .dc-go-top:hover
  {
    background-color: #9c6;
  }

  .dailycaller
  {
  background: transparent -moz-linear-gradient(center left , #202027 20%, #fff 50%, #202027 80%) repeat scroll 0% 0%;
  }

  button, input, optgroup, select, textarea
  {
    border-radius: 10px;
  }
  
  header .contain-to-grid .top-bar-section, 
  header .contain-to-grid.sticky .top-bar-section, 
  header .contain-to-grid.sticky.fixed nav.top-bar ul, 
  header .contain-to-grid.sticky.fixed nav.top-bar ul li, 
  header .contain-to-grid.sticky.fixed nav.top-bar ul li a, 
  .contain-to-grid.sticky.fixed, 
  .contain-to-grid.sticky.fixed nav.top-bar, 
  .contain-to-grid.sticky.fixed .top-bar-section li:not(.has-form) a:not(.button)
  {
    xbackground-color: #669933;
    background: transparent -moz-linear-gradient(center top , #360, #030) repeat scroll 0% 0%;
    text-shadow: 2px 2px #000;
    color: #fff;
    border-radius: 10px;
  }

  header nav.top-bar, 
  header nav.top-bar ul, 
  header nav.top-bar ul li, 
  header nav.top-bar ul li a, 
  .contain-to-grid, 
  .top-bar-section li:not(.has-form) a:not(.button) 
  {
    background: transparent -moz-linear-gradient(center top , #9c6, #360) repeat scroll 0% 0%;
    border-radius: 10px;
    xbackground-color: #cf9;
    color: #000;
    text-shadow: none;

  }  
  
  a, a b, body.single #primary .post h1, body.single .article-primary .post h1, body.single .section, .post_tags a 
  {
    color: #669933;
  }  

  a:visited, a b:visited, .post_tags a:visited 
  {
    color: #669944;
  }  

  a:hover
  {
    color: #99cc66;
  }  
  
.hybrid-thumbs-1r .video-title, .hybrid-text-links-2c .trc_rbox_header
  {
    color: #666666;
  }
  
.info, .info .author a, .info .section, .info .author, .info_feature .author-feature
  {
    color: #336600;
  }  
  
.widget h3.post a, .widget h3.post
  {
    color: #669933;
  }

  .bio-container
  {
    border-bottom-color: #9c3;
  }
  
  #secondary .box h2, #primary .box h2, .article-primary .box h2
  {
    background-color: #669933;
  }

  div.civicscience.ballotbox div.poll_title
  {
    color: #669933;
  }

  .comments-link > a
  {
  background: transparent -moz-linear-gradient(center top , #9c3, #690) repeat scroll 0% 0%;
  }
  

  .box-post .pic img 
  {
    height: 100%; x:200px;
    }
  
.gbqfwa .gbqfwax_putt, .gbqfwapq, .gbqfwa, .medium-5, div#_buatongz_net266, .nmx_ad_slot, .nmx_ad_prefix, 
  .AR_2.ob-widget .ob-widget-header, .AR_2.ob-widget .ob_what, .AR_2.ob-widget .ob-widget-items-container
  {
    display: none !important;
    visibility: hidden !important;
    width: 1px;
    height: 10px;
    xbackground-color: red;
  }
  
  body.single .author-box #name,
  div.article-data > div.author-box > div.data > a > div.authortitle
  {
    color: #565;
  }
  
  .post_tags
{
   color: #565;
}
  
  
  .medium-7
  {
    width: 100%;
  }
  
}


@-moz-document domain("disqus.com") {


div.post-body > header > span.post-meta > a,
div.post-body > header > span.post-meta > a:hover,
span.post-byline > span > a.parent-link,
span.post-byline > span > a.parent-link:hover
  {
    color: #464;
  }
  
li.reply > a > span.text, 
li.reply.active > a > span.text, 
li.reply.active > a, 
li.share > a.toggle > span.text 
{
  color: #226611;
}


  .post-content footer .voting .vote-down
  {
    color: #662211;
  }
.post-content footer .voting .vote-down:hover
{
    color: #992211;
}
  
  .post-content footer .voting .vote-up
  {
    color: #226611;
  }
.post-content footer .voting .vote-up:hover
{
    color: #229911;
}
  
  .post-content footer .voting .control 
{
  color: whtie;
}
  
  .widget > .box-post.opinion > .small-12.medium-12.columns.lr.user > h3.post > a
  {
    color: #693;
  }
div#dc_contributors-4.box.dc_contributors-4 > h2
  {
    background-color: #693;
    color: black;
  }

}
