css:
@-moz-document url-prefix("http://www.gocomics.com") {
 body {
     background: white !important;
 }
 #wrapper {
     background: none !important;
 }
 #content-wrapper,
 .container {
     width: auto !important;
     background: none !important;
 }
 #content {
     margin: 0 auto !important;
     float: none !important;
     width: 75% !important;
     max-width: 980px !important;
     min-width: 700px !important;
 }
 .feature,
 .comment-panel-container,
 .comment {
     width: 100% !important;
     background: none !important;
 }
 .feature_item,
 .strip {
     width: 100% !important;
     height: auto !important;
 }
 .comment-top-orphan,
 div.comment-faux,
 .comment-bottom-orphan {
     width: 100%;
     padding-right: 0 !important;
     background: none !important;
 }
 .banner {
     text-align: center;
 }
 .share-nav,
 #outbrain-wrapper,
 #sidebar,
 #footer-wrapper,
 .tool_box,
 .addthis_toolbox,
 .global-fb-like,
 .zoom_link {
     display: none !important;
 }
 div#carousel-nav h1.title-collection,
 div#carousel-nav h1 {
     text-align: center;
     padding: 5px 0;
 }
 ul.carousel-nav {
     display: -ms-flexbox;
     display: -webkit-flex;
     display: flex;
     -webkit-flex-direction: row;
     -ms-flex-direction: row;
     flex-direction: row;
     -webkit-flex-wrap: nowrap;
     -ms-flex-wrap: nowrap;
     flex-wrap: nowrap;
     -webkit-justify-content: center;
     -ms-flex-pack: center;
     justify-content: center;
     -webkit-align-content: flex-start;
     -ms-flex-line-pack: start;
     align-content: flex-start;
     -webkit-align-items: center;
     -ms-flex-align: center;
     align-items: center;
 }
 ul.carousel-nav li {
     -webkit-flex: 0 1 auto;
     -ms-flex: 0 1 auto;
     flex: 0 1 auto;
     -webkit-align-self: auto;
     -ms-flex-item-align: auto;
     align-self: auto;
 }
 ul.carousel-nav li.new-day strong {
     top: 35px;
 }
}
