css:
@-moz-document domain("tv.wp.pl") {
body { background: #1a1a1a!important;}
#stgHeader {display:none!important;}
#expandBigCnt, #expandButton {display:none!important;}
#adv02, #adv03, #adv09, .rekPrawa { display:none!important;}
#nawigator { background: #1a1a1a!important;}
.nawigatorIn { background:#1a1a1a!important;}
#nawigator .nawigatorOpcje #nawigatorLista { background-color:#257caa }
#nawigator .nawigatorOpcje li.on { background: #257caa }
#nawigator .nawigatorOpcje li { font: 11px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color: #e2f0f6; background: #343839; border-radius: 4px;}
#nawigator .nawigatorOpcje li:hover { background-color: #445458;text-decoration:none;}
.nawigatorSearch .wszystkieKanaly {background: #2e3739; font:12px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color: #e2f0f6; margin-bottom: 8px; border-radius: 4px; }
.nawigatorSearch .wszystkieKanaly:hover { text-decoration: none; background: #445458; }
#nawigator #nawigatorSlider .slider .slide:hover { background-color: #257caa!important; }
#nawigator #nawigatorSlider { background-color:#282a2a!important; border-radius:4px; }
#nawigator #nawigatorSlider .slider .slide.on { /*width:155px;*/ background-color:#257caa !important; }
.nawigatorSearch input { width:231px; padding:4px 28px 6px 8px; margin:0; border:0; height:22px; border-radius:4px; background-color:#343839; border-bottom:0px; font:12px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color:#e2f0f6; }
.nawigatorCzas { display:block; background-color:#282a2a; border-radius:4px; height:33px; padding:6px 10px; position: relative; }
.nawigatorCzas .nawigatorZakresy li { font: 11px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color: #fff; background: #343839; border-radius: 4px; }
.nawigatorCzas .nawigatorZakresy li.on { background-color:#758d14; }
.nawigatorCzas .nawigatorZakresy li:hover {	background: #445458; }
.nawigatorCzas .wyborDaty { color:#fff; font:12px "Lucida Sans Unicode", "Lucida Grande", sans-serif; background-color: #343839; width:225px; border-radius:4px; }
.nawigatorCzas #listyBtn { font: 11px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color: #fff; border-radius: 4px; background:#343839 url(http://a.wpimg.pl/a/i/programtv/2012/s.png) 0px -356px no-repeat; }
.nawigatorCzas #listyBtn:hover { background-color: #445458;text-decoration:none; }
.nawigatorCzas #notesBtn { font: 11px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color: #fff; border-radius: 4px; background:#343839 url(http://a.wpimg.pl/a/i/programtv/2012/s.png) -116px -488px no-repeat; }
.nawigatorCzas #notesBtn:hover { background-color: #445458;text-decoration:none; }
.arrCh .prev, #Slider .prev { border-right: 0px; background: #282a2a url(http://a.wpimg.pl/a/i/programtv/2012/s.png) 10px -952px no-repeat;}
.arrCh .next, #Slider .next { border-left: 0px; background: #282a2a url(http://a.wpimg.pl/a/i/programtv/2012/s.png) -14px -952px no-repeat;}
.lsStat li { background: #282a2a; width: 161px; height: 42px; border-right: #2e3638 solid 1px;}
.lsStat li:hover a { text-decoration: none; background-color: #3b464a; }
.ramowka .lsDay li.empty, .ramowka .lsDay li.past, #Slider .lsDay li.empty, #Slider .lsDay li.past { background: #847813; color: #eaedf0; }
.fldStat.days li.today, #Slider .days li.today { background: #758d14; }
.ramowka .lsDay li, #Slider .lsDay li { background: #374345; font: 13px "Lucida Sans Unicode", "Lucida Grande", sans-serif; color: #fff; text-transform: lowercase; text-align: center; border-top: 0px solid; border-right: #2b3336 2px solid; }
.ramowka .lsDay, #Slider .lsDay { background: #2b3336; }
.ramowka { background-color:#211f20; }
#expandBottomCnt {display:none!important;}
#kontent { background: #1a1a1a; padding-bottom:0px; border-top:0px!important;}
.kontentIn { background: #282a2a; padding: 0 7px 7px!important;}
.ramowka .hour { background: #211f20!important;}
.ramowka .hour:nth-child(even) { background-color: #232122!important;}
.ramowka .hour {border-bottom: #2f3131 dotted 2px;}
.ramowka .hour .prog.on, .ramowka .hour .prog.on:hover { background-image:none;background-color: #365a07!important; padding-bottom:3px; border-bottom:0px; border-top:0px}
.ramowka .hour .prog.on .tm, .ramowka .hour .prog.on:hover .tm { color:#8cae02; }
.ramowka .hour .prog .tm { font: bold 13px Arial, Helvetica, sans-serif; color: #747c7c; }
.ramowka .hour .prog h3 a { color: #e6f4fa; }
.ramowka .hour .prog p {color: #9a908f;}
.teraz, .trwa { display:none!important;}
.ramowka .hour .prog .preview:after {background:none!important;}
.preview-btn {display:none!important}
.nextHrs {color: #82a300; font: 11px "Lucida Sans Unicode", "Lucida Grande", sans-serif; background: #282a2a url(http://a.wpimg.pl/a/i/programtv/2012/s.png) center -330px  no-repeat; text-transform: lowercase;}
.sh1.expandpanelh1 {display:none!important;}
#sheCnt {height:0px!important;}
#boksy {display:none!important;}
.stopkaInfo {display:none!important;}
#stgFooter {display:none!important;}






}
