css:
@-moz-document domain(cohones.mmarocks.pl) {

#content .pageContent, #headerMover #headerProxy, #header { background: none }

html, body { background: #252830 }

body {
	color: #BEC4D4;   
}

a:link, a:visited {
    color: #637986;
}

.primaryContent {
    background-color: #1e2127;
    border-bottom: 1px solid #434857;
}


#QuickSearch, #QuickSearch form, #QuickSearch input {
    background: #383C48;
}

.formPopup .controlsWrapper {
    background: none;
}

#QuickSearch .secondaryControls input {
	border: 1px solid #5D6375;
}


.Menu { border-color: #2B3040 }
.Menu, .Menu .primaryContent, .Menu .secondaryContent {
	background: #2B3040;
}
.Menu .primaryContent, .navPopup .listItem {
    border-bottom: 1px solid #434857;
}
.navPopup .PopupItemLinkActive:hover {
	background-color: #191B21;
    border-radius: 0;
}

.navTabs {
	background: #434857;
    border: 1px solid #434857;
}

.navTabs .navTab.selected .tabLinks {
	background: #4F576F;
}

.navTabs .navTab.selected .navLink {
	background: #4F576F;
    border-color: #4F576F;
}

.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
	background: #434857;
}

.navTabs .navTab.PopupClosed:hover {
	background: #4F576F;
    border-bottom: 2px solid #4F576F;
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    background: #4F576F;
    color:#fff;
}

.Popup .PopupControl.PopupOpen .navLink, .Popup.PopupContainerControl.PopupOpen .navLink {
    color:#fff;
}


.breadcrumb .crust a.crumb, .breadcrumb .crust:last-child a.crumb {
    color: #62697B;
}


.messageList .message {
	margin-bottom: 13px;
}

.messageList .message:nth-child(odd), .messageList .message:nth-child(even) { 
	border:none;
	border-radius: 4px;
	background: #1e2127;
	padding: 16px 11px 10px 8px;
    box-shadow: inset 0 0 40px 0px #111419;
    -webkit-box-shadow: inset 0 0 40px 0px #111419;
}
    
.messageList .message:nth-child(even) {
	background: #111419;
}
    
.messageList .message .primaryContent {
	/*background: #1e2127;*/
    background:none;
}

.messageList .message:nth-child(even) .primaryContent {
	/*background: #111419;*/
    background:none;
}

.bbCodeQuote, .attachedFiles {
	border: 1px solid #363D4C;
	border-radius: 6px;
}

.bbCodeBlock .type, .attachedFiles .attachedFilesHeader {
	background: #363D4C;   
    border:none;
}
.bbCodeBlock .attribution, .attachedFiles .attachedFilesHeader {
	color: #A2ACBF;
}

.bbCodeQuote .quoteContainer, .attachedFiles .attachmentList, .attachment .boxModelFixer {
	background: #272C38;   
    border: none;
}

.bbCodeQuote .quoteContainer .quoteExpand {
    background: -webkit-linear-gradient(top, rgba(242, 246, 248, 0) 0%, rgb(17, 20, 25) 80%);
    background: -moz-linear-gradient(top, rgba(242, 246, 248, 0) 0%, rgb(17, 20, 25) 80%);
    background: -o-linear-gradient(top, rgba(242, 246, 248, 0) 0%, rgb(17, 20, 25) 80%);
    background: linear-gradient(to bottom, rgba(242, 246, 248, 0) 0%, rgb(17, 20, 25) 80%);
}

.messageContent .bbCodeSpoilerText {
	background: none;
    border: 1px solid #434857;
}

.primaryContent a {
	/*color: #968711;*/
    color: #F3A60B;
}

.primaryContent a:hover, .ugc a:hover, .ugc a:focus {
    transition: initial;
    /*font-weight: normal;*/
    color: #FF7100;
    background: none;
    box-shadow: none;
    -webkit-box-shadow: none;
    margin: 0;
    padding: 0;
    text-decoration:underline;
}

.message .messageContent {
	min-height:initial;
}

.messageText {
    font-family: tahoma;
    font-size: 13px;
	line-height: 22px;
}

.message .messageMeta { 
	border-top: 1px solid #434857;
    margin-top: 20px;
}
        
.message .messageMeta .control {
	color: #17566D;
    border: 1px solid #17566D;
    text-shadow:none;
	background-color: transparent;
}        
.message .messageMeta .control:hover {
	color: #1997c6;
    border: 1px solid #1997c6;
}

.message .likesSummary {
	border: none;
    background: #22272F;
}

.embeddedtweet {
    background-color: #3B404E;
    border:none;
    color:#BDC3D3;
}

.message .newIndicator {
    margin-right:-15px;
}

.messageUserBlock .extraUserInfo { display:none }

.messageUserBlock { border:none; padding-bottom:0; }

.messageUserBlock h3.userText { border:none; padding: 5px 0 0 0; color: #727273; }

.messageUserBlock .arrow { display:none }

.messageUserBlock a.username { color: #F3A60B; }

.userBanner.bannerStaff { color: #727273; }

.userBanner.bannerRed { 
	background-color: #770303;
    border-color: #8C0909;
    color: #CC7B7B;
}

.userBanner.bannerRed.wrapped span { background-color: #8C0909; }


.quickReply {
	background:none;    
}

.discussionListItem:hover, .discussionListItem:hover .posterAvatar, .discussionListItem:hover .stats {
	background:#f5f5f5;
}

.avatar img {
	max-width: 60px;
	max-height: 60px;
}

.avatar img, .avatar .img, .avatarCropper {
	background: #000;
    border:1px solid #000;
}

.messageUserInfo {
	width: 85px;
}

.message .messageInfo {
	margin-left:100px;
}


.messageUserBlock div.avatarHolder {
	padding:0
}
.messageUserBlock div.avatarHolder .onlineMarker {
	top:-1px;
}


.PageNav a, .itemPageNav a {
    color: #62A8C1;
    background-color: #17566D;
    border-radius: 2px;
}
.PageNav a:hover, .PageNav a:focus, .itemPageNav a:hover, .itemPageNav a:focus {
    background-color: #1789B3;
    color: #fff;
}
a.PageNavPrev, a.PageNavNext {
	background: none;
}
.itemPageNav a {
    color: #fff !important;
}

.quickReply {
    border:none;
}

.redactor_box {
    border:none;
}

.redactor_toolbar {
    background: #252830;
	border-bottom: none;
}

.blendedEditor .redactor_box .redactor_btn_group ul, .blendedEditor .redactor_box iframe, .blendedEditor .redactor_box .redactor_smilies li {
    opacity:1;
}

.redactor_toolbar li.redactor_btn_group ul {
    background: none;
    border:none;
    box-shadow:none;
    -webkit-box-shadow:none;
    -moz-box-shadow: none;
    -khtml-box-shadow:none;
}

.Responsive .redactor_toolbar li a:before, html .redactor_dropdown a:before {
    color:#BEC4D4;
}
.Responsive .redactor_toolbar li a:hover:before, html .redactor_dropdown a:before {
    color:#1997c6;
}

.redactor_toolbar li a:hover, .redactor_toolbar li a:focus {
    background: none;
    border: none;
}
.redactor_toolbar li a:active, .redactor_toolbar li a.redactor_act {
    border: 1px solid #1997c6;
    background: none;
}

.redactor_box iframe {
	width: 99.9%!important;
    border: 1px solid #434857!important;
}










.button.primary {
	background-color: #596A9E;
    border-radius: 2px;
}
.button.primary:hover {
	background-color: #6577AF;
}


.xenOverlay .primaryContent {
    background: #15171B;
    border-bottom: none;
}

.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading { color: #757272 }

.sidebar .statusPoster textarea { background: #1C2029; }

.profilePostListItem { border-top: 1px solid #4D515D; }

.xenTooltip { background-color: #4F576F; }
/*.xenTooltip .arrow { border-top: 6px solid #4F576F; }*/
.nodeDescriptionTip .arrow, .xenTooltip.statusTip .arrow { border-right-color:#4F576F; }

.xenPreviewTooltip .arrow span { border-top: 15px solid #15171B; }



.secondaryContent {
    background: #353944;
    border: none;
}


.sectionMain {
    border:none;
}

.nodeList .categoryStrip, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .discussionList .sectionHeaders {
    background: #16181D;
    color: #E0E0E1;
}

.nodeList .categoryStrip .nodeTitle a, .discussionList .sectionHeaders a {
    color: #E0E0E1;
}


.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
	background-color: #1e2127;
    border-bottom: 1px solid #434857;
}

.node.node .forumNodeInfo.unread .nodeIcon:after, .node.node .categoryForumNodeInfo.unread .nodeIcon:after {
	color: #4F576F;
}

.node .nodeLastPost {
 	background:none;
}

.nodeLastPost a.avatar img {
    border:none!important;
}



.discussionListItem, .discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: #1e2127;
    border-bottom: 1px solid #434857;
}
.discussionListItem .posterAvatar, .discussionListItem .stats {
    border-bottom:none;
}

.discussionListItem:hover, .discussionListItem:hover .posterAvatar, .discussionListItem:hover .stats {
    background-color: #131519;
}


.tabs { border-bottom:none; }




.discussionListItems .title a {
    color: #F3A60B;
}
.discussionListItems .title a:hover, .discussionListItems .title a:focus {
	color: #FF7100;
}





.sharePage, .breadBoxBottom, .footer, .footerLegal { display: none; }

}
