css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {
.reblog-list-item .reblog-header .reblog-avatar {
  display:none;
}
.reblog-list-item .reblog-header {
  padding-left:0;
} 
.reblog-list .reblog-list-item .reblog-content {
  margin-left: 6px;
  padding-left: 8px;
  border-left: 3px solid #dcdcdc;
}

.reblog-list .reblog-list-item.contributed-content .reblog-content {
  border: none;
  margin: 0;
}
  
.reblog-list .reblog-list-item.contributed-content {
  padding-left: 8px;
}
  
.reblog-list-item.contributed-content .reblog-header {
  display:none;
}
.reblog-list .reblog-list-item {
  border: none;
  padding-top: 0;
  padding-bottom: 10px;
}
.reblog-list {
  border: none;
}
.xtimestamp {
  margin-bottom: 0;
}
.reblog-list-item .reblog-header .post_info_link::after {
  content: ":";
}
.reblog-list .reblog-list-item .reblog-content figure {
  margin-left: 0;
}
}
