css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* --- PLATZ'S CODE --- */

.reblog-header img {
    display:none;
    overflow:hidden;
    text-decoration:none;
    background-color:#fff;
    visibility: hidden;
    border-bottom:0px;
}

.reblog-list-item .reblog-avatar, .reblog-list-item .reblog-avatar .reblog-avatar-image-thumb {
    visibility:hidden;
    width:0px;
    height:0px;
}

.post_info_link {
    margin-left:-34px;
}

.reblog-header {
    margin-top:-5px;
    
}

.post_header {
    border-bottom:0px;
}

.post_header a {
    margin-left:0px;
}

.reblog-content {
    border-left:2px #eee solid;
    padding-left:8px;
    margin-left:15px;
    margin-top:-7px;
}

.reblog-content img {
   margin-left:9px;
    padding-left:0px;
    max-width:500px;
    overflow:hidden;
}

.reblog-title {
    border-left:2px #eee solid;
    margin-left:15px;
    margin-bottom:-1px;
    padding-left:8px;
}

.post.is_regular .tmblr-full>img, .post.is_text .tmblr-full>img, .post.is_note .tmblr-full>img, .post.is_photo .tmblr-full>img, .post.is_photoset .tmblr-full>img, .post.is_link .tmblr-full>img, .post.is_audio .tmblr-full>img, .post.is_video .tmblr-full>img, .post.is_quote .tmblr-full>img, .post.is_answer .tmblr-full>img {
    max-width:500px;
    min-width:auto;
}

.original-reblog-content {
    border-bottom:0px;
}

.reblog-tumblelog-name:after {
    content: ':';
}

.reblog-list-item {
    padding-bottom:5px;
    padding-left:20px;
    padding-right:20px;
    padding-top:10px;
    margin-bottom:0px;
    border-top:0px;
    border-bottom:0px;
}

.original-reblog-content {
    border-top:0px;
}

.post-container {
    border-top:0px;
}

.reblog-list {
    border-top:0px;
}

}
