css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {

  /*[[avatars]]*/
  
  .reblog-list-item .reblog-tumblelog-name:after {
    content: ":";
  }
  
  .reblog-list-item .reblog-content{
    margin-top: -10px;
    padding-left: 15px;
    margin-top: 5px;
    border-left: 2px solid #E7E7E7;
    margin-left: 3px;
  }
  
  .reblog-list-item{
    padding-top: 7px;
    padding-bottom: 12px;
  }
  
  .reblog-list-item .reblog-tumblelog-deactivated-status {
    opacity: 1;
  }

}

style_setting_options.value:
.reblog-list-item .reblog-avatar, .reblog-list-item .reblog-avatar .reblog-avatar-image-thumb {
    visibility: hidden;
    width: 0px;
  }
  
  .reblog-list-item .reblog-tumblelog-name {
    margin-left: -35px;
    border-bottom: 1px solid #E7E7E7;
    font-weight: inherit;
  }
