css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("toyhou.se") {

  .alert, a.badge, span.thumb-character-name, tr.row.forum-thread-row.sticky, #dropdownMenuButton, div.pb-0:nth-child(1) > ul:nth-child(3) > li:nth-child(1) > a:nth-child(1),
  button.btn.btn-primary, label.btn.btn-primary, label.btn.btn-primary:hover, div.input-group-addon, input, a.btn, li.header, a.active {
  background-color: #820000 !important;
  border-color: #191919 !important;
  color: #191919 !important;
    font-family: Helvetica !important;
    font-weight: normal !important;
    text-transform: uppercase !important;
  box-shadow: 0px 0px #191919 !important;}
  
  span.characters-tag-content.btn.btn-sm.btn-secondary {background-color: #820000 !important; color: #191919 !important; border-color: #191919 !important;}
  
  a.page-link, span.page-link {background-color: #191919 !important; border-color: #2b2b2b !important; color: #aaa !important;}
  a.page-link:hover {background-color: #2b2b2b !important; border-color: #2b2b2b !important; color: #ddd !important;}
  
  div.fr-spoiler, div.fr-spoiler::before {background-color: #820000 !important; color: #aaa !important;}
  
  div.img-thumbnail {border: 0px solid !important; background-color: #191919 !important;}
  
  a, span {color: #aaa !important; box-shadow: 0 0 0 0 !important;}
  
  a:hover {color: #ddd !important; box-shadow: 0 0 0 0 !important;}
  
  #content, span.thumb-character-user, a.btn.btn-sm.btn-default.user-name-badge, textarea,
  div.comments:nth-child(2) > form:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(6) > div:nth-child(1) > div:nth-child(1), div.comments:nth-child(2) > form:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(6) > div:nth-child(1) > div:nth-child(2) > div:nth-child(1), #terms, #is_tradeable, .fr-element, .fr-toolbar
  {background-color: #191919 !important;
  color: #aaa !important;
  box-shadow: 0 0 0 0 !important;
  border-color: #191919 !important;}
  
  table {border-color: #191919 !important; background-color: #2b2b2b !important;}
  
  h4.forum-category-title:nth-child(1) {padding-top: 10px !important;}
  h4.forum-category-title {padding-bottom: 6px !important;}
  
  div.forum-category-forum:hover {background-color: #820000 !important; color: #ddd !important;}
  
  a.dropdown-item:hover {background-color: #191919 !important;}
  
  div.forum-category-forum, div.forum-category-wrapper {border-top: 1px solid #191919 !important;
  border-bottom: 1px solid #191919 !important;
  border-right: 0px solid !important;
  border-left: 0px solid !important;}
  
  .card-block, li, ul, .card, label.btn, div.forum-main,
  a.img-thumbnail, #sidebar, div.dropdown-menu, .bg-faded {background-color: #2B2B2B !important;
  border-color: #191919 !important;
  box-shadow: 0 0 0 0 !important;
  color: #aaa !important;}
  
  button {color: #aaa !important;}
  button:hover {color: #ddd !important; background-color: #191919 !important;}
  input.form-control {color: #aaa !important;}
  
  i.fa.fa-plus:hover, i.fa.fa-minus:hover {color: #ddd !important;}
  i.fi-folder, div.characters-folder-name, i.fa.fa-plus, i.fa.fa-tags, i.fi-magnifying-glass, i.fa.fa-minus, i.fa.fa-warning {color: #191919 !important;}
  i.fi-star, i.fi-arrow-up, i.fa.fa-tags.mr-1 {color: #820000 !important; box-shadow: 0 0 0 0 !important;}
}
