css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allbuffs.com") {

    .PanelScroller .scrollContainer, .PanelScrollerOff .panel {
        background-color: #E5CC89;
    }

    #content .pageContent {
        background-color: #E5CC89;
    }
    
    div#content {
        background-color: #000;
    }
    
    #content .pageContent {
        border-radius: 4px;
    }
    .nodeList .node.level_1 {
        border: 1px solid #E5CC89;
        box-shadow: 1px 1px 8px 0px rgba(0, 0, 0, 0.75);
    }    

    div.footer {
        background-color: #000;
    }
    html {
        background-color: #000;
    }
    
    #header {
        background-image: none;
    }
    
    #headerMover #header {
        background-color: #000;
    }
    
    .UserOnline, .UserOnlineInvisible, .UserOffline {
        display: none !important;
    }
    
    .messageUserBlock {
       color: #000;
       background-color: #E5CC89;
    }
    
    .messageUserBlock .extraUserInfo {
       background-color: #E5CC89;
    }

    .messageUserBlock div.avatarHolder {
       background-color: #E5CC89;
    }

    .messageUserBlock h3.userText {
       border-top: 1px solid #000;
       border-bottom: 1px solid #000;
    }

    .messageUserBlock .arrow {
       border-image: none;
       -moz-border-left-colors: #E5CC89;
    }
    .messageUserBlock .arrow span {
       border-image: none;
       -moz-border-left-colors: #E5CC89;
    }
    .messageUserBlock a.username {
       font-size: 14px;
    }
    .messageUserBlock .userTitle {
       font-size: 11px;
       padding-bottom: 5px;
    }
    .messageList .message {
       background-color: #F2E3BD;
       border: 1px solid #95782D;
    }
    .nodeList .categoryStrip {
       background-color: #000 !important;
       color: #E5CC89;
    }
    .nodeList .categoryStrip .nodeTitle a {
    color: #E5CC89 !important;
    }
    #Notices .panel .noticeContent {
       background-color: #F2DEA9;
       color: #000;
    }
    .subHeading a, .nodeList .categoryStrip .nodeTitle a {
       color: #F2E3BD !important;
       text-shadow: 0px 0px 0px transparent, 1px 1px 1px #F2E3BD;
    }
    .subHeading {
       background-color: #000;
    }
    .message .likesSummary {
       border: 1px solid #F2E3BD;
       background-color: #E5CC89;
    }
    .discussionListItem {
       background-color: #F2E3BD !important;
       border-bottom: 1px solid #95782C;
    }
    .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
       background-color: #F2DEA9 !important;
       border-bottom: 1px solid #95782C;
    }
    .nodeList .categoryStrip {
       color: #F2DEA9 !important;
    }
    .secondaryContent {
       background-color: #F2DEA9;
       border-bottom: 1px solid #95782C;
    }
    .WidgetFramework_WidgetRenderer_Threads_FullThreadList .messageInfo {
       background-color: #F2DEA9;
    }
    .WidgetFramework_WidgetRenderer_Threads_FullThreadList .info {
       border-top: 1px dashed #95782C;
       background-color: #E5CC89;
    }
    .WidgetFramework_WidgetRenderer_Threads_FullThreadList .message {
       background: #F2DEA9 none repeat scroll 0% 0%;
       padding-bottom: 0px;
       border: 1px solid #95782C;
    }
   .discussionList .sectionHeaders a {
    color: #CFB87C !important;
    }
   .redactor_toolbar {
    background-color: #000;
   }
   .blendedEditor .redactor_box .redactor_btn_group ul, .blendedEditor .redactor_box iframe, .blendedEditor .redactor_box .redactor_smilies li {
    opacity: 1;
   }
   .redactor_toolbar li.redactor_btn_group ul {
    background-color: #F2DEA9;
    border: 1px solid #E5CC89;
   }
   .redactor_box {
    border: 1px solid #000;
    background-color: #F2E3BD;
    border: 1px solid #EFEFEF;
   }
   .quickReply {
    background-color: #E5CC89;
    border: 1px solid #AC9E78;
   }
}
