css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {
  .reblog-tumblelog-name {
    font-weight: lighter!important;
    font-size: 10px!important;
  }
  
  .reblog-tumblelog-name:after {
    content: ':';
  }
  
  .reblog-avatar {
    display:none!important;
  }
  
  .reblog-header {
    padding-left:unset!important;
  }
  
  .reblog-list-item--text-post-original .reblog-tumblelog-name {
    display:none!important;
  }
  
  div:not(.original-reblog-content):not(.reblog-list-item--text-post-original) > .reblog-content {
    padding-left:15px!important;
  }
  
  .reblog-list-item {
    border-bottom:3px solid #e7e7e7;
  }
  .reblog-list-item.contributed-content {
    border-top: 3px solid #e7e7e7;
  }
  .reblog-list {
    border-top: unset!important;
  }
}
