css:
@-moz-document domain("irccloud.com") {
body {
    background-color: #fff
}

* {
    font-size: 11px !important;
    border-radius: 0 !important;
    border-top-right-radius: 0 !important;
    border-top-left-radius: 0 !important;
    border-bottom-right-radius: 0 !important;
    border-bottom-left-radius: 0 !important;
    box-shadow: none !important;
}

::-webkit-scrollbar-thumb, ::-webkit-scrollbar-track-piece {
    border-radius: 0;
}

::-webkit-scrollbar {
    width: 1px;
}

::-webkit-scrollbar-thumb {
    background-color: #47545A !important;
    border: 0;
}

::-webkit-scrollbar-track-piece {
    background-color: transparent;
    border: 0;
}

::-webkit-scrollbar-thumb:window-inactive {
    background-color: #202020;
}
   


td#headercell {
    
}

div.log div.me span.message, div.log div.me span.message a {
    font-style: italic;
    color: #db7676;
    margin: -3px;
    border: 0;
}


div.log span.me_prefix {
    color: #bbb;
    font-style: italic;
    display: none;
}

li.mono,
p.mono {
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
}

div.shim, td.nickcell a.nick, td.inputcell textarea, div.log div.messageRow, div.log div.me a.user {
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
}

.chat span.mode_symbol,
.chat span.mode_pill {
    display: none !important;
}


div.log div.row.highlight {
    background-color: inherit !important

}

div.log div.self
{
    background-color: inherit !important

}

div.log div.self a.author {

 text-decoration: underline;
}

div.log div.row.highlight a.author {
    border-left: solid 5px #C0DBFF;
    margin-left: -7px;
    padding-left: 2px

}

.buffer.bufferLink.author.user {
    display: inline-block;
    min-width: 90px;
    border-right: solid 1px;
}



.bufferHead {
border: 0;
    background-color: #FFF;
}

.mainContainer, table.buffer {
    border: 0;
}

#buffers .ssl h2 a.buffer,
#buffers h2 a.buffer {
    background: none;

}

#buffers li.connection {
        background: none;
}

.join.show {
    display: none !important;
}

ul.memberList li ul {
    background-color: #fff !important;
    border: 0 !important;
}

ul.memberList li h2 {
   background-color: #fff !important;
}
div.memberwrapper { 
    border: 0;
    background-color: #fff;
}

#mainwrapper {
    padding-left: 140px;
    padding-right: 0;
}


div#sidebarwrapper {
    left: 0;
    height: 100%;
    width: 140px;
}

.showMembers div.buffermain {
    padding-right: 134px;
}

div.memberwrapper {
    width: 134px;
}

#footer { 
    display: none;
}

#maintable tbody tr #headercell #statusActions{
    border-bottom: solid 1px gray;
    border-left: solid 1px gray;
    display: block;
    position: absolute;
    background: #fff;
    top: -42px;
    right: 0;
    z-index: 5000;
    padding: 3px;
}

#maintable tbody tr #headercell #title {
    display: none;
}

#maintable tbody tr #headercell #statusActions:hover {
    top: 0px;
}

div.log div.notice { 
    background: none;
}
}
