css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {
  
	.reblog-list-item {

	}
	  
		.reblog-list-item .reblog-header {
			padding-left: 0px !important;
		}
		  
			.reblog-list-item .reblog-header .reblog-avatar {
				display: none !important;
			}
			  
			.reblog-list-item .reblog-header .reblog-tumblelog-name {
				font-weight: normal !important;
				text-decoration: underline !important;
			}
			  
			.reblog-list-item .reblog-header .reblog-tumblelog-name::after {
				content: ":" !important;
			}

				.reblog-list-item .reblog-header .reblog-tumblelog-name .reblog-tumblelog-deactivated-status {
					display: none !important;
				}


		  
		.reblog-list-item .reblog-title {
			border-left: 5px solid #DCDCDC !important;
			padding-left: 10px !important;
			margin-bottom: 0px !important;
			padding-bottom: 10px !important;
		}
		  
		.reblog-list-item .reblog-content {
			border-left: 5px solid #DCDCDC !important;
			padding-left: 10px !important;
		}
  
			.reblog-list-item .reblog-content p {
				margin:0px !important;
			}
		  
			.reblog-list-item .reblog-content .tmblr-full {
				padding-left:20px !important;
				margin-top: 0px !important;
        margin-right: -10px !important;
			}
}
