css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("aliexpress.com") {  
  
  body{background: url(https://www.radiantresearch.com/wp-content/uploads/2014/10/21219-grey-texture-2560x1600-abstract-wallpaper.jpg)!important;  background-color: #111 !important;background-attachment: fixed !important;background-clip: border-box !important;background-origin: padding-box !important;background-position: center center !important;background-repeat: no-repeat !important;background-size: cover !important}
    
  /*body, html{background: linear-gradient(to bottom  , rgba(50, 50, 50, 0.8) 3%, rgba(0, 0, 0,  0.9) 110%)!important;
  background-attachment: fixed !important;background-color: #000 !important;	background-clip: border-box !important;
	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important}*/
  
  *{font-family:Constantia!important;color:#999 !important;border-color:#999!important}  
  a:hover{color:#3ce!important}  
  
  .index-page, .categories-main, .waterfall-tab a, .f-link-list {background-color:transparent!important}  
  .top-container-bg, .header, .categories-main .categories-content-title, dl.cl-item>dt:hover, .new-user-guide, .superdeals-list, .waterfall-tab a.selected, .buyers-show-list li .item-inner, .related-recommend .item-inner, .flyout-quick-entry li a:hover, .flyout-user-signIn .sign-btn, .flyout-visitors-login .join-btn, .narrow-down-area, .list-item .has-sku-image, .product-info-operation, .bp-horizontal-banner, .product-desc .ui-box-title, .detail-page .multi-lang-switch-desc a, .ui-tab-primary .ui-tab-nav li a, .ui-pagination-body, .p4pExpress .relatied-product-slide, .p4pExpress .relatied-products-container, .sku-attr li, .seller-discount-show, .nav-cart .cart-number {background-color: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;transition:all 350ms ease-in-out !important}
  
  .cl-item .sub-cate, .waterfall-tab .container, .site-server-box, .footer-sns, .site-footer, .user-account-main, .flyout-quick-entry li:first-child a, .gallery-mode .list-item:hover .item, .view-filter, .ui-textfield-system, #filter-options .filter-item, #list-currency-c, #show-piece-price, .box .hd, .box .bd, .col-extra-p4p .p4p-side h4, .col-extra-p4p ul, #shopping-history .show-history, .buyers-show-header .ui-tab-nav li a, .ui-pagination-navi a, .ui-pagination-navi span, .ui-button-normal, .ui-button-normal:active, .ui-button-normal:link, .ui-button-normal:visited, a.buy-now-btn, a.add-to-cart-btn{background-color: rgba(0, 0, 0, 0.95)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;transition:all 350ms ease-in-out !important}

   a.buy-now-btn, a.add-to-cart-btn, .detail-page .multi-lang-switch-desc a, .ui-button-normal, .ui-button-normal:link, .ui-button-normal:visited, .ui-button-normal:active{background-image:linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important;background: -webkit-linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important}
    
  .cl-item .cate-name span {background-color: rgba(0, 0, 0, 0.5)!important;border:1px!important; box-shadow: 0px 0px 10px rgba(50, 50, 50, .9 )inset!important}
  
  :root .shop-list .shop-info{background-color: rgba(0, 0, 0, 0.9)!important;border:1px!important; box-shadow: 0px 0px 10px rgba(50, 50, 50, .9 )inset!important}  
  
  .ui-fixed-panel{left:5px!important;opacity:.2!important;transition:all 750ms ease-in-out !important}
  .ui-fixed-panel:hover{opacity:1!important;transition:all 750ms ease-in-out !important}  
  .top-banner-container, .top-lighthouse, div.container:nth-child(15){display:none!important}  
  .advertise-main{opacity:.3!important;transition:all 1s ease-in-out !important}
  .advertise-main:hover{opacity:.9!important;transition:all 1s ease-in-out !important}}
