css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("zarabiam.com") {

body{
    background: rgba(0, 0, 0, 0) url("http://i.imgur.com/vPuQ0Md.png") repeat scroll 0 0;
}

.thead {
    background: #17687c none repeat scroll 0 0;
    border-bottom: 2px solid #151515;
    border-radius: 3px 3px 2px 2px;
    color: #ffffff;
    height: 30px;
    line-height: 25px;
    padding-left: 16px;
}

#forum_block {
    background: #202020 none repeat scroll 0 0;
    border-radius: 3px;
    margin-bottom: 15px;
    margin-right: 235px;
    padding: 14px;
    width: auto;
    box-shadow:0 0 8px #000;
}

.tborder {
    background: #151515 none repeat scroll 0 0;
    border: 1px solid #282828;
    border-collapse: unset;
    margin: 28px auto auto;
    padding: 2px;
    width: 100%;
}
a:link, a:visited {
    color: #fafafa;
    outline: medium none;
    text-decoration: none;
}
.trow1 {
    background: #252525 none repeat scroll 0 0;
    border: 0 none;
    padding: 13px 8px !important;
}
.trow1_summary {
    background: #252525 none repeat scroll 0 0;
}
.trow2 {
    background: #252525 none repeat scroll 0 0;
    border: 0 none;
    padding: 13px 8px !important;
}
.trow2_summary {
    background: #252525 none repeat scroll 0 0;
}
.trow1, .trow2 {
    background: #252525 none repeat scroll 0 0;
    border-bottom: 1px solid #151515;
    border-top: 1px solid #303030;
}

.forumbit_foot {
    display:none;
}

.tcat {
    display: none;
}
.menu ul {
    background: #17687c none repeat scroll 0 0;
}

.menu .separate {
    background:none;
}
.menu ul a {
    background: #0a5768 none repeat scroll 0 0;
}
#panel #panel_header {
background: #17687c none repeat scroll 0 0;
    border-radius: 2px;
    height: 26px;
    margin-bottom: 12px;
    width: 100%;
}
#panel_header_right{display:none;}
#panel_header_left{display:none;}
#messresult{background:#252525 none repeat scroll 0 0;}
.tfoot{background:#17687c none repeat scroll 0 0; height:26px; border-radius:1px!important;}
#posts{margin-top:-31px;}
}
