css:
@-moz-document domain("www.tumblr.com") {
div.reblog-header a.reblog-avatar{ display: none; width: 1px; height: 1px; margin-left: -20px;}
a.reblog-tumblelog-name.post_info_link{ font-weight: normal; letter-spacing:0; font-size: 15px; margin-left: -35px;}
a.reblog-tumblelog-name.post_info_link:after{ content: ':';}
div.reblog-header{ font-weight: normal; margin-bottom: 0px !important;}
div.reblog-content{ border-left: 2px solid; padding-left: 10px; margin-left: 2px;}
    
span.reblog-avatar.inactive{ display: none;}
span.reblog-tumblelog-name.inactive {margin-left: -35px; font-weight: normal; letter-spacing:0; font-size: 15px;}


div.reblog-list-item{ margin-top: -10px;}



}
