css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
  @keyframes qualityrotation {
    from {
      transform: rotate(20deg);
    }
    20% {
      transform: rotate(-20deg);
    }
    75% {
      transform: rotate(15deg);
    }
    75.5% {
      transform: rotate(10deg);
    }
    to {
      transform: rotate(-10deg);
    }
  }
  .compose-button, .new_post_buttons_container .post_avatar, .rapid-recs-container {
    display: none!important;
  }
  .l-header-container {
    box-shadow: none;
    padding-top: 20px;
    padding-bottom: 20px;
    z-index: 413;
    position: absolute;
    transform: none;
    background: none;
  }
  .plexi.color {
    position: fixed;
    display: block;
    height: 132px!important;
    z-index: 412;
    background-position: 50% 100%;
    background-color: transparent;
    background-repeat: repeat-x;
  }
  .discover-context {
    background-color: #36465D;
    background-position: 50% -28px;
    background-repeat: repeat-x;
  }
  body.discover {
    background-color: transparent;
  }
  .popover--base {
    z-index: 2103;
  }
  .l-header {
    width: 960px!important;
    max-width: 960px!important;
    margin: auto;
    position: static!important;
  }
  .l-header>*:not(#search_field) {
    position: fixed;
  }
  .l-container {
    padding-top: 135px;
  }
  .post_avatar.post-avatar--sticky .post_avatar_wrapper {
    top: 153px;
  }
  h1.logo, .logo-anchor {
    min-width: 221px;
    height: 92px;
    display: block!important;
  }
  #tabs_outer_container {
    right: calc(50% - 480px);
    margin-top: 20px;
    box-shadow: 0 -50px #36465D;
    z-index: 1;
    height: 110px;
    background-position: 100% -17px;
    background-repeat: no-repeat;
    background-color: transparent;
  }
  #search_field {
    width: 200px;
    position: relative;
    top: 115px;
    left: 765px;
    z-index: 0;
  }
  #discover_actions_discover #search_field {
    display: none;
  }
  .ui_search .search_form_field {
    margin-right: 0;
  }
  .ui_search .search_form_field::before {
    display: none;
  }
  #search_query {
    color: white!important;
    padding-left: 48px!important;
    background-color: transparent;
    background-repeat: no-repeat;
    background-position: 48px 11px;
    transition: none;
  }
  #search_query:active, #search_query:focus, #search_query:valid {
    background: none;
  }
  #search_query::-webkit-input-placeholder {
    display: none!important;
    opacity: 0!important;
  }
  #search_query::-moz-placeholder {
    display: none!important;
    opacity: 0!important;
  }
  #search_query:-ms-input-placeholder {
    display: none!important;
    opacity: 0!important;
  }
  #popover_search {
    position: absolute!important;
    z-index: 0!important;
    top: 240px!important;
    left: calc(50% + 280px)!important;
    width: 170px;
    color: white;
    border-radius: 0;
    box-shadow: none;
    background: transparent;
  }
  #popover_search .popover_inner, #popover_search .search_results_section {
    padding: 0;
    background: #36465D;
    border-radius: 0;
    box-shadow: none;
  }
  #popover_search h3 {
    color: white;
  }
  #popover_search a, #popover_search.search_popover .popover_inner a:hover, #popover_search .popover_menu_item:hover a, #popover_search .popover_menu_item:hover, #popover_search .result_link:hover .result_title {
    color: white!important;
    background-color: transparent!important;
  }
  #popover_search .result_thumb {
    display: none;
  }
  #popover_search .popover_menu_item {
    position: relative;
  }
  #popover_search .popover_menu_item:nth-child(3n) {
    left: 3px;
  }
  #popover_search .popover_menu_item:nth-child(3n+1) {
    left: -2px;
  }
  #popover_search .popover_menu_item:nth-child(4n-1) {
    top: -3px;
  }
  #popover_search .popover_menu_item:nth-child(4n-3) {
    top: 2px;
  }
  h1.logo:hover {
    animation: qualityrotation ease-out 1s alternate infinite;
  }
  .svg-logo {
    display: none;
  }
  #user_tools .tab.iconic .tab_anchor {
    width: 50px!important;
    opacity: 1!important;
  }
  #user_tools .tab.iconic .tab_anchor::before, #user_tools .tab.iconic .tab_anchor i {
    display: none!important;
  }
  #new-xkit-control > a {
    background: none!important;
  }
  #user_tools .tab.iconic .tab_anchor::after {
    display: block!important;
    color: white!important;
    text-align: left;
    font-size: 13px;
    white-space: normal;
  }
  #user_tools #home_button .tab_anchor {
    margin-top: 8px;
  }
  #user_tools #home_button .tab_anchor::after {
    content: "go to you're house";
  }
  #user_tools #discover_button .tab_anchor {
    margin-top: -2px;
  }
  #user_tools #discover_button .tab_anchor::after {
    content: "how about posts ?";
  }
  #user_tools #inbox_button .tab_anchor {
    margin-top: -5px;
  }
  #user_tools #inbox_button .tab_anchor::after {
    content: "get that mail dude!!";
  }
  #user_tools #messaging_button .tab_anchor {
    margin-left: -10px;
    margin-right: 10px;
  }
  #user_tools #messaging_button .tab_anchor::after {
    content: "do a convesration";
  }
  #user_tools #activity_button .tab_anchor {
    margin-top: 7px;
  }
  #user_tools #activity_button .tab_anchor::after {
    content: "msision control";
  }
  #user_tools #new-xkit-control {
    padding: 0;
  }
  #user_tools #new-xkit-control a {
    margin-top: 0px;
  }
  #user_tools #new-xkit-control a::after {
    content: "xkit snettings";
  }
  #user_tools #account_button .tab_anchor {
    margin-top: -1px;
  }
  #user_tools #account_button .tab_anchor::after {
    content: "look at your blogs";
  }
  .tab-bar-container .tab_notice {
    display: none;
  }
  #inbox_button .tab_notice, #messaging_button .tab_notice {
    background-color: transparent;
    position: relative;
    top: 48px;
    left: 3px;
    border: none;
    display: block!important;
  }
  #messaging_button .tab_notice {
    top: 28px;
  }
  #inbox_button .tab_notice .tab_notice_value, #messaging_button .tab_notice .tab_notice_value {
    color: red;
    display: inline-block;
    width: 40px;
    white-space: normal;
  }
  #inbox_button .tab_notice .tab_notice_value::after {
    content: " new mails";
  }
  #messaging_button .tab_notice .tab_notice_value::after {
    content: " cool words";
  }
  #right_column {
    min-height: 580px;
    min-width: 280px;
    margin-right: -60px;
    background-repeat: no-repeat;
  }
  #right_column * {
    display: none;
  }
  #tab_switching {
    width: 100%;
    display: inline-block!important;
    position: static;
    z-index: 2;
    opacity: 1;
    visibility: visible;
  }
  #tab_switching .tab_blogs {
    position: absolute;
    width: 100%;
    top: 0;
    left: 0;
    margin: 0;
  }
  #tab_switching .tab_blogs>*, #tab_switching .blog_icon {
    display: none;
    float: none;
  }
  #tab_switching .tab_blogs>*:nth-child(2) {
    display: block;
    width: 100%;
  }
  #tab_switching .blog_name {
    left: calc(50% + 287px);
    padding: 0;
    margin: 0;
    display: inline;
  }
  #tab_switching .blog_name_span::before {
    position: relative;
    top: 260px;
    content: "the blog";
    font-size: 15px;
    font-weight: normal;
  }
  #sidebar_footer_nav {
    position: absolute;
    bottom: auto;
    top: 0;
  }
  #sidebar_footer_nav .sidebar_nav>* {
    display: none;
    float: none;
  }
  #sidebar_footer_nav .sidebar_nav>*:nth-child(1) {
    display: block;
  }
  .new_post_buttons_container {
    background-repeat: no-repeat;
    min-width: 676px;
    min-height: 179px;
    margin-left: -95px!important;
    margin-top: 50px!important;
  }
  .new_post_buttons {
    background: none!important;
    max-width: 500px;
    top: 10px;
    left: 90px;
  }
  .new_post_buttons_container.post-forms-above-glass {
    background: none;
  }
  .new_post_buttons_container .post-form {
    margin-left: 110px!important;
  }
  .popover--post-settings-dropdown {
    z-index: 234982;
  }
  .new_post_label {
    background: none!important;
    width: 70px!important;
    padding-top: 10px!important;
    text-align: left;
    color: black!important;
  }
  #new_post_label_text {
    margin-top: 7px;
  }
  #new_post_label_text::after {
    content: "text's";
  }
  #new_post_label_photo {
    margin-top: 0px;
  }
  #new_post_label_photo::after {
    content: "picture man";
  }
  #new_post_label_quote {
    margin-top: 3px;
  }
  #new_post_label_quote::after {
    content: "talking speech";
  }
  #new_post_label_link {
    width: 40px!important;
    margin-top: 12px;
  }
  #new_post_label_link::after {
    content: "net grab";
  }
  #new_post_label_chat {
    width: 75px !important;
    margin-top: 10px;
  }
  #new_post_label_chat::after {
    content: "interac tranfer";
  }
  #new_post_label_audio {
    width: 78px!important;
    margin-top: 7px;
  }
  #new_post_label_audio::after {
    content: "tunez";
  }
  #new_post_label_video {
    margin-top: 18px;
  }
  #new_post_label_video::after {
    content: "vidoeb";
  }
  .new_post_label i {
    display: none;
  }
  .new_post_label_text {
    display: none;
  }
  
  #posts>.post_container:not(#new_post_buttons), #posts #new_post_buttons .post-container {
    width: 540px;
    margin: 20px 20px 40px!important;
  }
  #posts>.post_container:not(#new_post_buttons), #posts #new_post_buttons .post-container, #left_column .notification_pane, #xkit-control-panel, .post-form-modal-content .post-container, .popover--base {
    border-image-slice: 10 fill;
    border-image-width: 10px;
    border-image-outset: 10px;
    border-image-repeat: round;
    border-style: solid;
  }
  .popover--active, .pop-menu {
    box-shadow: none;
    background: none;
  }
  .popover--base {
    box-shadow: 0 0 22px rgba(0, 0, 0, .15);
  }
  .notifications_page {
    margin-top: 20px;
  }
  #left_column .notification_pane .divider {
    background: none;
  }
  #posts #new_post_buttons .post-container {
    position: relative;
    right: 15px;
  }
  .post_container:nth-child(3n) .post_info_link, .post_full .post_header .post_info .post_info_link:first-child {
    color: black!important;
    font-weight: normal!important;
  }
  .post_container:nth-child(3n) .reblog_source .post_info_link, .post_container:nth-child(5n) .reblog_source .post_info_link {
    position: relative;
    top: -5px;
  }
  .post_container:nth-child(3n) .post_avatar_link {
    position: relative;
    left: -8px;
  }
  .post_container:nth-child(5n) .post_avatar_link, .post_container:nth-child(5n+2) .post_avatar_link {
    position: relative;
    top: 11px;
  }
  .post_container:nth-child(6n+3) .post_avatar_link {
    transform: rotate(2deg);
  }
  .post_container:nth-child(6n) .post_avatar_link {
    transform: rotate(-2deg);
  }
  
  #posts li:nth-child(4n) {
    position: relative;
    left: -6px;
  }
  #posts li:nth-child(4n+1) {
    position: relative;
    left: 3px;
  }
  #posts li:nth-child(4n+2) {
    position: relative;
    left: -2px;
  }
  
  #posts li:nth-child(3n) {
    position: relative;
    top: -3px;
  }
  #posts li:nth-child(3n+1) {
    position: relative;
    top: -2px;
  }
  
  .post_full:hover .post_permalink {
    border-width: 0;
  }
  .post_permalink {
    width: 60px!important;
    opacity: 1!important;
    border-width: 0!important;
  }
  .post_permalink::after {
    content: "link it up";
  }
  .post_container:nth-child(3n) .post_permalink::after {
    content: "get linking";
  }
  .post_container:nth-child(7n+1) .post_permalink::after {
    content: "link me up bro";
  }
  .post_container:nth-child(7n+5) .post_permalink::after {
    content: "here a link";
  }
  
  .messaging-inbox .inbox-compose-toggle .compose-start::before {
    font-family: inherit;
    font-size: 14px;
    content: "new one";
  }
  
  .messaging-conversation .conversation-header .title {
    font-size: 1px;
    line-height: 14px;
  }
  .messaging-conversation .conversation-header .title a {
    font-size: 14px;
  }
  .messaging-conversation .conversation-header .title a:last-of-type {
    position: relative;
    top: 3px;
  }
  .messaging-conversation .conversation-header .title a:last-of-type::before {
    content: " adn ";
    padding: 0 5px;
  }
  
  .messaging-conversation .conversation-message .inline-activity, .conversation-actions, .popover--account-popover .popover .popover_header, .popover--account-popover .popover_subsection:first-of-type, .popover--account-popover .blog-sub-nav-details {
    text-transform: lowercase;
  }
  
  .conversation-message:nth-child(5n) .message-bubble {
    position: relative;
    left: -4px;
  }
  .conversation-message:nth-child(5n-3) .message-bubble {
    position: relative;
    left: 3px;
  }
  .conversation-message:nth-child(4n-1) .message-bubble {
    position: relative;
    top: -3px;
  }
  .conversation-message:nth-child(4n-3) .message-bubble {
    position: relative;
    top: 2px;
  }
  
  .conversation-message:nth-child(8n-2) .avatar {
    transform: rotate(90deg);
  }
  .conversation-message:nth-child(12n-7) .avatar {
    transform: rotate(-90deg);
  }
  .conversation-message:nth-child(14n-4) .avatar {
    transform: rotate(180deg);
  }
  
  .messaging-conversation .conversation-compose .submit::before {
    font-family: inherit;
    font-size: 14px;
    content: "sned";
    font-weight: bold;
  }
  
  /* explore page */
  
  body.discover {
    background-repeat: no-repeat;
    background-position: 35% 300px;
    overflow: hidden;
  }
  body.discover #tabs_outer_container {
    background: none;
    box-shadow: none;
  }
  .discover-controls-wrapper, .discover-content {
    opacity: 0;
    pointer-events: none;
  }
  
  /* i just have all the background urls here for reference in case i need to change stuff */
  
  .plexi.color, .discover-context {
    background-image: url("http://i.imgur.com/KmhZCip.png");
  }
  .logo-anchor {
    background-image: url("http://i.imgur.com/0yPhIxu.png");
  }
  #tabs_outer_container {
    background-image: url("http://i.imgur.com/U3DgwIj.png");
  }
  #search_query {
    background-image: url("http://i.imgur.com/ADIwrcQ.png");
  }
  #right_column {
    background-image: url("http://i.imgur.com/WKJE8Y8.png");
  }
  .new_post_buttons_container {
    background-image: url("http://i.imgur.com/VdhGw5r.png");
  }
  #posts>.post_container, #posts #new_post_buttons .post-container, #left_column .notification_pane, #xkit-control-panel, .post-form-modal-content .post-container, .popover--base {
    border-image-source: url("http://i.imgur.com/6ESRe93.png");
  }
  #posts>.post_container:nth-child(odd) {
    border-image-source: url("http://i.imgur.com/f7IsxoQ.png");
  }
  body.discover {
    background-image: url("http://i.imgur.com/c6tiZKy.png");
  }
}
