css:
@-moz-document regexp('.+page=ws-plugin--s2member-.+') {
	body[class*='s2member'] #wpwrap {
		background: inherit;
	}
	.ws-menu-page {
		margin: 15px 15px 15px 0 !important;
	}

	.ws-menu-page a {
		color: #0073aa !important;
	}
	.ws-menu-page a:active, .ws-menu-page a:hover {
		color: #00a0d2 !important;
	}

	.ws-menu-page h2 {
		margin-bottom: 10px !important;
		padding: 35px 0 0 110px !important;
		color: #23282d !important;
		font-size: 25px !important;
		font-style: italic !important;
		text-shadow: none !important;
	}
	.ws-menu-page-groups-hide, .ws-menu-page-groups-show {
		position: absolute !important;
		right: 15px !important;
		margin: 0 !important;
		border: 0 !important;
		padding: 6px !important;
		background-color: #32373C !important;
		color: #ddd !important;
	}
	.ws-menu-page-groups-show {
		top: 50px !important;
	}
	.ws-menu-page-toolbox {
		width: 300px !important;
		margin: 0 35px 0 0 !important;
	}
	.ws-menu-page-toolbox>.links {
		text-align: left !important;
	}
	.ws-menu-page-toolbox>.links a {
		min-width: 135px !important;
		margin: 0 0 0 10px !important;
		color: #00a0d2 !important;
		font-weight: bold !important;
		text-shadow: 1px -1px #fff !important;
	}
	.ws-menu-page-toolbox>.links a:active, .ws-menu-page-toolbox>.links a:hover {
		color: #0073aa !important;
	}
	.ws-menu-page-toolbox>.links i {
		width: 14px !important;
	}

	div.updated.fade, div.error.fade {
		margin: 10px 20px 10px 5px !important;
	}
	#ws-plugin--s2member-options-form {
		margin: 0 2px 0 0 !important;
	}
	
	.ws-menu-page-table {
		border-spacing: 1px !important;
	}
	.ws-menu-page-group-header:first-of-type, .ws-menu-page-r-group-header:first-of-type {
		margin-top: 1px !important;
	}
	.ws-menu-page-group-header, .ws-menu-page-r-group-header {
		background-color: #32373C !important;
		margin: 6px 0 0 0 !important;
		padding: 6px !important;
		font-size: 20px !important;
		font-family: "Open Sans",sans-serif !important;
		font-style: italic !important;
	}
	.ws-menu-page-group-header::after, .ws-menu-page-r-group-header::after {
		color: #ddd !important;
		font-style: normal !important;
	}
	
	.ws-menu-page-group, .ws-menu-page-r-group {
		background-color: rgba(255,255,255,.5) !important;
		font-family: "Open Sans",sans-serif !important;
		padding: 10px !important;
	}
	.ws-menu-page-r-group {
		width: 96% !important;
	}
	.open {
		margin-bottom: 0 !important;
	}

	h3, h4, label {
		font-style: italic !important;
		font-weight: bold !important;
	}
	h3, h4 {
		margin: 0 0 .5em 0 !important;
	}
	p {
		font-size: small !important;
	}
	.ws-menu-page dd, .ws-menu-page li {
		margin: .5em 0 !important;
		font-size: small !important;
	}
	.ws-menu-page-hilite {
		background-color: #eef !important;
	}
	strong {
		font-weight: bold !important;
		color: #555 !important;
	}
	code, kbd {
		margin: 0 !important;
		background-color: rgba(255, 255, 255, 0.5) !important;
		box-shadow: none !important;
		padding: 0 1px !important;
		font-family: Consolas,Monaco,monospace !important;
		color: inherit !important;
	}
	.highlight-php, .highlight-shortcodes, .code {
		padding: 0 5px !important;
		background-color: rgba(255, 255, 255, 0.75) !important;
		box-shadow: none !important;
		font-size: 1em !important;
	}
	h3 code, h4 code, label code {
		font-family: "Open Sans",sans-serif !important;
		color: #23282d !important;
		font-weight: bold !important;
	}
	.ws-menu-page-error-hilite {
		background-color: #e00 !important;
		color: #fff !important;
	}
	.ws-menu-page-error {
		color: #e00 !important;
	}
	.info {
		margin: 1em .5em !important;
		background-color: rgba(238,238,255,.9) !important;
		border: 0 !important;
	}
	.warning, .ws-menu-page-warning-hilite {
		margin: 1em .5em !important;
		background-color: #ffe !important;
		border: 0 !important;
		border-radius: 5px !important;
		color: inherit !important;
	}
	.form-table>tbody>tr>th {
		font-family: "Open Sans",sans-serif !important;
		font-style: italic !important;
		font-size: 1em !important;
		color: #23282d !important;
	}
	.form-table>tbody>tr>td {
		padding: .25em 0 !important;
		font-size: small !important;
	}
	select, textarea, input[type='text'], input[type='password'], .ws-menu-page-scrollbox {
		background-color: #fff !important;
		color: #444 !important;
		border: 1px solid #ddd !important;
		box-shadow: none !important;
	}
	textarea, input[type='text'] {
		font-family: Consolas,Monaco,monospace !important;
	}
	form[enctype='multipart/form-data'] textarea {
		width: 100% !important;
		margin-top: .5em !important;
	}
	form[enctype='multipart/form-data'] br {
		display: none !important;
	}

	select {
		padding: 0 !important;
	}
	.ws-menu-page-scrollbox {
		height: auto !important;
	}
	.ws-menu-page-hr, .ws-menu-page-hr {
		background-color: #bbb !important;
	}
	
	#ws-plugin--s2member-custom-reg-field-configuration-table, table.coupons-table {
		margin: .5em 0 !important;
		border-spacing: 1px !important;
	}
	#ws-plugin--s2member-custom-reg-field-configuration-table th, .coupons-table th {
		padding: 10px !important;
		background-color: #eee !important;
		color: #23282d !important;
	}
	tr.e {
		background-color: #fff !important;
	}
	#ws-plugin--s2member-custom-reg-field-configuration-table td {
		padding: 5px 10px !important;
	}
	.coupons-table th.-actions,
	.coupons-table thead::after {
		display: none !important;
	}

	input[type='submit'],
	input[type='button'],
	button,
	.toggler,
	#ws-plugin--s2member-custom-reg-field-configuration-tools a,
	.coupon-add a,
	a[onclick^="ws_plugin__s2member_generateSecurityKey"],
	.ws-menu-page-table-l>p a {
		display: inline-block !important;
		width: auto !important;
		margin: 1px !important;
		padding: 6px !important;
		background: #00a0d2 !important;
		box-shadow: inset 0 1px 0 rgba(120,200,230,.5),0 1px 0 rgba(0,0,0,.15) !important;
		border: 1px solid #0073aa !important;
		border-radius: 4px !important;
		color: #fff !important;
		font-family: inherit !important;
		font-size: 13px !important;
		line-height: 1em !important;
		font-weight: normal !important;
		font-style: normal !important;
		text-decoration: none !important;
		text-transform: capitalize !important;
		vertical-align: bottom !important;
	}
	::-webkit-file-upload-button {
		margin: 1px !important;
		padding: 6px !important;
		background: #00a0d2 !important;
		box-shadow: inset 0 1px 0 rgba(120,200,230,.5),0 1px 0 rgba(0,0,0,.15) !important;
		border: 1px solid #0073aa !important;
		border-radius: 4px !important;
		font-family: inherit !important;
		font-size: 13px !important;
		color: #fff !important;
	}
	.toggler:hover {
		background-color: #0095C2 !important;
	}
	#ws-plugin--s2member-custom-reg-field-configuration-tools a:hover, 
	.coupon-add a:hover,
	a[onclick^="ws_plugin__s2member_generateSecurityKey"]:hover,
	.ws-menu-page-table-l>p a:hover {
		color: #fff !important;
		text-decoration: none !important;
	}
	input[type='submit']:active,
	input[type='button']:active,
	button:active,
	.toggler:active,
	#ws-plugin--s2member-custom-reg-field-configuration-tools a:active,
	.coupon-add a:active,
	a[onclick^="ws_plugin__s2member_generateSecurityKey"]:active,
	.ws-menu-page-table-l>p a:active {
		background-color: #0073aa !important;
		transform: none !important;
		color: #fff !important;
		text-decoration: none !important;
	}
	::-webkit-file-upload-button:active {
		background-color: #0073aa !important;
	}
	.coupon-add i, .ws-menu-page-table-l>p i {
		display: none !important;
	}
	#ws-plugin--s2member-debugging-tips-details input {
		margin: .5em 0 .5em 1em !important;
		min-width: 210px !important;
	}

	form[enctype='multipart/form-data'] {
		position: relative !important;
	}
	input[type='file'] {
		position: relative !important;
		top: -1px !important;
		padding: 0px !important;
		border: 1px solid #ddd !important;
		box-shadow: none !important;
		background-color: #fff !important;
		vertical-align: bottom !important;
		font-family: inherit !important;
		font-style: italic !important;
		color: #666 !important;
	}
	::-webkit-file-upload-button {
		position: relative !important;
		top: -3px !important;
		left: -1px !important;
		padding: 3px 4px 4px 4px !important;
	}

	.toggler {
		width: .9em !important;
		margin: 0 !important;
	}
	.ws-menu-page-table-r {
		width: 15px !important;
	}
	.ws-menu-page-table-r.open {
		width: 215px !important;
	}
	.ws-menu-page-table-r>.wrapper {
		top: 5px !important;
		left: 1px !important;
		width: 200px !important;
		padding: 0 !important;
		background: none !important;
		box-shadow: none !important;
	}
	.ws-menu-page-table-r .ws-menu-page-r-group-header:first-of-type {
		margin-top: -4px !important;
	}
}
