css:
@-moz-document domain(football.fantasysports.yahoo.com) {
  
html {
    color:#DADADA
}

.Table th {
    color:#B0B0B0
}

a {
    color:#8EC0FF
}

.Nav-h:not(.Nav-main) > .Navitem.Selected > .Navtarget {
    color:#EEE
}

.Nav-main > .Selected > .Navtarget,.Nav-main > .Selected:hover > .Navtarget {
    color:#EEE
}

.Mod {
    background-color:#161719
}

#Stencil .Bdr,#Stencil .Bdrx,#Stencil .Bdry,#Stencil .Bdrtop,#Stencil .Bdrbot,#Stencil .Bdrstart,#Stencil .Bdrend {
    border-color:#232439
}

.Table td {
    border-color:#232439
}

.Table-interactive tr:hover > td {
    background-color:#161719!important
}

.Table-interactive tr:hover > td.Selected {
    background-color:#464852!important
}

.Table td.Selected {
    background-color:#464852!important
}

.Table th.Selected {
    background-color:#464852!important
}

.Thm-inherit .ysf-rosterswapper tbody .bench {
    background-color:#1C1D1F!important
}

.Nav-h:not(.Nav-main) {
    border-bottom-color:#232439!important;
    border-top-color:#232439!important
}

.Page-maincontent,.Page-footercontent,.Page-ft {
    background-color:#232429
}

.template-html5 .Subnav-main-wrap:before,.template-html5 .Subnav-main-wrap:after,.template-html5 .Page-bd:before,.template-html5 .Page-bd:after,.template-html5 .Page-ft:before,.template-html5 .Page-ft:after {
    background-color:#232429
}

.template-html5 .Subnav-main-wrap,.template-html5 .Page-bd,.template-html5 .Page-ft {
    background-color:#232429
}

.Subnav-main,#Stencil .Bg-shade2,#Stencil .Bg-shade {
    background-color:#2E2F30!important
}

.Table thead > tr:not(.First),.Table thead > tr.First.Last {
    background-color:#0F1011
}

.Btn:hover,.Btn-secondary:hover {
    background-color:#000
}

.Btn,.Btn-secondary {
    background-color:#9D9C9C;
    border-color:#9D9C9C;
    color:#FFF
}

/* Modal */
.yui3-ysplayernote .yui3-widget-ft,
.yui3-ysplayernote .playernoteview-notes,
.yui3-ysplayernote .teamsched-bd,
.yui3-ysplayernote .yui-dt-bd,
.yui3-ysplayernote .teamsched,
.yui3-ysplayernote .teamsched-bd h5,
.teamsched-bd .even,
.teamsched-bd .future,
.teamsched-bd .current,
.yui3-ysplayernote .teamsched-ft p,
.yui3-ysplayernote .playernotes-ft p,
tbody tr.past{
    background-color:#333
}

#yspfooter,
.yspsubcontent #yspmain,
.yspcontent #yspmain,
#bd {
    background:#333
}

#yspmessage .bd .thread-topic,#yspmessage .bd .thread-message,#ysparchive_message #yspmain .thread-topic,#ysparchive_message #yspmain .thread-message {
    border:1px solid #555;
    background-color:#444
}

#yspmessage .bd .thread-message,
#ysparchive_message #yspmain .thread-message,
.yspsubcontent #yspsub,
.yspcontent #yspsub{
    background-color:#333
}d

#Stencil .F-reset {
    color:#DADADA!important
}

p a,
td a,
#Stencil .F-link {
    color:#787878
}

#pr-wrapper-728x90 {
    visibility:hidden
}

.Desktop .ysf-rosterswapper:not(.swapping) tbody tr:hover,.Desktop .ysf-rosterswapper:not(.swapping) tbody tr:focus {
    background:rgba(230,237,248,0.1);
    outline:none
}

.Ta-c .Fw-b .Bdrstart {
    color:#fff
}

.Grid-table.Phone-px-med:hover,
.Table tr.Selected td,
.playernotes-bd {
    background-color:#333
}

div.Grid-u.Fz-xxs.Fw-b {
    color:#161719
}

#slogan-content.Mtop-sm {
    background-color:#333;
    font-weight:700;
    color: #FFF !important;
}

.yui3-ysplayernote #playernotes-pointsagainst,
.yui3-ysplayernote p.rating-value,
.noteitem,
.yui3-ysplayernote p.rating-comment {
    color:#D5D5D5!important;
}

#Stencil .F-shade.Ta-c.Bg-shade {
    background:#333;
    font-weight:700;
}

li.My-xl .Grid-h-mid {
    background:#333;
}

tr.headerRow0 th {
    color:#000;
}
.Btn, .Btn-secondary {
    background-color: #333;
    border-color: #FFF;
}
li.Navitem-main a.Wordwrap-bw {
    color: #FFF !important;
}
td.Tst-manager a.F-reset {
    color: #DADADA !important;
}
#Stencil a.F-reset {
    color: #DADADA !important;
}
#Stencil .Bg-overlay {
    background-color: #232429;
}
.df-ad.dfad-buttonhoverrow.dfad-topoffset,
.df-ad {
    display: none;
}
.Atomic .Bgc\(gray2\){
    background-color: rgb(62, 62, 62);
}
.Atomic .Bgc\(\#fff\), .Atomic .Bgc\(lightBg\), .Atomic .Bgc\(main-bg-color\) {
    background-color: rgb(62, 62, 62);
}
.Atomic .Bgc\(table-hdr-bg\) {
    background-color: rgb(62, 62, 62);
}
}
