css:
@-moz-document domain("app.simplenote.com") {
* {color: #fff !important; border-color: #555; font-family: monospace; border-bottom: none !important;}
html#app-page {background: #000;}
.app {background: #000;}
.popover .menu li a, .left-side-menu .sn-rocker-option, #fancybox-outer h3, #already_published_output, #publish_success_output, td, small, label, td span, .history h4, .history p, .history button, #fancybox-content p, .tags h4, li.as-result-item, li.as-result-item em, li.as-selection-item.selected{color: #000 !important;}
.note #static_content, .note #txtarea, .note #historytxt, textarea{font-size: 95% !important; font-family: monospace;}
a, li.as-result-item.active em, li.as-result-item.active {color: blue !important;}
.notes li.selected{background: #111 !important;}
.notes li:hover {background: #448ac9 !important;}
.notes li:active {background: #008ac9 !important;}
a:link, a:visited, a{color: #448ac9 !important;}
a.active {color: #fff !important;}
.notes li{ text-shadow: none !important;}
.toolbar .tools .tbutton:hover, .toolbar .tools .tbutton:focus{box-shadow: none;}
::-webkit-scrollbar
{
width: 3px;
height: 3px;
}
::-webkit-scrollbar-track-piece
{
background-color: #666;
-webkit-border-radius: 3px;
}
::-webkit-scrollbar-thumb:vertical
{
height: 5px;
background-color: #fff;
-webkit-border-radius: 3px;
}
::-webkit-scrollbar-thumb:horizontal
{
width: 5px;
background-color: #fff;
-webkit-border-radius: 3px;
}
.popover .menu.history .versions-right .version-restore {border: none;}
ul.as-selections li.as-selection-item:hover {color: #000 !important;}
::selection {
background: #718FAB;
}
::-moz-selection {
background: #718FAB;
}
#versions{background: #666;}
#versions .ui-slider-handle{background: #448ac9 linear-gradient(#448ac9, #004685);}
.footer-right:before{content: " Â© Sentirel.org theme";}
}
