css:
@-moz-document domain("youtube.com") {

#body-container{
	background-color: #333333;
}

#yt-masthead-container, #appbar-guide-menu {
	background-color:rgba(40,40,40, 0.9);
    border-bottom: none;
}

#masthead-appbar {
	background-color:rgba(50,50,50, 0.9);
    border-bottom: none; 
}

#masthead-appbar h2, #masthead-appbar span{
     color: #fff !important;
}

#appbar-guide-menu {
    border:none;
}

#appbar-guide-menu span{
    color: #fff;
}

.guide-item.guide-item-selected, .guide-item:hover{
	border-radius:10px;   
}

.yt-uix-button-menu{
	color: #333 !important;   
}

#yt-masthead #logo-container .logo{
	background-position: -165px -203px;
    background-color: #aaa;
    padding: 2px;
    border-radius: 10px;
    -webkit-filter: grayscale(100%);
	filter: grayscale(100%);
}

.content-region {
	display:none;   
}

.yt-card{
	border-radius: 3px;
	box-shadow: 0 0 29px 0px;
	border: 1px solid #eee;
	background-color:#eee;
}

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
 border-right:none;   
}

.yt-uix-button-icon-bell {
    filter: invert(1) !important;
    -webkit-filter: invert(1);
}

.sb-card-border {
	box-shadow:none;
    border:none;
    border-radius:6px;
}

.yt-ui-ellipsis {
	background-color: transparent;
}

.guide-section {
	color: #fff;
}
  
}
