@-moz-document domain("detik.com") {
/* by Fauzi mas.uziku@gmail.com */

body,
html,
.wrapper_atas {
    background: #322F2F none;
    color: #b7b4b4;
}
.boxlr,
.box {
    background-color: #1D1919;
}
.list_fokus a,
.berita_terkait .list_berita li a {
    color: #928B8B;
}
.detail_content,
.menu2 a.new span {
    background: #3F3B3B;
}
.detail_text {
    color: #A4A4A4;
}
#framebar_new .new_search,
#framebar_new .ico_detik,
.boxlr .title_lr {
    background: #2C2929 none repeat scroll 0%;
    border-right: none;
}
#framebar_new,
.list_fokus li,
.box_hl_new,
.title_feed,
.list_feed li,
.title_feed {
    border-bottom: none;
    background: #2C2929!important;
}
.menu {
    background-color: #424347;
    border-bottom: none;
    position: inherit;
}
.menu2 a {
    color: #fff;
}
.menu ul li {
    border-left: none;
}
.list_news1 li {
    border-bottom: none;
}
.list_news1 a {
    color: #7A7373;
}
.banner_reg_lr,
.box_hl_new .box_img {
    border: none;
    background: none;
}
.num_fokus2,
.sf-menu li,
#framebar_new .login_area .masuk {
    background: #47494B none repeat scroll 0% 0%;
}
.header {
    padding: 0;
    height: 0;
}
.header_top,
.title_bu,
.box_hl_new .label,
.tag_pm,
.list_dk .dk_action .count,
#detikcontent {
    background-color: #656060;
    margin-top: 0;
}
.box_popcom .title_lr a.selected span {
    border-color: #6C6156;
}
.box_popcom .title_lr a:hover span {
    border-color: #6C6156;
}
.box_popcom .title_lr a span,
.t_lfl {
    border-color: #9D9EA2;
}
.boxlr .title_lr span {
    border-bottom: 4px solid #9D9EA2;
}
.desc_nhl h2,
.num_fokus {
    color: #848175;
}
.box_hl_new .hl_kait .title_lh {
    color: #939189;
}
.footer,
.sortby a:hover,
.sortby a.selected,
.foto_story {
    background-color: inherit;
}
.list_dk .dk_komen,
.list_dk .dk_action a,
.list_dk .dk_action .dk_share span {
    color: #A8A2A2;
}
.list_dk .list_dk_text .dk_name {
    color: #AEB4BF;
}
.box_popcom .title_lr a.selected span,
.box_popcom .title_lr a:hover span {
    border-color: #78726C;
}
.desc_idx h2,
.desc_idx {
    color: #B1ADAD;
}
.jdl h1 {
    color: #B2B5BD;
}
.menu ul li {
    border-left: 1px solid #2C2C2D;
    border-right: 1px solid #4B4C50;
}
.footer {
    background-color: #27282D;
}
.list_feed li {
    border-top: solid 1px #77787B;
}
.m_content {
    background-color: #292929;
}
.pic_artikel_sisip_table .pic,
.main .block__list__item__content h5 a {
    color: rgb(255, 186, 51)
}
.main .content .list__news__content p,
.main .content .list__news__sub,
.footer__bottom,
.footer__bottom a,
.main .populer .counter dt h4 a,
.main .block__item__content h5 a {
    color: #fff
}
.list_feed_detail .desc_nhl h2 {
    color: #8c8989;
}
.list-berita article .box_text h2 {
    color: #c3b7b7;
}
.list-berita article .box_text p {
    color: #e8e8e8;
}
.beritautama .title_bu {
    line-height: 140%;
}
}
