css:
@-moz-document domain("www.imdb.com") {
#styleguide-v2, #styleguide-v2 #wrapper {background:#232323 !important;}
a:link {color: #A7A1B9 !important;}
#root, #root td {color:#ffffff !important;}
.text-muted{color: #ffffff !important;}
#content-2-wide #sidebar {background-color: #232323 !important;}
.recently-viewed {background-color: #232323 !important;}
.pagecontent { background: #545454 !important;}
#rvi-div, .footer-grid-wrapper {border-top: 1px solid #545454 !important;}
#content-2-wide #main .article {border-bottom: 1px solid #545454 !important; background:#232323 !important;}
#sidebar h3 {color: #FFF !important;}
h1, h2, h3 {color: #A9A9A9 !important;}
.rec_details .rec-jaw-upper>div {background: #232323 !important; border: 1px solid #545454 !important;}
.homepage .rec_details .rec-jaw-lower>div.rec-jaw-teeth { background:#232323 !important;}
.homepage .rec_details .rec-jaw-upper, .homepage .rec_details .rec-jaw-lower {background: #232323 !important;}
#footer {background-color: #232323 !important;}
.article.title-overview, .article.name-overview {border: solid 1px #545454 !important; background: #232323 !important;}
.aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4 {
    background-color: #232323 !important;
    background: #232323 !important; border: 1px solid #545454 !important;}
.btn2.primary {background: #484848 !important;border: 1px solid #545454 !important;}
.btn2 { color:#CACACA !important;}
#overview-bottom .add_to_checkins, #overview-bottom .add_to_watchlist, #overview-bottom .wlb_classic_wrapper, #overview-bottom .title-trailer, #overview-bottom .launch-share-popover {background: #484848 !important;border: 1px solid #545454 !important;}
.redesign .article.highlighted {
    background: #8E8E8E !important; border: 6px double #505050 !important;}
#navbar input {background:#404040 !important;color:#ffffff !important;}
#navbar #nb_search #navbar-suggestionsearch a.highlighted {background-color: #2F2F2F !important;}
#navbar #nb_search #navbar-suggestionsearch {background-color: #2F2F2F !important;}
#navbar #nb_search #navbar-suggestionsearch a .title {color:#FFFFFF !important;}
#navbar #nb_search #navbar-suggestionsearch a .detail {color: #A0A0A0 !important;}
#navbar #nb_search #navbar-suggestionsearch a {border-bottom: 1px solid #545454 !important;}
#navbar #nb_search #navbar-suggestionsearch>a.moreResults {background-color: #5D5D5D !important;}
#navbar select {color: #FFFFFF !important; background: #272727 !important; }
ul.main_nav a {color: #908F8F !important;}
.article {border: 1px solid #545454 !important; background:#232323 !important;}
.rec_details .rec-jaw-lower>div.rec-jaw-teeth {background:#232323 !important;}
.rec_details .rec-actor {background:#232323 !important;}
#root, #root td {background:#232323 !important;border: 1px solid #545454 !important;}
.odd {background:#232323 !important;border: 1px solid #545454 !important;}
.even {background:#585858 !important;border: 1px solid #545454 !important;}
h1, .article h1.header span.itemprop {color:#ffffff !important;}
#navbar {background:#232323 !important;}
#nb20 { background:#232323 !important;}
div.sub_nav {background-color: #232323 !important;}
#latestHeadlines {background-color: #232323 !important;}
}
