css:
/* by Fauzi mas.uziku@gmail.com */

@-moz-document domain("www.tokopedia.com") {
body,
.all_cat_container,
.footer-main {
    background-color: #393636;
    color: #fff;
}
a {
    color: #A4D2A4;
}
.btn,
.product-report,
.product-notif,
.product-box-header h4,
.product-box-header,
.btn-buy {
    border-radius: 0px;
    background-color: #3E3939;
    background-image: none;
    border: 1px solid #514E4E;
}
.btn a:hover {
    background-color: #3E3939;
}
.btn-buya:hover {
    background-color: #3E3939;
}
.left-nav {
    background-color: none;
}
.shop-notes .left-nav > li > a {
    color: #D1CDCD;
}
.shop-notes .left-nav > li > a:hover {
    background-color: #444141;
}
.shop-notes .left-nav > li > a {
    border-width: 0px 0px 0px;
}
.product-ratingstat li {
    border-bottom: none;
}
.navbar-inner {
    padding-left: 20px;
    background: #110F0F none repeat scroll;
    border-bottom: 1px solid #656865;
}
.topbar-nav .border-both {
    border-left: 1px solid #5C5757;
    border-right: 1px solid #5C5757;
}
.topbar-nav .border-left {
    border-left: 1px solid #5C5757;
}
.dropdown-menu > li > a {
    color: #DAD7D7;
}
.dropdown-menu {
    background: #110F0F none repeat scroll
}
.dropdown-category-thumb .title {
    color: #C9C5C5;
}
.alert-info {
    background-color: #222223;
    border-color: #868D8F;
}
.btn-cp {
    background: #4C6C44 none repeat scroll 0px 0px;
}
.allcat a {
    border-width: 1px 1px 1px 1px;
    border-color: #628164 #628164 #628164 #628164;
    background-color: #393636;
}
.allcat a:hover {
    color: #868D8F;
    background-color: #222223;
}
.allcat a > i {
    color: #6D746D;
}
.allcat a .title {
    color: #FFF;
}
.all_cat_container,
.footer-main {
    border-top: none;
    border-bottom: none;
}
.btn-action {
    background-color: #5B5D5B;
    color: #FFF;
    background-image: -moz-linear-gradient(center top, #5F5F5F 0%, #727571 100%);
}
.btn-action:hover,
.btn-action:focus,
.btn-action:active,
.btn-action.active,
.btn-action.disabled,
.btn-action[disabled] {
    background-color: #5F6C5F;
    color: #FFF;
    background-image: -moz-linear-gradient(center top, #5F745F 0%, #5F745F 100%);
}
.all-hotlist {
    background: #575252 none repeat scroll 0% 0%;
}
.btn-group > .btn-switch:first-child {
    border-radius: 0px;
}
.btn-group > .btn-switch:last-child {
    border-radius: 0px;
}
.btn-switch.active,
.btn-switch.disabled,
.btn-switch[disabled] {
    background-color: #606860;
}
.box-white {
    background-color: #363434 !important;
}
.box {
    border: 1px solid #514E4E;
}
#content-container > .container-fluid {
    border: 1px solid #514E4E;
    box-shadow: none;
}
.box-shadow {
    box-shadow: none;
}
hr {
    border-color: #514E4E;
}
.left-nav-admin {
    background-color: #3C3B3B;
    border-radius: 0px;
}
#content-container > .container-fluid {
    background: #3C3B3B none repeat scroll 0% 0%;
    border: 1px solid #514E4E;
}
.left-nav-admin .head span {
    background: #424040 none repeat scroll 0% 0%;
}
.top-admin {
    border-width: 1px 1px 1px;
    border-style: solid;
    border-color: #E5E5E5;
    background: #393636;
}
.accordion-heading {
    border-bottom: none !important;
}
.left-nav-admin ul > li > a:hover {
    background-color: #9F9797;
}
a:hover,
a:focus {
    color: #000;
}
.left-nav-admin ul > li > a {
    border-bottom: none;
}
.left-nav-admin ul > li:last-child > a {
    border-bottom: none;
}
#paging-div {
    border-top: none;
    border-bottom: 1px solid #514E4E;
}
.pagination ul > li > a:hover {
    background-color: #fff;
}
.pagination ul > li > a:focus,
.pagination ul > .active > a,
.pagination ul > .active > span {
    background-color: #292626;
}
.pagination ul > .active > a,
.pagination ul > .active > span {
    color: #fff;
}
.pagination ul > li > a,
.pagination ul > li > span {
    border-style: solid;
    border-color: #514E4E;
}
.horizontal-tab > li a {
    background-color: #514B4B;
    color: #fff;
}
.horizontal-tab > .active > a,
.horizontal-tab > .active > a:hover,
.horizontal-tab > .active > a:focus {
    background-color: #262222;
}
.horizontal-tab > li > a:hover,
.horizontal-tab > li > a:focus {
    border-color: #514E4E;
    background-color: #625D5D;
    color: #fff;
}
#fav-prod-grid .product .meta-misc,
.promo-al-grid .product .meta-misc {
    background: #262222 none repeat scroll 0% 0%;
}
.promo-al-grid .title-promo-al {
    padding-top: 8px;
    background: #4A4444 none repeat scroll;
}
.btn-buy {
    background-color: #7A7371;
    background-image: -moz-linear-gradient(center top, #7A7371 0%, #7A7371 100%);
    border: 1px solid #514E4E;
}
.sf-menu li {
    border: 1px solid #514E4E;
}
.breadcrumb > li {
    text-shadow: none;
}
.breadcrumb > li a:hover {
    color: #fff;
}
.breadcrumb li h1,
.breadcrumb li h2,
.breadcrumb li h3 {
    color: #fff;
}
.list-box-text {
    color: #F6EBEB;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #D1D1D1;
}
.product-reviewsummary {
    background: #333030 none repeat scroll 0% 0%;
    border: 1px solid #514E4E;
}
.list-box-replycomment {
    background: #3E3939 none repeat scroll 0% 0%;
    border-top: 1px solid #514E4E;
}
.list-box li .list-box-content,
#pin-grid .list-box-content,
.product-imagebig,
.horizontal-tab {
    border-width: 1px 1px 1px;
    border-style: solid;
    border-color: #514E4E;
}
.horizontal-tab > li > a {
    border-top: 1px solid #514E4E;
    border-left: 1px solid #514E4E;
    border-right: 1px solid #514E4E;
}
.horizontal-tab > .active > a,
.horizontal-tab > .active > a:hover,
.horizontal-tab > .active > a:focus {
    border-left: 1px solid #514E4E;
    border-right: 1px solid #514E4E;
    border-top: 2px solid #171817;
}
.detail-info {
    border: 1px solid #514E4E;
}
.detail-info dt,
.detail-info dd {
    border-bottom: 1px solid #514E4E;
}
.product-box {
    border: 1px solid #514E4E;
    border-radius: 0px;
}
.selectBox-dropdown {
    border-radius: 0px;
    background-color: rgb(57, 51, 51);
    border: 1px solid #514E4E;
}
.reviewsummary-table1 {
    border: 1px solid #514E4E;
    background: none;
}
.product-reviewsummary .table tr td {
    border-right: 1px solid #514E4E;
}
.pagination ul > li:last-child > a,
.pagination ul > li:last-child > span {
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
}
.grid-home-productfav .meta-product,
.grid-home-product .meta-product,
.grid-shop-product .meta-product {
    background: none;
    border-color: -moz-use-text-color #514E4E;
}
.grid-shop-product .product,
.grid-home-product .product,
.home-only .grid-shop-product .product,
.home-only .grid-home-product .product {
    border-bottom: 1px solid #514E4E;
    border-radius: 0px;
}
.left-nav {
    background-color: #3E3939;
}
.dropdown-category-thumb .all-cat {
    border-top: 1px dashed #514E4E;
    background-color: #3E3939;
}
.jqmWindow .jqm-inner {
    background: none repeat scroll 0 0 #4E4747;
}
.well {
    background-color: #353131;
}
.container-product .first-talk {
    background: #383232;
}
}
