css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bashscripts.org") {
#acp, #forum, p, td, th {

	font-family: Verdana, Arial, Helvetica, sans-serif;

	font-size: 16px !important;

	color: #000000 !important;

}
.gen { font-size: 16px !important; }

.genmed { font-size: 16px !important; }

.gensmall { font-size: 16px !important; }



.error-message {

	font-size: 16px !important;

	color: #FF1010;

	font-weight: bold;

}
.forum-buttons {

	font-size: 16px !important;

	color: #FF1010;

}
.forumline th {

	color: #455995;

	font-size: 16px !important;

}

.forumline .row-header span {

	color: #EDEFF4;

	font-size: 16px !important;

}
.row-center-small {

	font-size: 16px !important;

}
.nav-header {

    	color: #888888;

	font-size: 16px !important;
}

nav-links {

	color: #888888;

	font-size: 16px !important;

}

.forumlink, .forumlink-new {

	font-size: 16px !important;

}
.moderators {

	font-size: 16px !important;

	color: #888;

}
.topiclink b, .topiclink-new b { 

	color: #444;

	font-size: 16px !important;

}



.topiclink a, .topiclink-new a {

	font-size: 16px !important;

}

pagination { 

	font-size: 16px !important;

}

.gotopage { 

	font-size: 16px !important;

}
post-name {

	font-weight: bold;

	color: #455995;

	font-size: 16px !important;

}
.post-rank {

	color: #888;

	font-size: 16px !important;

}



.post-details {

	color: #666;

	font-size: 16px !important;

}
.post-subject {

	color: #000000 !important;

        font-size: 16px !important;

}

.post-text {

	font-size: 16px !important;

}
.post-text blockquote {

	color: #000000 !important;

	font-size: 16px !important;

}
post-text blockquote h6 {

	font-size: 16px !important;

	color: #000000 !important;

}

post-text .code {

	color: #000000 !important;

	font-size: 16px !important;

}

.post-text .code h6 {

	font-size: 16px !important;

	color: #000000 !important;

}
popuppm-new a { 

	font-size: 16px !important; 

	color: #000000 !important;
}

}
