css:
@-moz-document domain(roblox.com),url(http://www.roblox.com/),url-prefix(https://forum.roblox.com/) {
.ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard{display:none !important}
/*#MasterContainer, .nav-content-inner, .container-main, .gutter-ads {
        /*background: url(http://i.imgur.com/2zfLaSK.png) top center repeat-x #DDDDDB;
    }*/
#GamesPageLeftColumn { width: auto; margin-right: 10px !important; margin-left: 10px !important; }
#GamesListsContainer > div {
	/*background-color: transparent !important;*/
	width: 100% !important;
    }
#GamesPageRightColumnSidebar.sidebar-no-ad.games-page-right-sidebar {
    display: none
	}
body .forum-table-header, body .forum-table-header th, body .forum-table-footer {
        background: #9e1f1f;
    }
.rbx-header {
    background: #cb1010;
	}
.icon-default-logo-r, .icon-logo-r {
    background-image: url(http://vignette2.wikia.nocookie.net/rblx-roblox-talk/images/f/f5/Logo_R.png);
}
.icon-default-logo, .icon-logo {
    background-image: url(http://vignette3.wikia.nocookie.net/rblx-roblox-talk/images/b/bb/Roblox_logo.png);
}
.btn-secondary-md {
    background-color: #f32b2b;
    border-color: #f32b2b;

}
.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
	background: #e64141 !important;
    border-color: #e64141;
    }
.notification-red {
	background-color: #00A2FF; 
}
.rbx-scrollbar li .notification-blue {
	background-color: #00A2FF; 
}
.chat-main .chat-header{
    background: #cb1010;
}

.dialog-container .dialog-header{
    background: #cb1010;
}
.container-header .btn-more:not(.btn-control-xs), .container-header .btn-more:hover:not(.btn-control-xs), .container-header .btn-more:active:not(.btn-control-xs) {
    background: #f32b2b;
    border-color: #f32b2b;
}
.games-page-left .see-all-button {
    background: #f32b2b;
    border-color: #f32b2b;
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    box-shadow: 0 -4px 0 0 #f32b2b inset;
}
.rbx-tabs-horizontal .rbx-tab-heading:hover, .rbx-tabs-horizontal .rbx-tab-heading:focus, .rbx-tabs-horizontal .rbx-tab-heading:active {
    box-shadow: 0 -4px 0 0 #f32b2b inset;
}
.btn-secondary-xs {
    background: #f32b2b;
    border-color: #f32b2b;
}
.btn-secondary-xs:hover, .btn-secondary-xs:focus {
	background: #f32b2b;
    border-color: #f32b2b;
}
.slide-switcher.groups {
    background: #cb1010;
}
.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0 0 0  #f32b2b inset;
}
}
