@-moz-document url-prefix("http://www.aliexpress.com/") {
/* Banner */

.top-banner-container a { height: 0 !important;}

/* Header */

.top-banner-container { background: #292929 !important;}
.top-lighthouse { border-bottom: 1px solid #656565 !important;
    background-color: #3A3A3A !important;}

.header {background-color: #292929 !important;
    border-bottom: 1px solid #656565 !important;}
.searchbar-form .search-key-box input {background: #3A3A3A !important; color:#F7F7F7 !important;}
.searchbar-form .search-key-box {background: #3A3A3A !important; border: 1px solid #656565 !important;}
.ui-autocomplete-his-header { background: #3A3A3A !important;}
.ui-autocomplete {border: 1px solid #656565 !important;}
.ui-autocomplete .hot-word {    background: #3A3A3A !important;}
.ui-autocomplete {background: #3A3A3A !important;}
hr {border: 1px solid #656565 !important;}
a {color: #fff !important;}
.ui-autocomplete li.his .suggest_key {  color: #fff !important;}  

.searchbar-form .search-category {    border-top: 1px solid #656565 !important;
    border-bottom: 1px solid #656565 !important;}
.searchbar-form .search-cate {    background: #3A3A3A !important; color:#fff !important;}
.user-account-unfold .user-account-info { background-color: #292929 !important;border: 1px solid #656565 !important;border-color: #656565 !important;}
a, abbr, acronym, address, article, aside, blockquote, body, code, dd, del, details, dfn, dialog, div, dl, dt, em, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, iframe, img, label, legend, li, menu, nav, object, ol, p, pre, q, section, span, summary, ul {    color: #C1C1C1 !important;}
.user-account-unfold .user-account-main { background:#292929 !important;}
.user-account-main { border: 1px solid #656565 !important;}
.flyout-user-signIn {border-bottom: 1px solid #656565 !important;}
.flyout-quick-entry li:first-child a {
    background-color: #676767 !important;}
.flyout-quick-entry li a {background: #676767 !important;}

/* Categories */

.top-container-bg {background-color: #3A3A3A !important;  border-bottom: 1px solid #656565 !important}
.categories-main .categories-content-title {
    background-color: #292929 !important;border-bottom: 1px solid #656565 !important}
.categories-main {border: 1px solid #656565 !important;}
.index-page {
    background-color: #292929!important;}
.cl-item .cate-name span {background-color: #3A3A3A !important;border: 1px solid #656565 !important;}
.sub-cate-main {background: #3A3A3A !important;}
.sub-cate-items dt {border-bottom: 1px solid #656565 !important;}
.bottom-show-list li a {border: 1px solid #656565 !important;}

/* Panels */

.collections-panel .title {background: #3A3A3A !important;border-bottom: 1px solid #656565 !important;}
.today-deal-panel .title {background: #3A3A3A !important;border-bottom: 1px solid #656565 !important;}
.today-deal-panel .container-main {background: #3A3A3A !important;}

.floor-fixed-panel {background-color: #292929 !important;border: 1px solid #656565 !important;}

.industry-floor .container-inner {
    background-color: #3A3A3A !important;
    border-top: 2px solid #656565 !important;}
.indutry-info .industry-enter a {border-bottom: 1px solid #656565 !important;}
.rec-industry-list li a {border: 1px solid #656565 !important;}
.rec-industry-right-list li a {border-bottom: 1px solid #656565 !important;}
.top-industry-product a {border: 1px solid #656565 !important;}

.brand-box .brand-slidebox-next a, .brand-box .brand-slidebox-prev a { background-color: #151515 !important;border-bottom: 1px solid #656565 !important;border-top: 1px solid #656565 !important;}

.related-recommend .change-product-btn a {background-color: #404040 !important;}

/* Recommended */
.related-recommend .item-inner {    background-color: #3A3A3A !important;}

/* Footer */

.site-server-box {border-bottom: 1px solid #656565 !important; background-color: #292929 !important;}
.site-item:first-child {border-left: 1px solid #656565 !important;}
.site-item { border-right: 1px solid #656565 !important;border-top: 1px solid #656565 !important;}
.user-helper-footer {background-color: #292929 !important;
background-color: #292929 !important;}
.alert-subscribe-form .alert-subscribe-input {background-color: #3A3A3A !important;border: 1px solid #656565 !important;}
.partner-box {background-color: #3A3A3A !important;border: 1px solid #656565 !important;}
.site-footer {background-color: #292929 !important;}
#shopping-history .show-history {background-color: #676767 !important;}
/* Buttons */

.nav-global .ng-item { border-right: 1px solid #656565 !important; }
.nav-global .ng-item a {color: #C1C1C1 !important;}
.ng-switcher .switcher-sub .switcher-common {border-bottom: 1px solid #656565 !important;
    background: #3A3A3A !important;}
.ng-switcher .switcher-sub {border: 1px solid #656565 !important;}
.ng-switcher .switcher-title {color: #FFF;
    border-bottom: 1px solid #656565 !important;}
.ng-switcher .label {color: #FFF !important;}
.ng-switcher .country-selector .link-fake-selector {background: #292929 !important;
    border: 1px solid #656565 !important;}
.ng-switcher .country-selector .link-fake-selector .country-text {color: #fff !important; }
.ng-switcher .country-selector .list-container {background-color: #292929 !important;
    border-color: #656565 !important;}
.ng-switcher .country-selector .list-container a { border: 0 !important; border-bottom: 1px #656565 solid !important;}
.ng-switcher .switcher-currency-c span {    border: 1px solid #656565 !important;}
.nav-global .ng-sub-title, .nav-global .ng-switcher .switcher-info {background: #3A3A3A !important;}
.ng-switcher.active .switcher-info {border: 1px solid #656565 !important;}
.nav-global .ng-switcher-language .switcher-sub {background: #3A3A3A !important;}
.ng-switcher .google-trans-btn {border: 1px solid #656565 !important;}
.goog-te-gadget-simple .goog-te-menu-value span {color:#E2E2E2 !important;}
.goog-te-menu2 {background-color:#3A3A3A !important;border: 1px solid #656565 !important;}
.goog-te-menu2-item div, .goog-te-menu2-item:link div, .goog-te-menu2-item:visited div, .goog-te-menu2-item:active div {
    color: #DEDEDE !important;
    background: #3A3A3A !important;}


/* SEARCH PAGE */
.gallery-mode .list-item:hover .item {    background: #3A3A3A !important; 
    border: 1px solid #656565 !important;}

body {    background: #292929 !important;}
.narrow-down-area {background-color: #3A3A3A !important;border-top: 1px solid #656565 !important;border-left: 1px solid #656565 !important;border-right: 1px solid #656565 !important;}
#filter {border-bottom: 1px solid #656565 !important;}
#filter-options {border-bottom: 1px solid #656565 !important;}
.view-filter {background-color: #3A3A3A !important;border-bottom: 1px solid #656565 !important;border-left: 1px solid #656565 !important;border-right: 1px solid #656565 !important;}
.ui-textfield-system {    color: #F7F7F7 !important;
    background-color: #3A3A3A !important;
    border: 1px solid #656565 !important;}
.shipping-to-wrap .country-selector .link-fake-selector .list-title {background: #1F1F1F !important;}
.shipping-to-wrap .link-fake-selector {border: 1px solid #656565 !important;}
.country-selector .list-container {    background-color: #1F1F1F !important;
    border-color: #656565 !important;}
.country-selector .list-container a {border: 0px solid #FFf !important; border-bottom: 1px #656565 !important;}
#filter-options .filter-item {    background-color: #292929 !important;     border: 2px solid #656565 !important;}
#show-piece-price span {background: #292929 !important;}
#show-piece-price {    background-color: #292929 !important;
    border: 1px solid #656565 !important;}
#list-currency-c a {background: #292929 !important;}
#list-currency-c span {background: #292929 !important;  border: 2px solid #656565 !important;}
#list-currency-c li {background: #292929 !important;}

.p4pExpress ul.p4p-bottom-content {    background: #3A3A3A !important;}
.p4pExpress .relatied-product-slide {border: 1px solid #656565 !important;}
.bp-list-banner {border: 1px solid #656565 !important;background: #3A3A3A !important;}
.list-item {border: 1px solid #656565 !important;}
#refine-category-list {
    border-right: 1px solid #292929 !important; 
    border-bottom: 1px solid #656565 !important; }
#refine-category-list .category-list {border-bottom: 1px solid #656565 !important; }
.cate-dot-line {border-bottom: 1px solid #656565 !important; }
.ui-breadcrumb .active {color: #FFFFFF!important;}
.free-s {color: #FFFFFF!important;}
#pop-search {border-bottom: 1px solid #656565 !important; }
.col-extra-p4p ul {background-color: #292929 !important;}
.col-extra-p4p .p4p-side h4 {background-color: #292929 !important;border-bottom: 1px solid #656565 !important; }
.col-extra-p4p .p4p-side {border: 1px solid #656565 !important; }
.col-extra-p4p .p4p-side ul li {border-bottom: 1px solid #656565 !important; }

/* PRODUCT */
.product-info-operation {background: #3A3A3A !important;}
.product-info {border: 1px solid #656565 !important; }

#img .img-zoom-in {background: #3A3A3A !important;border: 1px solid #656565 !important;}
.ui-button-normal, .ui-button-normal:link, .ui-button-normal:visited, .ui-button-normal:active {background: #3A3A3A !important;border: 1px solid #656565 !important;}
.bp-horizontal-banner {background-color: #3A3A3A !important;}
.seller-discount-show {background: #3A3A3A !important;}
.seller-discount-show-hd a.hd-shop-now {background: #3A3A3A !important;}
.seller-discount-show-bd {border-left: 1px solid #656565 !important; }
.ui-tab-primary .ui-tab-nav li.ui-tab-active a, .ui-tab-primary .ui-tab-nav li.ui-tab-active a:hover {background-color: #3A3A3A !important;border-color: #656565 !important;}
.ui-tab-primary .ui-tab-nav li a {background-color: #3A3A3A !important;border: 1px solid #656565 !important;}
.product-desc .ui-box-title {background-color: #3A3A3A !important;}
#transaction-history th {background: #3A3A3A !important;border-bottom: 1px solid #656565 !important;}
#transaction-history td {border-bottom: 1px solid #656565 !important;}
.ui-pagination-body {background: #292929 !important;}
.ui-pagination-navi span.ui-pagination-disabled.ui-pagination-prev, .ui-pagination-navi span.ui-pagination-disabled.ui-pagination-prev:hover {border: 1px solid #656565 !important;}
.ui-slidebox-prev, .ui-slidebox-next {background: #292929 !important;}
#rpbk-from-thisseller {border-bottom: 1px solid #656565 !important;}
.ui-slidebox-horizontal .ui-slidebox-item {border: 1px solid #656565 !important;}
.ui-slidebox-horizontal li {border-left: 1px solid #656565 !important;}
.ui-button.ui-button-primary, .ui-button.ui-button-normal {color:#ffffff !important;}
#personalized-recommendation-p4p h4 {border-top: 1px solid #656565 !important;}
.seller-info {border: 1px solid #656565 !important;}
.seller-info .seller {border-bottom: 1px solid #656565 !important;}
.shipping-method th {background: #3A3A3A !important;}
.shipping-method th, .shipping-method td {border: 1px solid #656565 !important;}
.shipping-payment .ui-box-title {background: #3A3A3A !important;}
.rating-table thead th  {background: #3A3A3A !important;}
.rating-table .product-name {color:#ffffff !important;}
.seller-detail-show td {border: 1px solid #656565 !important;}
.buy-protection .bp ul {border: 1px solid #656565 !important;}
.ui-notice-normal .ui-notice-alert, .ui-notice-normal .ui-notice-help, .ui-notice-normal .ui-notice-prompt, p.ui-notice.ui-notice-normal {background: #3A3A3A !important;}
.ui-notice .ui-notice-help, p.ui-notice.ui-notice-normal.ui-notice-help {border: 2px solid #656565 !important;}
.box .bd {background-color: #292929 !important; }
.box .hd h3 {background-color: #292929 !important; }
.m-sop-crumb {background-color: #292929 !important; }
.store-header {background: #3A3A3A !important;border-bottom: 1px solid #656565 !important;}
.m-sop-crumb {background: #3A3A3A !important;}
.ui-box .ui-box-body {border: 1px solid #656565 !important;}
.ui-box-wrap {background: #3A3A3A !important;}
.m-sop-contact-list .ui-box-wrap .ui-box-title {background: #292929 !important;border: 1px solid #656565 !important;}
.m-sop-contact-list .contact-item a.atm16grey {color: #B9B9B9 !important; }
.m-sop-shop-cate .ui-box-wrap .ui-box-title {background: #292929 !important;border: 1px solid #656565 !important;}
.m-sop-shop-cate .groups-list {background: #3A3A3A !important;}
.m-sop-shop-cate .ui-box-wrap .ui-box-body {background: #3A3A3A !important;}
.m-sop-shop-cate .groups-list li.group-item a {background: #3A3A3A !important;}
.m-sop-shop-cate .groups-list .expand {background: #3A3A3A !important;}
.m-sop-top-selling .ui-box-wrap .ui-box-title, .m-o-small-all-detail .ui-box-wrap .ui-box-title {background: #292929 !important;border: 1px solid #656565 !important;}
.store-header .store-summary {background: #3A3A3A !important;border-right: 1px solid #656565 !important;}
.store-header .store-info-header {background: #3A3A3A !important;border-right: 1px solid #656565 !important;}
.store-header .store-operate-box {border-top: 1px solid #656565 !important;}
.store-header .shop-time {background: #292929 !important;border: 1px solid #656565 !important;}
.store-header .store-follow-btn {border: 1px solid #656565 !important;}
}
