@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://([^:]+)(:[0-9]+)?/transmission/web/.*") {
    * {
        background-color: transparent !important;
        color: inherit !important;
        border-color: var(--border-color) !important;
    }
      
    body, div.dialog_container div.dialog_window, .inspector-tab, div#jqContextMenu ul, .ui-widget-content {
        background-color: var(--background-color) !important;
        color: var(--foreground-color) !important;
    }
      
    div#toolbar, #statusbar, div.torrent_footer, .ui-widget-header {
        background-color: var(--background-color) !important;
        background-image: var(--gradient-header) !important;
    }

    div.dialog_container div.dialog_window a {
        background-color: var(--background-color) !important;
        background-image: var(--gradient-button) !important;
    }
    div.dialog_container div.dialog_window a:hover, div.dialog_container div.dialog_window a:active {
        background-color: var(--background-color) !important;
        background-image: var(--gradient-button-selected);
        border-color: var(--border-color-selected) !important;
    }

    .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
        background-color: var(--background-color) !important;
        background-image: var(--gradient-tab);
    }
    .ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
        background-color: var(--background-color) !important;
        background-image: var(--gradient-tab-selected) !important;
        border-color: var(--border-color-selected) !important;
    }
      
    ul {
        list-style: none;
    }
      
    ul.torrent_list li.torrent.even {
        background-color: rgba(255, 255, 255, 0.1) !important;
    }
    ul.torrent_list li.torrent.selected, ul.torrent_list li.torrent.selected.even, div#jqContextMenu ul li:hover {
        color: var(--foreground-color-selected) !important;
        border-color: var(--border-color-selected) !important;
    }
      

    
    div.inspector-tab.selected { border-color: var(--border-color-selected) !important; }
    #inspector-tab-files {             background-image: var(--tab-files),    var(--gradient-button)          !important; }
    #inspector-tab-files.selected {    background-image: var(--tab-files),    var(--gradient-button-selected) !important; }
    #inspector-tab-info {              background-image: var(--tab-info),     var(--gradient-button)          !important; }
    #inspector-tab-info.selected {     background-image: var(--tab-info),     var(--gradient-button-selected) !important; }
    #inspector-tab-peers {             background-image: var(--tab-peers),    var(--gradient-button)          !important; }
    #inspector-tab-peers.selected {    background-image: var(--tab-peers),    var(--gradient-button-selected) !important; }
    #inspector-tab-trackers {          background-image: var(--tab-trackers), var(--gradient-button)          !important; }
    #inspector-tab-trackers.selected { background-image: var(--tab-trackers), var(--gradient-button-selected) !important; }

    div.torrent_footer div.selected { border-color: var(--border-color-selected) !important; }
    div.torrent_footer div.main_container { display: none; }
    #prefs-button {                    background-image: var(--button-prefs),              var(--gradient-button)          !important; }
    #turtle-button {                   background-image: var(--button-turtle),             var(--gradient-button)          !important; }
    #turtle-button.selected {          background-image: var(--button-turtle),             var(--gradient-button-selected) !important; }
    #compact-button {                  background-image: var(--button-compact),            var(--gradient-button)          !important; }
    #compact-button.selected {         background-image: var(--button-compact),            var(--gradient-button-selected) !important; }

    div.file-priority-radiobox > div.selected { border-color: var(--border-color-selected) !important; }
    div.file-priority-radiobox > div.low {             background-image: var(--priority-low),    var(--gradient-button)          !important; }
    div.file-priority-radiobox > div.low.selected {    background-image: var(--priority-low),    var(--gradient-button-selected) !important; }
    div.file-priority-radiobox > div.normal {          background-image: var(--priority-normal), var(--gradient-button)          !important; }
    div.file-priority-radiobox > div.normal.selected { background-image: var(--priority-normal), var(--gradient-button-selected) !important; }
    div.file-priority-radiobox > div.high {            background-image: var(--priority-high),   var(--gradient-button)          !important; }
    div.file-priority-radiobox > div.high.selected {   background-image: var(--priority-high),   var(--gradient-button-selected) !important; }
      
    :root {
        --background-color: #333;
        --border-color: #222;
        --border-color-selected: #666;
        --foreground-color: rgba(255, 255, 255, 0.8);
        --foreground-color-selected: rgba(0, 255, 0, 0.8);
        --gradient-to-top:    linear-gradient(to top,    rgba(255, 255, 255, 0), rgba(255, 255, 255, 0.1));
        --gradient-to-bottom: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(255, 255, 255, 0.1));
        --gradient-selected:  linear-gradient(to right, rgba(0, 255, 0, 0.2), rgba(0, 255, 0, 0),     rgba(0, 255, 0, 0.2));

        --gradient-header: var(--gradient-to-top);
        --gradient-button: var(--gradient-to-top);
        --gradient-button-selected: var(--gradient-to-top), var(--gradient-selected);
        --gradient-button-selected: var(--gradient-to-bottom);
        --gradient-tab: var(--gradient-to-bottom);
        --gradient-tab-selected: var(--gradient-to-top);

        /* Images */
        --priority-high: url("data:image/png;base64,");
        --priority-normal: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAASklEQVQokWNgGIlAVFRURVZW9qmMjMwDfFhWVvadiIiIFIOEhISWpKTkfyKxPIOIiAiviIhIk4iISD0+LCoq2i4oKMg/0CFCDQAAK2Qigs7t83QAAAAASUVORK5CYII=");
        --priority-low: url("data:image/png;base64,");

        --button-turtle: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAICAYAAADwdn+XAAAAmklEQVQokY3PPQoCUQwE4E9sFEsbK8FOEES0trT0FLZ2Woi9XkTY4l3DO1haCHoJwWfzFtafXR0IhGQyk+ETLayQ4YDlF04pRrgjvtUZzQKvgc77cTuRH18EIq4YYFaYbYsCl4rjMuE9dHH64Z7XDTvM0cud+9jg+Id7M8ZYewkeQqinNkukBcYYpszTlD8ms1IETCr26/T6C54Bx0EzYFeoXQAAAABJRU5ErkJggg==");
        --button-prefs: url("data:image/png;base64,");
        --button-compact: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAICAYAAADwdn+XAAAAbUlEQVQokcWQMQrAMAwD5UAwmUoGZzDK/5/nJ7hLU9op3Sq4RXAaBJI559xCMt19AsBySCbcPb/Sez8AvByYWYwxcoeZBa4s59n9GFWN1lruUNUAcDwdVQ3UWvMra+DViUiUUnKHiNyHLUdE4gRg62K+ejzZwAAAAABJRU5ErkJggg==");

        --tab-files: url("data:image/png;base64,");
        --tab-info: url("data:image/png;base64,");
        --tab-peers: url("data:image/png;base64,");
        --tab-trackers: url("data:image/png;base64,");
    }
}
