css:
@-moz-document domain("explosm.net") {
#superheader-bar {    background: #1B1B1B !important; border-bottom: 1px solid #404040 !important;}
.inner-wrap {background: #2B2B2B !important;}
.inner-wrap>.row {background: #2B2B2B !important;}
#this-past-week-title {    color: #F1F1F1 !important;}
#past-week-container {
    background-color: #1B1B1B !important;
    border: 1px solid #404040 !important;}
.past-week-comic-title a, .featured-comic-title a, .blog-title a {color: #ffffff !important;}
.fa {color: #FFA93A !important; }
.author-credit-name, .comment-meta-data, .meta-data {    color: #BFBFBF !important;}
#comic-container { border: 1px solid #404040 !important;}
label {color: #C1C1C1 !important;}
input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], textarea {background-color: #4A4949 !important;    border: 1px solid #404040 !important;color: #ffffff !important;}
h1, h2, h3, h4, h5, h6 {    color: #A9A9A9 !important;}
body {color: #FFFFFF !important;}
hr {border: 1px solid #404040 !important;}
#explosmore-callout-banner {    background: #1B1B1B !important;}
#latest-news {background: #1B1B1B !important;}
.body-font {
    color: #F5F5F5 !important;}
#randomsplosm-container {background: #2B2B2B !important;}
.sub-nav dt.active a, .sub-nav dd.active a, .sub-nav li.active a {background-color: #FFA93A !important; }
button.expand, .button.expand {background: #1B1B1B !important; border: 1px solid #404040 !important;}
.thumb-overlay {background: #1B1B1B !important;}
.footer-leaderboard-container {background: #1B1B1B !important;}
#footer {background: #2B2B2B !important;}
#legal-footer {background: #1B1B1B !important;}
.custom-secondary-button {background: #1B1B1B !important;}
}
